<body>
    <div class="player">
        <div class="bg"></div>
        <div class="glass"></div>
        
        <div class="content">
            <div class="vinyl" id="vinyl" onclick="showModal()"></div>
            
            <div class="middle">
                <div class="info">
                    <div class="title" id="title">点击唱片添加音乐</div>
                    <div class="status" id="status">就绪</div>
                </div>
                
                <div class="progress-wrap" id="progressBar">
                    <div class="progress">
                        <div class="bar" id="bar"></div>
                    </div>
                    <div class="time">
                        <span id="current">0:00</span>
                        <span id="total">0:00</span>
                    </div>
                </div>
                
                <div class="controls">
                    <button class="btn" onclick="skip(-10)">
                        <svg viewBox="0 0 24 24">
                            <path d="M11 18V6l-8.5 6 8.5 6zm.5-6l8.5 6V6l-8.5 6z"/>
                        </svg>
                    </button>
                    <button class="btn main" onclick="playPause()">
                        <svg viewBox="0 0 24 24" id="playIcon">
                            <path d="M8 5v14l11-7z"/>
                        </svg>
                        <svg viewBox="0 0 24 24" id="pauseIcon" style="display:none">
                            <path d="M6 19h4V5H6v14zm8-14v14h4V5h-4z"/>
                        </svg>
                    </button>
                    <button class="btn" onclick="skip(10)">
                        <svg viewBox="0 0 24 24">
                            <path d="M4 18l8.5-6L4 6v12zm9-12v12l8.5-6L13 6z"/>
                        </svg>
                    </button>
                </div>
            </div>
            
            <div class="volume">
                <svg viewBox="0 0 24 24">
                    <path d="M3 9v6h4l5 5V4L7 9H3z"/>
                </svg>
                <input type="range" id="vol" min="0" max="100" value="70">
            </div>
        </div>
    </div>

    <div class="modal" id="modal">
        <div class="modal-box">
            <h3>输入音乐URL</h3>
            <input id="url" placeholder="MP3音频链接..." value="https://files.catbox.moe/8w2ex2.mp3">
            <div class="modal-btns">
                <button class="modal-btn ok" onclick="loadMusic()">确认</button>
                <button class="modal-btn cancel" onclick="hideModal()">取消</button>
            </div>
        </div>
    </div>

    <audio id="audio" crossorigin="anonymous"></audio>

    <script>
        const a = document.getElementById('audio');
        const v = document.getElementById('vinyl');
        let paused = false, timer;

        // 初始化
        a.src = 'https://files.catbox.moe/lhafnf.mp3';
        a.volume = 0.7;

        // 播放控制
        function playPause() {
            if (!a.src) return showModal();
            
            if (a.paused) {
                paused = false;
                a.play().then(() => {
                    playIcon.style.display = 'none';
                    pauseIcon.style.display = 'block';
                    v.classList.add('spin');
                    status.textContent = '播放中';
                    forcePlaying();
                });
            } else {
                paused = true;
                a.pause();
                playIcon.style.display = 'block';
                pauseIcon.style.display = 'none';
                v.classList.remove('spin');
                status.textContent = '已暂停';
                clearInterval(timer);
            }
        }

        // 持续播放
        function forcePlaying() {
            clearInterval(timer);
            timer = setInterval(() => {
                if (!paused && a.paused) a.play();
            }, 500);
        }

        // 跳转
        function skip(s) {
            a.currentTime = Math.max(0, Math.min(a.duration, a.currentTime + s));
        }

        // 模态框
        function showModal() {
            modal.classList.add('show');
        }

        function hideModal() {
            modal.classList.remove('show');
        }

        // 加载音乐
        function loadMusic() {
            const u = url.value.trim();
            if (u) {
                a.src = u;
                title.textContent = u.split('/').pop().split('.')[0] || '自定义音乐';
                hideModal();
                paused = false;
                a.play().then(() => {
                    playIcon.style.display = 'none';
                    pauseIcon.style.display = 'block';
                    v.classList.add('spin');
                    status.textContent = '播放中';
                    forcePlaying();
                });
            }
        }

        // 进度条
        a.addEventListener('timeupdate', () => {
            if (a.duration) {
                bar.style.width = (a.currentTime / a.duration * 100) + '%';
                current.textContent = fmt(a.currentTime);
            }
        });

        a.addEventListener('loadedmetadata', () => {
            total.textContent = fmt(a.duration);
        });

        // 循环
        a.addEventListener('ended', () => {
            if (!paused) {
                a.currentTime = 0;
                a.play();
            }
        });

        // 进度点击
        progressBar.addEventListener('click', e => {
            if (a.duration) {
                const rect = progressBar.getBoundingClientRect();
                a.currentTime = (e.clientX - rect.left) / rect.width * a.duration;
            }
        });

        // 音量
        vol.addEventListener('input', e => {
            a.volume = e.target.value / 100;
        });

        // 格式化时间
        function fmt(s) {
            if (isNaN(s)) return '0:00';
            const m = Math.floor(s / 60);
            const sec = Math.floor(s % 60);
            return `${m}:${sec.toString().padStart(2, '0')}`;
        }

        // 保持播放
        document.addEventListener('visibilitychange', () => {
            if (!document.hidden && !paused && a.paused) a.play();
        });

        // 快捷键
        document.addEventListener('keydown', e => {
            if (e.code === 'Space') {
                e.preventDefault();
                playPause();
            }
        });
    </script>
</body>
</html>
