            height: 150px;
            position: relative;
            border-radius: 20px;
            overflow: hidden;
            box-shadow: 0 10px 40px rgba(0,0,0,0.8);
        }

        .bg {
            position: absolute;
            inset: 0;
            background: url('https://files.catbox.moe/buayj6.jpeg') center/cover;
            filter: brightness(0.4) blur(1px);
        }

        .glass {
            position: absolute;
            inset: 0;
            background: linear-gradient(135deg,
                rgba(144,238,144,0.1) 0%,
                rgba(152,251,152,0.15) 100%);
            backdrop-filter: blur(8px);
        }

        .content {
            position: relative;
            height: 100%;
            display: flex;
            align-items: center;
            padding: 20px;
            gap: 20px;
        }

        .vinyl {
            width: 60px;
            height: 60px;
            min-width: 60px;
            background: radial-gradient(circle at 30% 30%,
                #3a3a3a, #1a1a1a 30%, #0a0a0a 50%, #2a2a2a);
            border-radius: 50%;
            box-shadow: 0 4px 15px rgba(0,0,0,0.8);
            cursor: pointer;
            position: relative;
            transition: transform 0.3s;
        }

        .vinyl:hover {
            transform: scale(1.1);
        }

        .vinyl.spin {
            animation: rotate 3s linear infinite;
        }

        @keyframes rotate {
            to { transform: rotate(360deg); }
        }

        .vinyl::before {
            content: '';
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            width: 22px;
            height: 22px;
            background: radial-gradient(circle, #90ee90, #3cb371);
            border-radius: 50%;
        }

        .vinyl::after {
            content: '';
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            width: 5px;
            height: 5px;
            background: #000;
            border-radius: 50%;
        }

        .middle {
            flex: 1;
            display: flex;
            flex-direction: column;
            gap: 10px;
            min-width: 0;
        }

        .info {
            color: #fff;
            font-size: 12px;
            white-space: nowrap;
            overflow: hidden;
            text-overflow: ellipsis;
        }

        .title {
            font-weight: 600;
        }

        .status {
            opacity: 0.7;
            font-size: 11px;
        }

        .progress-wrap {
            cursor: pointer;
        }

        .progress {
            height: 3px;
            background: rgba(255,255,255,0.2);
            border-radius: 2px;
            overflow: hidden;
        }

        .bar {
            height: 100%;
            background: linear-gradient(90deg, #90ee90, #7cfc00);
            width: 0%;
            transition: width 0.1s;
        }

        .time {
            display: flex;
            justify-content: space-between;
            font-size: 10px;
            color: rgba(255,255,255,0.6);
            margin-top: 4px;
        }

        .controls {
            display: flex;
            gap: 8px;
            align-items: center;
        }

        .btn {
            width: 32px;
            height: 32px;
            border: 1px solid rgba(255,255,255,0.2);
            background: rgba(255,255,255,0.1);
            border-radius: 50%;
            cursor: pointer;
            display: flex;
            align-items: center;
            justify-content: center;
            transition: all 0.2s;
        }

        .btn:hover {
            background: rgba(144,238,144,0.3);
            transform: scale(1.1);
        }

        .btn.main {
            width: 38px;
            height: 38px;
            background: rgba(144,238,144,0.2);
        }

        .btn svg {
            width: 16px;
            height: 16px;
            fill: #fff;
        }

        .btn.main svg {
            width: 18px;
            height: 18px;
        }

        .volume {
            position: absolute;
            bottom: 15px;
            right: 15px;
            display: flex;
            align-items: center;
            gap: 5px;
        }

        .volume svg {
            width: 12px;
            height: 12px;
            fill: rgba(255,255,255,0.7);
        }

        .volume input {
            width: 50px;
            height: 2px;
            appearance: none;
            background: rgba(255,255,255,0.2);
            outline: none;
            cursor: pointer;
        }

        .volume input::-webkit-slider-thumb {
            appearance: none;
            width: 8px;
            height: 8px;
            background: #90ee90;
            border-radius: 50%;
            cursor: pointer;
        }

        .modal {
            display: none;
            position: fixed;
            inset: 0;
            background: rgba(0,0,0,0.8);
            align-items: center;
            justify-content: center;
            z-index: 100;
        }

        .modal.show {
            display: flex;
        }

        .modal-box {
            background: linear-gradient(135deg, #90ee90, #98fb98);
            padding: 25px;
            border-radius: 15px;
            width: 350px;
        }

        .modal h3 {
            margin-bottom: 15px;
            color: #1a1a1a;
        }

        .modal input {
            width: 100%;
            padding: 10px;
            border: 2px solid rgba(255,255,255,0.3);
            border-radius: 8px;
            background: rgba(255,255,255,0.3);
            outline: none;
        }

        .modal-btns {
            display: flex;
            gap: 10px;
            margin-top: 15px;
        }

        .modal-btn {
            flex: 1;
            padding: 8px;
            border: none;
            border-radius: 6px;
            cursor: pointer;
            font-weight: 600;
        }

        .modal-btn.ok {
            background: #3cb371;
            color: white;
        }

        .modal-btn.cancel {
            background: rgba(255,255,255,0.5);
        }
    </style>
</head>
<body>
    <div class="player">
        <div class="bg"></div>
        <div class="glass"></div>

        <div class="content">
            <div class="vinyl" id="vinyl" onclick="showModal()"></div>

            <div class="middle">
                <div class="info">
                    <div class="title" id="title">点击唱片添加音乐</div>
                    <div class="status" id="status">就绪</div>
                </div>

                <div class="progress-wrap" id="progressBar">
                    <div class="progress">
                        <div class="bar" id="bar"></div>
                    </div>
                    <div class="time">
                        <span id="current">0:00</span>
                        <span id="total">0:00</span>
                    </div>
                </div>

                <div class="controls">
                    <button class="btn" onclick="skip(-10)">
                        <svg viewBox="0 0 24 24">
                            <path d="M11 18V6l-8.5 6 8.5 6zm.5-6l8.5 6V6l-8.5 6z"/>
                        </svg>
                    </button>
                    <button class="btn main" onclick="playPause()">
                        <svg viewBox="0 0 24 24" id="playIcon">
                            <path d="M8 5v14l11-7z"/>
                        </svg>
                        <svg viewBox="0 0 24 24" id="pauseIcon" style="display:none">
                            <path d="M6 19h4V5H6v14zm8-14v14h4V5h-4z"/>
                        </svg>
                    </button>
                    <button class="btn" onclick="skip(10)">
                        <svg viewBox="0 0 24 24">
                            <path d="M4 18l8.5-6L4 6v12zm9-12v12l8.5-6L13 6z"/>
                        </svg>
                    </button>
                </div>
            </div>

            <div class="volume">
                <svg viewBox="0 0 24 24">
                    <path d="M3 9v6h4l5 5V4L7 9H3z"/>
                </svg>
                <input type="range" id="vol" min="0" max="100" value="70">
            </div>
        </div>
    </div>

    <div class="modal" id="modal">
        <div class="modal-box">
            <h3>输入音乐URL</h3>
            <input id="url" placeholder="MP3音频链接..." value="https://files.catbox.moe/8w2ex2.mp3">
            <div class="modal-btns">
                <button class="modal-btn ok" onclick="loadMusic()">确认</button>
                <button class="modal-btn cancel" onclick="hideModal()">取消</button>
            </div>
        </div>
    </div>

    <audio id="audio" crossorigin="anonymous"></audio>

    <script>
        const a = document.getElementById('audio');
        const v = document.getElementById('vinyl');
        let paused = false, timer;

        // 初始化
        a.src = 'https://files.catbox.moe/lhafnf.mp3';
        a.volume = 0.7;

        // 播放控制
        function playPause() {
            if (!a.src) return showModal();

            if (a.paused) {
                paused = false;
                a.play().then(() => {
                    playIcon.style.display = 'none';
                    pauseIcon.style.display = 'block';
                    v.classList.add('spin');
                    status.textContent = '播放中';
                    forcePlaying();
                });
            } else {
                paused = true;
                a.pause();
                playIcon.style.display = 'block';
                pauseIcon.style.display = 'none';
                v.classList.remove('spin');
                status.textContent = '已暂停';
                clearInterval(timer);
            }
        }

        // 持续播放
        function forcePlaying() {
            clearInterval(timer);
            timer = setInterval(() => {
                if (!paused && a.paused) a.play();
            }, 500);
        }

        // 跳转
        function skip(s) {
            a.currentTime = Math.max(0, Math.min(a.duration, a.currentTime + s));
        }

        // 模态框
        function showModal() {
            modal.classList.add('show');
        }

        function hideModal() {
            modal.classList.remove('show');
        }

        // 加载音乐
        function loadMusic() {
            const u = url.value.trim();
            if (u) {
                a.src = u;
                title.textContent = u.split('/').pop().split('.')[0] || '自定义音乐';
                hideModal();
                paused = false;
                a.play().then(() => {
                    playIcon.style.display = 'none';
                    pauseIcon.style.display = 'block';
                    v.classList.add('spin');
                    status.textContent = '播放中';
                    forcePlaying();
                });
            }
        }

        // 进度条
        a.addEventListener('timeupdate', () => {
            if (a.duration) {
                bar.style.width = (a.currentTime / a.duration * 100) + '%';
                current.textContent = fmt(a.currentTime);
            }
        });

        a.addEventListener('loadedmetadata', () => {
            total.textContent = fmt(a.duration);
        });

        // 循环
        a.addEventListener('ended', () => {
            if (!paused) {
                a.currentTime = 0;
                a.play();
            }
        });

        // 进度点击
        progressBar.addEventListener('click', e => {
            if (a.duration) {
                const rect = progressBar.getBoundingClientRect();
                a.currentTime = (e.clientX - rect.left) / rect.width * a.duration;
            }
        });

        // 音量
        vol.addEventListener('input', e => {
            a.volume = e.target.value / 100;
        });

        // 格式化时间
        function fmt(s) {
            if (isNaN(s)) return '0:00';
            const m = Math.floor(s / 60);
            const sec = Math.floor(s % 60);
            return `${m}:${sec.toString().padStart(2, '0')}`;
        }

        // 保持播放
        document.addEventListener('visibilitychange', () => {
            if (!document.hidden && !paused && a.paused) a.play();
        });

        // 快捷键
        document.addEventListener('keydown', e => {
            if (e.code === 'Space') {
                e.preventDefault();
                playPause();
            }
        });
    </script>
</body>
</html>
