                const commentsHtml = post.comments.length > 0 ? `<ul class="comments-section">${post.comments.map(c => `<li><span class="comment-author">${c.key.startsWith('USER') ? getDisplayName('user') : getDisplayName('char')}</span>: ${c.data.text}</li>`).join('')}</ul>` : '';
                interactionsHtml = `<div class="post-interactions">${likersHtml}${commentsHtml}</div>`;
            }
            const displayTime = formatMomentTimestamp(post.data.date, post.data.time);
            const deleteBtnHtml = fromUser ? `<i class="fas fa-trash-alt delete-moment-btn" title="åˆ é™¤"></i>` : '';
            li.innerHTML = `<img src="${authorAvatar}" alt="Avatar" class="post-author-avatar">
                                         <div class="post-details">
                                             <span class="post-author-name">${authorName}</span>
