            viewElement.style.backgroundSize = 'cover';
            viewElement.style.backgroundPosition = 'center';
            viewElement.style.backgroundAttachment = 'fixed'; // Prevents resizing when keyboard appears
        } else {
            viewElement.style.backgroundImage = defaultBg;
            viewElement.style.backgroundSize = 'cover';
            viewElement.style.backgroundPosition = 'center';
            viewElement.style.backgroundAttachment = 'scroll'; // Reset to default behavior
        }
    }

