    .style-recommendation .card-content-wrapper {
        display: flex;
        width: 100%;
        max-width: 600px;
        filter: drop-shadow(0px 8px 20px rgba(0, 50, 150, 0.2));
        background: transparent;
        font-family: 'Inter', system-ui, sans-serif;
        color: #333;
        --card-radius: 20px;
        --stub-width: 140px;
    }

    .style-recommendation .card-content-wrapper:hover {
        transform: translateY(-4px); 
    }

    .style-recommendation .texture-overlay {
        position: absolute; top: 0; left: 0; right: 0; bottom: 0;
        opacity: 0.06; pointer-events: none; z-index: 1;
        background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 200 200' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noise'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.8' numOctaves='3' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noise)' opacity='1'/%3E%3C/svg%3E");
        border-radius: inherit;
    }

    .style-recommendation .ticket-main {
        flex: 1; min-width: 0; background-color: #ffffff; padding: 30px; position: relative;
        border-radius: var(--card-radius) 0 0 var(--card-radius);
    }
    .style-recommendation .ticket-stub {
        flex: 0 0 var(--stub-width); background: linear-gradient(135deg, #007aff 0%, #0050a0 100%);
        color: white; padding: 20px 15px; position: relative;
        border-radius: 0 var(--card-radius) var(--card-radius) 0;
        display: flex; flex-direction: column; justify-content: space-between; text-align: center; overflow: hidden;
        box-shadow: inset 2px 0 10px rgba(0,0,0,0.1), inset -2px -2px 10px rgba(255,255,255,0.1);
    }
    .style-recommendation .ticket-stub::before {
        content: ''; position: absolute; top: -50%; left: -50%; width: 200%; height: 200%;
        background: linear-gradient(45deg, transparent 40%, rgba(255,255,255,0.1) 45%, rgba(255,255,255,0.3) 50%, rgba(255,255,255,0.1) 55%, transparent 60%);
        transform: rotate(25deg); pointer-events: none; z-index: 2; mix-blend-mode: overlay;
    }
    .style-recommendation .perforation-line {
        position: absolute; top: 15px; bottom: 15px; right: 0; width: 0; border-right: 2px dashed #e0e0e0; z-index: 2;
    }
    .style-recommendation .ticket-header { display: flex; justify-content: space-between; align-items: center; margin-bottom: 25px; padding-bottom: 12px; border-bottom: 2px solid #f2f2f7; }
    .style-recommendation .airline-brand { font-weight: 800; font-size: 1.1em; color: #007aff; display: flex; align-items: center; gap: 8px; }
    .style-recommendation .priority-tag { font-size: 0.65em; font-weight: 700; color: #007aff; background: rgba(0, 122, 255, 0.1); padding: 5px 10px; border-radius: 20px; }
    .style-recommendation .content-body { margin-bottom: 30px; position: relative; z-index: 3; }
    .style-recommendation .rec-label { font-size: 0.7em; color: #9eaab5; font-weight: 700; letter-spacing: 1px; margin-bottom: 6px; }
    .style-recommendation .rec-title { font-size: 1.6em; font-weight: 800; color: #111; line-height: 1.2; margin-bottom: 5px; }
    .style-recommendation .rec-artist { font-size: 1em; color: #007aff; font-weight: 600; }
    .style-recommendation .rec-quote-box { margin-top: 15px; font-size: 0.9em; color: #555; line-height: 1.6; background: #f4f9ff; border-radius: 12px; padding: 15px; border-left: 4px solid #007aff; }
    .style-recommendation .info-grid { display: grid; grid-template-columns: 1.5fr 1fr 1fr; gap: 10px; }
    .style-recommendation .info-unit label { display: block; font-size: 0.65em; color: #9eaab5; margin-bottom: 4px; font-weight: 700; }
    .style-recommendation .info-unit span { display: block; font-size: 1.05em; font-weight: 700; color: #222; font-family: 'JetBrains Mono', monospace; }
    .style-recommendation .stub-content { position: relative; z-index: 3; flex: 1; display: flex; flex-direction: column; justify-content: flex-start; }
    .style-recommendation .stub-dest-group { margin-bottom: 20px; }
    .style-recommendation .dest-code { font-size: 2.2em; font-weight: 900; letter-spacing: -1px; line-height: 1; text-shadow: 0 2px 10px rgba(0,0,0,0.15); }
    .style-recommendation .stub-label { font-size: 0.65em; opacity: 0.85; font-weight: 700; margin-bottom: 2px; }
    .style-recommendation .stub-meta { display: flex; flex-direction: column; gap: 10px; font-family: 'JetBrains Mono', monospace; }
    .style-recommendation .stub-val { font-size: 1.1em; font-weight: 700; }
    .style-recommendation .barcode-container { position: relative; z-index: 3; background: rgba(255, 255, 255, 0.922); border-radius: 6px; padding: 5px; display: flex; align-items: center; justify-content: center; margin-top: auto; }
    .style-recommendation .barcode-font { font-family: 'Libre Barcode 128', cursive; font-size: 2.5em; line-height: 0.8; color: #000; white-space: nowrap; padding-top: 10px; }

    @media (max-width: 480px) {
        .style-recommendation .card-content-wrapper { flex-direction: column; }
        .style-recommendation .ticket-main { border-radius: var(--card-radius) var(--card-radius) 0 0; padding: 25px; }
        .style-recommendation .perforation-line { top: auto; bottom: 0; left: 15px; right: 15px; width: auto; height: 0; border-right: none; border-bottom: 2px dashed #e0e0e0; }
        .style-recommendation .ticket-stub { flex: 0 0 auto; width: auto; border-radius: 0 0 var(--card-radius) var(--card-radius); padding: 18px 25px; flex-direction: row; align-items: center; justify-content: space-between; gap: 15px; }
        .style-recommendation .stub-content { flex: 0 1 auto; flex-direction: row; align-items: center; gap: 15px; }
        .style-recommendation .stub-dest-group { margin-bottom: 0; text-align: left; }
        .style-recommendation .dest-code { font-size: 1.8em; }
        .style-recommendation .stub-meta { display: none; }
        .style-recommendation .barcode-container { margin-top: 0; flex-shrink: 0; padding: 4px 8px; max-width: 100%; overflow: hidden; }
        .style-recommendation .barcode-font { font-size: 2em; }
    }


