    .style-memory .card-content-wrapper {
        position: relative;
        width: 100%;
        max-width: 450px;
        background-color: #222;
        border-radius: 16px;
        overflow: hidden;
        display: flex; flex-direction: column; padding: 25px;
        color: #e0e0e0;
        font-family: 'Space Mono', monospace;
        background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 200 200' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noise'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='1.5' numOctaves='3' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noise)' opacity='0.08'/%3E%3C/svg%3E");
        border: 1px solid #000;
        box-shadow: 
            0 10px 3px -10px rgba(0,0,0,0.8), 
            inset 0 2px 0 rgba(255,255,255,0.15),
            inset -2px -5px 0 rgba(0,0,0,0.5)
    }

    .style-memory .cassette-shell {
        background: linear-gradient(135deg, #f2ece1 0%, #e6e1d3 40%, #dcd6c5 100%);
        border-radius: 6px;
        padding: 12px;
        position: relative;
        margin-bottom: 25px;
        display: flex; flex-direction: column; align-items: center;

        box-shadow: 
            0 5px 10px rgba(0,0,0,0.5),
            0 2px 3px rgba(0,0,0,0.3),
            inset 2px 2px 0 rgba(255,255,255,0.8),
            inset -2px -2px 0 rgba(0,0,0,0.15);

        border: 1px solid #aaa; 
    }

    .style-memory .screw { 
        position: absolute; width: 8px; height: 8px; 
        background: #bbb; border-radius: 50%; 
        box-shadow: inset 1px 1px 2px rgba(0,0,0,0.8), 0 1px 0 rgba(255,255,255,0.5); 
    }
    .style-memory .screw::after { content: ''; position: absolute; top: 50%; left: 50%; width: 100%; height: 1px; background: #555; transform: translate(-50%, -50%) rotate(45deg); }
    .style-memory .screw.tl { top: 6px; left: 6px; } .style-memory .screw.tr { top: 6px; right: 6px; }
    .style-memory .screw.bl { bottom: 6px; left: 6px; } .style-memory .screw.br { bottom: 6px; right: 6px; }

    .style-memory .tape-label {
        width: 100%; background: #fff; border-radius: 2px;
        padding: 8px 5px; margin-bottom: 10px; text-align: center;
        border-top: 6px solid #ff9900;
        box-shadow: 0 1px 2px rgba(0,0,0,0.15);
        transform: rotate(-0.5deg);
    }
    .style-memory .handwritten-title {
        font-family: 'Caveat', cursive; color: #222; font-size: 1.4em; font-weight: 700; line-height: 1.1;
        white-space: nowrap; overflow: hidden; text-overflow: ellipsis;
    }

    .style-memory .tape-window {
        width: 200px; height: 45px;
        background: #2a2a2a; 
        border-radius: 30px;
        position: relative; display: flex; justify-content: center; align-items: center; gap: 35px;
        border: 1px solid #888;
        overflow: hidden;
        box-shadow: 
            inset 0 3px 8px rgba(0,0,0,0.7), 
            0 1px 0 rgba(255,255,255,0.6); 
    }
    .style-memory .tape-window::before {
        content: ''; position: absolute; width: 100%; height: 100%;
        background: repeating-linear-gradient(90deg, transparent 0, transparent 2px, rgba(255,255,255,0.05) 2px, rgba(255,255,255,0.05) 4px);
    }

    .style-memory .reel {
        width: 32px; height: 32px; background: #fff; border-radius: 50%; position: relative; z-index: 1; 
        border: 4px solid #fff; 
        background-image: radial-gradient(circle at center, #333 30%, transparent 31%), conic-gradient(from 0deg, #333 0deg 60deg, transparent 60deg 120deg, #333 120deg 180deg, transparent 180deg 240deg, #333 240deg 300deg, transparent 300deg 360deg);
        animation: spinReel 4s linear infinite;
        box-shadow: 0 1px 2px rgba(0,0,0,0.3); 
    }
    .style-memory .reel.left { box-shadow: 0 0 0 3px #5e4b35; /* ç£å¸¦ç›˜ */ } 
    .style-memory .reel.right { box-shadow: 0 0 0 1px #5e4b35; animation-delay: -2s; }

    .style-memory .lcd-panel {
        background: #9cad8e; border-radius: 4px; padding: 12px 15px;
        border: 4px solid #1a1a1a;
        box-shadow: inset 2px 2px 5px rgba(0,0,0,0.4), 0 1px 0 rgba(255,255,255,0.1);
        font-family: 'Space Mono', monospace; color: #111;
        position: relative; display: flex; flex-direction: column; gap: 10px;
    }
    .style-memory .lcd-panel::after {
        content: ''; position: absolute; top: 0; left: 0; width: 100%; height: 100%;
        background: linear-gradient(rgba(0,0,0,0.03) 50%, transparent 50%); background-size: 100% 4px; pointer-events: none;
    }

    .style-memory .meta-row { display: flex; justify-content: space-between; align-items: center; font-size: 0.7em; font-weight: 700; opacity: 0.8; border-bottom: 1px dotted #111; padding-bottom: 4px; }

    .style-memory .scrolling-text {
        font-size: 0.95em; line-height: 1.5; height: 90px; overflow-y: auto; white-space: pre-wrap;
        text-align: left; padding-right: 5px; margin: 0;
    }
    .style-memory .scrolling-text::-webkit-scrollbar { width: 3px; }
    .style-memory .scrolling-text::-webkit-scrollbar-thumb { background: #1a1a1a; }

    .style-memory .thought-line {
        font-size: 0.8em; color: #444; background: rgba(0,0,0,0.05); padding: 6px 8px; border-radius: 2px;
        font-style: italic; border-left: 3px solid #ff4444; margin-top: 2px;
    }
    .style-memory .thought-label { font-weight: bold; text-transform: uppercase; font-size: 0.8em; opacity: 0.7; margin-right: 5px; }

    .style-memory .visualizer-row { display: flex; justify-content: space-between; align-items: flex-end; margin-top: 5px; padding-top: 5px; border-top: 1px dotted #111; }
    .style-memory .play-status { font-size: 0.7em; font-weight: bold; display: flex; align-items: center; gap: 5px; }
    .style-memory .led { width: 6px; height: 6px; background: #ff3333; border-radius: 50%; box-shadow: 0 0 5px #ff3333; animation: blinkLed 1.5s infinite; }
    .style-memory .visualizer { display: flex; align-items: flex-end; gap: 2px; height: 12px; }
    .style-memory .bar { width: 3px; background: #111; animation: equalizer 0.8s infinite ease-in-out; }
    .style-memory .bar:nth-child(1) { animation-duration: 0.6s; height: 40%; }
    .style-memory .bar:nth-child(2) { animation-duration: 1.0s; height: 80%; }
    .style-memory .bar:nth-child(3) { animation-duration: 0.7s; height: 50%; }
    .style-memory .bar:nth-child(4) { animation-duration: 0.9s; height: 90%; }
    .style-memory .bar:nth-child(5) { animation-duration: 1.1s; height: 30%; }

    .style-memory .controls-deco { display: flex; justify-content: center; gap: 12px; margin-top: 20px; }
    .style-memory .btn-deco { 
        width: 35px; height: 12px; background: #3a3a3a; border-radius: 2px; 
        box-shadow: 0 3px 0 #111, 0 4px 5px rgba(0,0,0,0.5); 
    }
    .style-memory .btn-deco.active { 
        background: #555; transform: translateY(3px); 
        box-shadow: 0 0 0 #111, inset 0 1px 3px rgba(0,0,0,0.5); 
        border-bottom: 2px solid #ff9900; 
    }

    @keyframes spinReel { from { transform: rotate(0deg); } to { transform: rotate(360deg); } }
    @keyframes equalizer { 0%, 100% { height: 20%; } 50% { height: 100%; } }
    @keyframes blinkLed { 0%, 100% { opacity: 1; } 50% { opacity: 0.3; } }

