                    button.className = 'option-button';
                    button.textContent = optionText.trim();
                    optionsList.appendChild(button);
                });
            };

            const lightParticleColors = ['rgba(192, 168, 133, 0.6)', 'rgba(113, 128, 150, 0.6)', 'rgba(255, 255, 255, 0.7)'];
            const darkParticleColors = ['rgba(204, 165, 106, 0.5)'];

            const createParticles = (theme) => {
                particlesContainer.innerHTML = '';
                const isDark = theme === 'dark';
                const particleCount = isDark ? 25 : 40;
                const colors = isDark ? darkParticleColors : lightParticleColors;
                for (let i = 0; i < particleCount; i++) {
                    let particle = document.createElement('div');
                    particle.style.position = 'absolute';
                    particle.style.borderRadius = '50%';
                    const color = colors[Math.floor(Math.random() * colors.length)];
                    particle.style.background = color;
                    if (isDark) particle.style.boxShadow = `0 0 8px ${color}, 0 0 4px ${color}`;
                    const size = isDark ? (Math.random() * 2.5 + 1) : (Math.random() * 3 + 1.5);
                    particle.style.width = `${size}px`;
                    particle.style.height = `${size}px`;
                    particle.style.left = `${Math.random() * 100}%`;
                    particle.style.top = `${Math.random() * 100}%`;
                    const animation = particle.animate([
                        { transform: `translate(0, 0)`, opacity: Math.random() * 0.7 + 0.2 },
                        isDark 
                          ? { transform: `translate(${(Math.random() - 0.5) * 40}px, -150px)`, opacity: 0 }
                          : { transform: `translate(${(Math.random() - 0.5) * 80}px, ${(Math.random() - 0.5) * 80}px)`, opacity: 0 }
                    ], {
                        duration: Math.random() * (isDark ? 15000 : 8000) + (isDark ? 10000 : 5000),
                        iterations: Infinity,
                        easing: isDark ? 'ease-in' : 'cubic-bezier(0.25, 0.1, 0.25, 1.0)'
                    });
                    if (isDark) animation.startTime = Math.random() * -25000;
                }
            };

            const setTheme = (theme) => {
                body.classList.toggle('dark-mode', theme === 'dark');
                localStorage.setItem('ivory-tower-theme-preference', theme);
                createParticles(theme);
            };

            const toggleTheme = () => {
                const currentTheme = body.classList.contains('dark-mode') ? 'light' : 'dark';
                setTheme(currentTheme);
            };

            const loadPreferences = () => {
                const savedTheme = localStorage.getItem('ivory-tower-theme-preference') || 'light';
                setTheme(savedTheme);
            };

            const getParentTextarea = () => {
                try { return window.top?.document.querySelector('#send_textarea'); } 
                catch (e) { return null; }
            };

            const updateParentInput = (text) => {
                const textarea = getParentTextarea();
                if (textarea) {
                    textarea.value = text;
                    textarea.dispatchEvent(new Event('input', { bubbles: true }));
                    showNotification('å†…å®¹å·²å¡«å…¥', 'success');
                } else {
                    showNotification('æœªèƒ½æ‰¾åˆ°è¾“å…¥æ¡†', 'error');
                }
            };

            const clearParentInput = () => {
                 const textarea = getParentTextarea();
                 if (textarea) {
                    textarea.value = '';
                    textarea.dispatchEvent(new Event('input', { bubbles: true }));
                    showNotification('è¾“å…¥æ¡†å·²æ¸…ç©º', 'success');
                 }
            };

            const resetAllSelections = () => {
                document.querySelectorAll('.option-button').forEach(btn => btn.classList.remove('chosen'));
                if (isMultiSelect) submitSelectionBtn.style.display = 'none';
                selectedInOrder = [];
            };

            const showNotification = (message, type = 'success', duration = 2500) => {
                const notification = document.createElement('div');
                notification.className = `notification ${type}`;
                notification.textContent = message;
                document.body.appendChild(notification);
                setTimeout(() => notification.classList.add('show'), 10);
                setTimeout(() => {
                    notification.classList.remove('show');
                    notification.addEventListener('transitionend', () => notification.remove());
                }, duration);
            };

            const setSelectionMode = (isMulti) => {
                isMultiSelect = isMulti;
                resetAllSelections();
                multiSelectBtn.classList.toggle('active', isMulti);
                singleSelectBtn.classList.toggle('active', !isMulti);
                submitSelectionBtn.style.display = 'none';
            };

            const bindOptionButtonEvents = () => {
                const optionButtons = document.querySelectorAll('.option-button');
                optionButtons.forEach((button, index) => {
                    button.dataset.message = button.textContent.trim();
                    button.style.animationDelay = `${0.2 + index * 0.08}s`;
                    button.addEventListener('click', function() {
                        if (isMultiSelect) {
                            this.classList.toggle('chosen');
                            const message = this.dataset.message;
                            if (this.classList.contains('chosen')) {
                                selectedInOrder.push(message);
                            } else {
                                selectedInOrder = selectedInOrder.filter(item => item !== message);
                            }
                            submitSelectionBtn.style.display = selectedInOrder.length > 0 ? 'block' : 'none';
                        } else {
                            optionButtons.forEach(btn => btn.classList.remove('chosen'));
                            this.classList.add('chosen');
                            updateParentInput(this.dataset.message);
                        }
                    });
                });
            };

            themeToggleBtn.addEventListener('click', toggleTheme);
            singleSelectBtn.addEventListener('click', () => setSelectionMode(false));
            multiSelectBtn.addEventListener('click', () => setSelectionMode(true));
            resetOptionsBtn.addEventListener('click', () => {
                resetAllSelections();
                showNotification('é€‰é¡¹å·²é‡ç½®', 'success');
            });
            clearInputBtn.addEventListener('click', clearParentInput);
            submitSelectionBtn.addEventListener('click', () => {
                if (selectedInOrder.length === 0) {
                    showNotification('è¯·è‡³å°‘é€‰æ‹©ä¸€é¡¹', 'error');
                    return;
                }
                updateParentInput(selectedInOrder.join('\n\n'));
            });

            populateOptionsFromRawData();
            bindOptionButtonEvents();
            loadPreferences();
            setSelectionMode(false);
        });
    </script>
