                    if (seeds && seeds.trim()) {
                        seedsDetails.style.display = 'block';
                        seedsContent.innerHTML = '';
                        const seedsLines = seeds.trim().split('\n').filter(line => line.trim() !== '');

                        seedsLines.forEach(line => {
                            const p = document.createElement('p');
                            const trimmedLine = line.trim();
                            if (trimmedLine.startsWith('-')) {
                                p.textContent = trimmedLine.substring(1).trim();
                            } else {
                                p.textContent = trimmedLine;
                                p.classList.add('seed-title');
                            }
                            seedsContent.appendChild(p);
                        });
                    } else {
                        seedsDetails.style.display = 'none';
                    }
                } else {
                    console.error("æ— æ³•è§£æžraw-dataä¸­çš„å†…å®¹ã€‚");
                }
            }

            const body = document.body;
            const header = document.getElementById('meow-toggle-header');
            const collapsibleContent = document.getElementById('meow-collapsible-content');
            const buttonText = document.getElementById('expand-button-text');
            const seedsDetails = document.querySelector('.seeds-details');
            const particlesContainer = document.getElementById('particles-js');
            const themeToggle = document.getElementById('theme-toggle-checkbox');

            function createParticles(theme) {
                particlesContainer.innerHTML = '';
                const isDark = theme === 'dark';
                const particleCount = isDark ? 25 : 40;
                const lightParticleColors = ['rgba(192, 168, 133, 0.6)', 'rgba(113, 128, 150, 0.6)', 'rgba(255, 255, 255, 0.7)'];
                const darkParticleColors = ['rgba(204, 165, 106, 0.5)'];
                const colors = isDark ? darkParticleColors : lightParticleColors;

                for (let i = 0; i < particleCount; i++) {
                    let particle = document.createElement('div');
                    particle.style.position = 'absolute';
                    particle.style.borderRadius = '50%';
                    const color = colors[Math.floor(Math.random() * colors.length)];
                    particle.style.background = color;
                    if (isDark) particle.style.boxShadow = `0 0 8px ${color}, 0 0 4px ${color}`;

                    const size = isDark ? (Math.random() * 2.5 + 1) : (Math.random() * 3 + 1.5);
                    particle.style.width = `${size}px`;
                    particle.style.height = `${size}px`;
                    particle.style.left = `${Math.random() * 100}%`;
                    particle.style.top = `${Math.random() * 100}%`;

                    const animation = particle.animate([
                        { transform: `translate(0, 0)`, opacity: Math.random() * 0.7 + 0.2 },
                        isDark 
                          ? { transform: `translate(${(Math.random() - 0.5) * 40}px, -150px)`, opacity: 0 }
                          : { transform: `translate(${(Math.random() - 0.5) * 80}px, ${(Math.random() - 0.5) * 80}px)`, opacity: 0 }
                    ], {
                        duration: Math.random() * (isDark ? 15000 : 8000) + (isDark ? 10000 : 5000),
                        iterations: Infinity,
                        easing: isDark ? 'ease-in' : 'cubic-bezier(0.25, 0.1, 0.25, 1.0)'
                    });
                    if (isDark) animation.startTime = Math.random() * -25000;
                }
            }

            function setTheme(theme) {
                body.classList.toggle('dark-mode', theme === 'dark');
                themeToggle.checked = theme === 'dark';
                localStorage.setItem('summary-theme-preference', theme);
                createParticles(theme);
            }

            header.addEventListener('click', () => {
                const isOpen = collapsibleContent.classList.contains('open');
                collapsibleContent.classList.toggle('open');
                buttonText.textContent = isOpen ? 'å©å“å¡”é—¨â€¦' : 'å°å­˜å›žå“â€¦';
            });

            seedsDetails.addEventListener('click', (event) => {
                 event.stopPropagation();
            });

            themeToggle.addEventListener('change', () => {
                setTheme(themeToggle.checked ? 'dark' : 'light');
            });

            parseAndRender();
            const savedTheme = localStorage.getItem('summary-theme-preference') || 'light';
            setTheme(savedTheme);
        });
    </script>
</body>
</html>
