    <button class="send-button" id="send-button" onclick="sendSelection()" disabled>
      Execute
    </button>
  </div>
</div>

<script>
  let selectedOptionText = null, selectedElement = null;

  function selectOption(el, txt) {
    const disp = document.getElementById('selected-choice-output'),
          btn  = document.getElementById('send-button');
    selectedElement?.classList.remove('selected');
    el.classList.add('selected');
    selectedElement = el; selectedOptionText = txt;
    disp.textContent = `> ${txt}`;
    disp.querySelector('.placeholder')?.remove();
    btn.disabled = false;
  }

  function sendSelection() {
    if (!selectedOptionText) return alert('ERROR: NO COMMAND SELECTED.');
    try {
      if (window.parent?.SillyTavern?.sendOocMessage) {
        window.parent.SillyTavern.sendOocMessage(selectedOptionText);
        const btn = document.getElementById('send-button');
        btn.textContent = 'SENT!'; btn.disabled = true;
        setTimeout(() => {
          btn.textContent = 'Execute';
          selectedElement?.classList.remove('selected');
          document.getElementById('selected-choice-output').innerHTML =
            '<span class="placeholder">// WAITING FOR INPUT...</span>';
          selectedOptionText = null; selectedElement = null;
        }, 1500);
      } else {
        const ta = window.parent.document.querySelector('#send_textarea'),
              sb = window.parent.document.querySelector('#send_but');
        if (ta && sb) {
          ta.value = selectedOptionText;
          ta.dispatchEvent(new Event('input',{bubbles:true}));
          setTimeout(()=> sb.click(), 100);
        } else {
          alert('CRITICAL ERROR: Cannot find parent elements.');
        }
      }
    } catch(e) { alert(`EXCEPTION: ${e.message}`); console.error(e); }
  }
</script>

</body>
</html>
