btn.addEventListener('click', (e)=>{ e.stopPropagation(); toggleTheme(); });

// åˆå§‹åŒ–ä¸»é¢˜
applyTheme(localStorage.getItem('prologueTheme') ?? 'light');

/* åŠ¨æ€æ•°æ®ç›‘å¬ */
new MutationObserver(render).observe(raw,{childList:true,characterData:true,subtree:true});
