    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // --- DOM Element Selection ---
            const elements = {
                original: document.getElementById('original-text'),
                translation: document.getElementById('translation-text'),
                source: document.getElementById('source-text'),
                cursor: document.getElementById('cursor'),
                indicator: document.getElementById('indicator'),
                display: document.getElementById('pagerDisplay'),
                scrollUpBtn: document.getElementById('scrollUp'),
                scrollDownBtn: document.getElementById('scrollDown'),
                rawData: document.getElementById('pager-raw-data')
            };

            const scrollStep = 30;
            let typingInterval; // Keep track of the interval to clear it if needed

            // --- Core Logic: Parsing, Typing, Scrolling ---

            /**
             * Parses multi-line text into a structured object.
             * @param {string} content - The raw text content.
             * @returns {{original: string, translation: string, source: string}|null}
             */
            function parseContent(content) {
                const lines = (content || '').split('\n').map(l => l.trim()).filter(Boolean);
                if (!lines.length) return null;
                // Last line is the source, remove leading dash variants
                const source = (lines.pop() || '').replace(/^[â€”â€•â”€-]\s*/, '').trim();
                return {
                    original: lines.length > 1 ? lines[0] : '',
                    translation: lines.length > 1 ? lines[1] : (lines[0] || ''),
                    source: source
                };
            }

            /**
             * Updates the state of navigation buttons based on scroll position.
             */
            function updateNavButtonsState() {
                const { scrollTop, scrollHeight, clientHeight } = elements.display;
                elements.scrollUpBtn.classList.toggle('disabled', scrollTop <= 0);
                elements.scrollDownBtn.classList.toggle('disabled', scrollTop + clientHeight >= scrollHeight - 1);
            }

            /**
             * Checks if the content overflows the display area and updates buttons.
             */
            function checkForOverflow() {
                if (elements.display.scrollHeight > elements.display.clientHeight) {
                    updateNavButtonsState();
                }
            }

            /**
             * Simulates a typewriter effect for a given text.
             * @param {HTMLElement} element - The element to type into.
             * @param {string} text - The text to type.
             * @param {number} speed - The typing speed in milliseconds.
             * @param {function} callback - Function to call when typing is complete.
             */
            function typeWriter(element, text, speed, callback) {
                let i = 0;
                element.innerHTML = '';
                element.parentNode.appendChild(elements.cursor);
                elements.cursor.style.display = 'inline-block';

                clearInterval(typingInterval); // Clear previous interval
                typingInterval = setInterval(() => {
                    if (i < text.length) {
                        element.innerHTML += text.charAt(i);
                        i++;
                    } else {
                        clearInterval(typingInterval);
                        if (callback) callback();
                    }
                }, speed);
            }

            /**
             * Main function to orchestrate the entire message display sequence.
             */
            function runDisplaySequence() {
                const parsedData = parseContent(elements.rawData.textContent);
                if (!parsedData) return;

                // Reset UI state
                elements.original.innerHTML = '';
                elements.translation.innerHTML = '';
                elements.source.innerHTML = '';
                elements.source.style.opacity = '0';
                elements.indicator.style.display = 'block';
                elements.indicator.style.animation = 'breathe 1.5s ease-in-out infinite';

                // Start sequence after a delay
                setTimeout(() => {
                    elements.indicator.style.animation = 'none';
                    elements.indicator.style.opacity = '0';

                    typeWriter(elements.original, parsedData.original, 60, () => {
                        typeWriter(elements.translation, parsedData.translation, 70, () => {
                            elements.cursor.style.display = 'none';
                            elements.source.textContent = 'â€” ' + parsedData.source;
                            elements.source.style.opacity = '1';

                            setTimeout(checkForOverflow, 100);
                        });
                    });
                }, 2000);
            }

            // --- Event Listeners & Initialization ---
            elements.scrollUpBtn.addEventListener('click', () => { elements.display.scrollTop -= scrollStep; });
            elements.scrollDownBtn.addEventListener('click', () => { elements.display.scrollTop += scrollStep; });
            elements.display.addEventListener('scroll', updateNavButtonsState);

            // Setup MutationObserver to re-run the sequence on data change
            const observer = new MutationObserver(runDisplaySequence);
            observer.observe(elements.rawData, {
                childList: true,
                characterData: true,
                subtree: true
            });

            // Initial run
            runDisplaySequence();
        });
    </script>
