  localStorage.setItem('prologueTheme', mode);
}

function toggleTheme(){
  // å…ˆåŠ â€œthemingâ€åšä¸€ä¸ªè½»å¾®ç¼“å†²ï¼Œè®©è¿‡æ¸¡æ›´æŸ”å’Œ
  card.classList.add('theming');
