                scrollDownBtn: document.getElementById('wheel-down'),
                rawData: document.getElementById('raw-data')
            };
            const scrollStep = 40;
            let typingInterval;
            function parseContent(content) {
                const lines = (content || '').split('\n').map(l => l.trim()).filter(Boolean);
                if (!lines.length) return null;
                const source = (lines.pop() || '').replace(/^[â€”â€•â”€-]\s*/, '').trim();
                return { original: lines.length > 1 ? lines[0] : '', translation: lines.length > 1 ? lines[1] : (lines[0] || ''), source: source };
            }
            function updateNavButtonsState() {
                requestAnimationFrame(() => {
                    const { scrollTop, scrollHeight, clientHeight } = elements.display;
                    elements.scrollUpBtn.classList.toggle('disabled', scrollTop <= 0);
                    elements.scrollDownBtn.classList.toggle('disabled', scrollTop + clientHeight >= scrollHeight - 1);
                });
            }
            function checkForOverflow() { if (elements.display.scrollHeight > elements.display.clientHeight) { updateNavButtonsState(); } }
            function typeWriter(element, text, speed, callback) {
                let i = 0;
                element.innerHTML = '';
                element.parentNode.appendChild(elements.cursor);
                elements.cursor.style.display = 'inline-block';
                clearInterval(typingInterval);
                typingInterval = setInterval(() => {
                    if (i < text.length) {
                        element.innerHTML += text.charAt(i);
                        updateNavButtonsState();
                        i++;
                    } else {
                        clearInterval(typingInterval);
                        if (callback) callback();
                    }
                }, speed);
            }
            function runDisplaySequence() {
                const parsedData = parseContent(elements.rawData.textContent);
                if (!parsedData) return;
                elements.original.innerHTML = '';
                elements.translation.innerHTML = '';
                elements.source.innerHTML = '';
                elements.source.style.opacity = '0';
                elements.display.scrollTop = 0;
                elements.scrollUpBtn.classList.add('disabled');
                elements.scrollDownBtn.classList.add('disabled');
                typeWriter(elements.original, parsedData.original, 60, () => {
                    typeWriter(elements.translation, parsedData.translation, 70, () => {
                        elements.cursor.style.display = 'none';
                        elements.source.textContent = 'â€” ' + parsedData.source;
                        elements.source.style.opacity = '1';
                        setTimeout(checkForOverflow, 100);
                    });
                });
            }
            elements.scrollUpBtn.addEventListener('click', () => { elements.display.scrollTop -= scrollStep; });
            elements.scrollDownBtn.addEventListener('click', () => { elements.display.scrollTop += scrollStep; });
            elements.display.addEventListener('scroll', updateNavButtonsState);
            const observer = new MutationObserver(runDisplaySequence);
            observer.observe(elements.rawData, { childList: true, characterData: true, subtree: true });
            runDisplaySequence();
        });
    </script>
