    // æž„å»ºæˆåŠŸæ¶ˆæ¯
    let message = `âœ… ç§»åŠ¨æ“ä½œå®Œæˆï¼\n\n`;
    message += `æˆåŠŸå°† ${movedCount} ä¸ªå±€éƒ¨è„šæœ¬ç§»åŠ¨åˆ°å…¨å±€åŒºåŸŸã€‚\n\n`;

    // æ·»åŠ ç§»åŠ¨çš„è„šæœ¬åç§°åˆ—è¡¨ï¼ˆé™åˆ¶æ˜¾ç¤ºå‰5ä¸ªï¼‰
    if (movedScripts && movedScripts.length > 0) {
