  } catch (error) {
    console.error(`[FilterGroup] æ˜¾ç¤ºæˆåŠŸæ¶ˆæ¯æ—¶å‡ºé”™:`, error);
    // å¤±è´¥æ—¶æ˜¾ç¤ºç®€å•æ¶ˆæ¯
