            } catch (error) {
                document.getElementById('quick-reply-list').innerHTML = `<div class="empty-state">åŠ è½½å¤±è´¥: ${error.message}</div>`;
                debugLog('å¿«é€Ÿå›žå¤åŠ è½½é”™è¯¯: ' + error.stack);
            }
        }

        function togglePreset(name) {
            if (selectedPresets.includes(name)) {
                selectedPresets = selectedPresets.filter(n => n !== name);
            } else {
                selectedPresets.push(name);
            }
            debugLog(`é¢„è®¾é€‰æ‹©: ${selectedPresets.length} ä¸ª`);
        }

        function toggleRegex(id) {
            if (selectedRegexes.includes(id)) {
                selectedRegexes = selectedRegexes.filter(n => n !== id);
            } else {
                selectedRegexes.push(id);
            }
            debugLog(`æ­£åˆ™é€‰æ‹©: ${selectedRegexes.length} ä¸ª`);
        }

        function toggleQuickReplySet(name) {
            if (selectedQuickReplySets.includes(name)) {
                selectedQuickReplySets = selectedQuickReplySets.filter(n => n !== name);
            } else {
                selectedQuickReplySets.push(name);
            }
            debugLog(`å¿«é€Ÿå›žå¤é›†é€‰æ‹©: ${selectedQuickReplySets.length} ä¸ª`);
        }

        async function createPackage() {
            if (selectedPresets.length === 0 && selectedRegexes.length === 0 && selectedQuickReplySets.length === 0) {
                showStatus('è¯·è‡³å°‘é€‰æ‹©ä¸€ä¸ªé¡¹ç›®', 'error');
                return;
            }

            const packageName = document.getElementById('package-name').value.trim();
            if (!packageName) {
                showStatus('è¯·è¾“å…¥åŒ…åç§°', 'error');
                return;
            }

            const tagPrefix = document.getElementById('tag-prefix').value.trim();
            const regexNoOverwrite = document.getElementById('regex-no-overwrite').checked;

            try {
                showStatus('ç”Ÿæˆæ‰“åŒ…æ–‡ä»¶...', 'info');
                showProgress(10);

                const packageObj = {
                    name: packageName,
                    created: new Date().toISOString(),
                    tag_prefix: tagPrefix,
                    presets: {},
                    regexes: {},
                    quick_reply_sets: {}
                };

                // æ‰“åŒ…é¢„è®¾
                for (const presetName of selectedPresets) {
                    const preset = TavernHelper.getPreset(presetName);
                    if (preset) {
                        const finalName = tagPrefix ? `${tagPrefix}${presetName}` : presetName;
                        packageObj.presets[finalName] = preset;
                        debugLog(`å·²æ‰“åŒ…é¢„è®¾: ${finalName}`);
                    }
                }
                showProgress(30);

                // æ‰“åŒ…æ­£åˆ™
                const allRegexes = TavernHelper.getTavernRegexes();
                for (const regexId of selectedRegexes) {
                    const regex = allRegexes.find(r => r.id === regexId);
                    if (regex) {
                        let finalName = regex.script_name;
                        if (tagPrefix) {
                            finalName = `${tagPrefix}${finalName}`;
                        }
                        if (regexNoOverwrite) {
                            finalName = `${finalName}_æ–°`;
                        }
                        const regexCopy = { ...regex, script_name: finalName };
                        delete regexCopy.id; // ID will be regenerated on import
                        packageObj.regexes[finalName] = regexCopy;
                        debugLog(`å·²æ‰“åŒ…æ­£åˆ™: ${finalName}`);
                    }
                }
                showProgress(60);

                // æ‰“åŒ…å¿«é€Ÿå›žå¤
                for (const setName of selectedQuickReplySets) {
                    const finalSetName = tagPrefix ? `${tagPrefix}${setName}` : setName;
                    const qrListJson = await TavernHelper.triggerSlash(`/qr-list "${setName}"`);
                    const qrList = JSON.parse(qrListJson);
                    const repliesArray = [];
                    for(const qrLabel of qrList) {
                        const qrDataJson = await TavernHelper.triggerSlash(`/qr-get set="${setName}" label="${qrLabel}"`);
                        const qrData = JSON.parse(qrDataJson);
                        repliesArray.push(qrData);
                    }
                    packageObj.quick_reply_sets[finalSetName] = repliesArray;
                    debugLog(`å·²æ‰“åŒ…å¿«é€Ÿå›žå¤é›†: ${finalSetName}`);
                }
                showProgress(80);

                const jsonStr = JSON.stringify(packageObj, null, 2);
                const blob = new Blob([jsonStr], { type: 'application/json' });
                const url = URL.createObjectURL(blob);

                const a = document.createElement('a');
                a.href = url;
                a.download = packageName + '.json';
                document.body.appendChild(a);
                a.click();
                document.body.removeChild(a);
                URL.revokeObjectURL(url);

                showProgress(100);
                showStatus('æ‰“åŒ…å®Œæˆ', 'success');
                debugLog(`æ‰“åŒ…å®Œæˆ: ${Object.keys(packageObj.presets).length} é¢„è®¾, ${Object.keys(packageObj.regexes).length} æ­£åˆ™, ${Object.keys(packageObj.quick_reply_sets).length} å¿«é€Ÿå›žå¤é›†`);
            } catch (error) {
                showProgress(100);
                showStatus('æ‰“åŒ…å¤±è´¥: ' + error.message, 'error');
                debugLog('æ‰“åŒ…é”™è¯¯: ' + error.stack);
            }
        }

        function handleFileSelect(event) {
            const file = event.target.files[0];
            if (!file) {
                debugLog('æœªé€‰æ‹©æ–‡ä»¶');
                return;
            }

            debugLog(`é€‰æ‹©æ–‡ä»¶: ${file.name}`);
            event.target.value = '';

            const reader = new FileReader();
            reader.onload = function(e) {
                try {
                    packageData = JSON.parse(e.target.result);
                    debugLog('æ–‡ä»¶è§£æžæˆåŠŸ');
                    displayPackageInfo(packageData);
                } catch (error) {
                    showStatus('æ–‡ä»¶æ ¼å¼é”™è¯¯', 'error');
                    debugLog('è§£æžé”™è¯¯: ' + error.message);
                    packageData = null;
                    document.getElementById('package-info').style.display = 'none';
                }
            };

            reader.onerror = function() {
                showStatus('æ–‡ä»¶è¯»å–å¤±è´¥', 'error');
                debugLog('è¯»å–é”™è¯¯');
            };

            reader.readAsText(file);
        }

        function displayPackageInfo(data) {
            const detailsEl = document.getElementById('package-details');
            let html = `<p><strong>åç§°:</strong> ${data.name || 'æœªçŸ¥'}</p>`;
            html += `<p><strong>åˆ›å»º:</strong> ${data.created ? new Date(data.created).toLocaleString() : 'æœªçŸ¥'}</p>`;
            if (data.tag_prefix) {
                html += `<p><strong>æ ‡ç­¾:</strong> ${data.tag_prefix}</p>`;
            }
            html += `<p><strong>é¢„è®¾:</strong> ${data.presets ? Object.keys(data.presets).length : 0} ä¸ª</p>`;
            html += `<p><strong>æ­£åˆ™:</strong> ${data.regexes ? Object.keys(data.regexes).length : 0} ä¸ª</p>`;
            html += `<p><strong>å¿«é€Ÿå›žå¤é›†:</strong> ${data.quick_reply_sets ? Object.keys(data.quick_reply_sets).length : 0} ä¸ª</p>`;

            detailsEl.innerHTML = html;
            document.getElementById('package-info').style.display = 'block';
            debugLog(`åŒ…ä¿¡æ¯: ${Object.keys(data.presets || {}).length} é¢„è®¾, ${Object.keys(data.regexes || {}).length} æ­£åˆ™, ${Object.keys(data.quick_reply_sets || {}).length} å¿«é€Ÿå›žå¤é›†`);
        }

        async function importPackage() {
            if (!packageData) {
                showStatus('è¯·å…ˆé€‰æ‹©æ–‡ä»¶', 'error');
                return;
            }

            try {
                showStatus('å¯¼å…¥ä¸­...', 'info');
                let totalItems = (packageData.presets ? Object.keys(packageData.presets).length : 0) + 
                                 (packageData.regexes ? Object.keys(packageData.regexes).length : 0) +
