        if (typeof currentChunkSizeSetting !== 'undefined' && !isNaN(currentChunkSizeSetting) && currentChunkSizeSetting >= 2 && currentChunkSizeSetting % 2 === 0) {
            chunkSize = currentChunkSizeSetting;
        } else {
