                        if (selectedSummaryType === 'small') customSmallChunkSizeSetting = chunkSize;
                        else customLargeChunkSizeSetting = chunkSize;
                        logDebug(`è‡ªå®šä¹‰${summaryTypeName}é—´éš”å·²é€šè¿‡UIäº¤äº’ä¿å­˜:`, chunkSize);
                    } catch (error) { logError(`ä¿å­˜è‡ªå®šä¹‰${summaryTypeName}é—´éš”å¤±è´¥ (localStorage):`, error); }
                }
            } else {
                if (calledFrom === "handleAutoSummarize_UI" || calledFrom === "ui_interaction") {
                    showToastr("warning", `è¾“å…¥çš„${summaryTypeName}é—´éš” "${uiChunkSizeVal}" æ— æ•ˆã€‚å°†ä½¿ç”¨ä¹‹å‰ä¿å­˜çš„è®¾ç½®æˆ–é»˜è®¤å€¼ (${chunkSize} å±‚)ã€‚`);
