        if (currentRangeStart !== -1) { if (inSummarizedBlock) { summarizedRanges.push(`${currentRangeStart + 1}-${totalMessages}`); } else { unsummarizedRanges.push(`${currentRangeStart + 1}-${totalMessages}`); } }
        let statusText = "";
        if (summarizedRanges.length > 0) statusText += `å·²æ€»ç»“æ¥¼å±‚: ${summarizedRanges.join(', ')}. `;
        if (unsummarizedRanges.length > 0) statusText += `æœªæ€»ç»“æ¥¼å±‚: ${unsummarizedRanges.join(', ')}.`;
        if (statusText.trim() === "") statusText = allChatMessages.every(m => m.summarized) ? "æ‰€æœ‰æ¥¼å±‚å·²æ€»ç»“å®Œæ¯•ã€‚" : "ç­‰å¾…æ€»ç»“...";
        $summaryStatusDisplay.text(statusText.trim() || "çŠ¶æ€æœªçŸ¥ã€‚");
    }
