// å­—ä½“æ¥æºæ ‡è¯†å‡½æ•°
function getFontDisplayName(fontName, isFromImport = false, isFromTTF = false, isFromURL = false) {
    if (!fontName) return fontName;

    if (isFromTTF) {
        return `${fontName} [TTF]`;
    } else if (isFromImport) {
        return `${fontName} [IMPORT]`;
    } else if (isFromURL) {
        return `${fontName} [URL]`;
    }
    return fontName;
}

// å­—ä½“æ¥æºæ£€æµ‹å‡½æ•°
function detectFontSource(fontName) {
    // æ£€æŸ¥æ˜¯å¦ä¸ºæœ¬åœ°TTFå­—ä½“
    const isLocalFont = state.localFonts.some(font => font.fontName === fontName);
    if (isLocalFont) return 'TTF';

    // æ£€æŸ¥å­—ä½“åç§°ä¸­æ˜¯å¦å·²åŒ…å«æ ‡è¯†
    if (fontName.includes('[TTF]')) return 'TTF';
    if (fontName.includes('[IMPORT]')) return 'IMPORT';
    if (fontName.includes('[URL]')) return 'URL';

    // é€šè¿‡æ˜ å°„å…³ç³»æ£€æŸ¥å­—ä½“æ¥æº
    const fontUrl = state.fontFamilyUrlMap && state.fontFamilyUrlMap[fontName];
    if (fontUrl) {
        // æ£€æŸ¥æ˜¯å¦ä¸º@importç±»åž‹çš„URL
        if (fontUrl.toLowerCase().includes('.css') || 
            state.fontImportUrls.some(url => url === fontUrl && (
                url.toLowerCase().includes('.css') || 
                url.includes('fonts.googleapis.com') ||
                url.includes('fontsapi.zeoseven.com')
            ))) {
            return 'IMPORT';
        }

        // æ£€æŸ¥æ˜¯å¦ä¸ºç›´æŽ¥å­—ä½“æ–‡ä»¶URL (@font-faceæˆ–ç›´æŽ¥URLæ ¼å¼)
        if (fontUrl.match(/\.(ttf|otf|woff2?)(\?.*)?$/i)) {
            return 'URL';
        }

        // å…¶ä»–URLç±»åž‹é»˜è®¤ä¸ºIMPORT
        return 'IMPORT';
    }

    // é€šè¿‡å­—ä½“åç§°æ¨¡å¼æ£€æŸ¥
    if (/^å­—ä½“\d+$/.test(fontName)) {
        return 'URL'; // æ ¼å¼3ç”Ÿæˆçš„å­—ä½“åç§°
    }

    if (/^Import-\d+-/.test(fontName)) {
        return 'IMPORT'; // ä¸´æ—¶Importå­—ä½“åç§°
    }

    // æœ€åŽæ£€æŸ¥fontImportUrlsä¸­æ˜¯å¦æœ‰ç›¸å…³URL
    const isImportFont = state.fontImportUrls.some(url => {
        return url.toLowerCase().includes(fontName.toLowerCase()) ||
               url.toLowerCase().includes('.css') ||
               url.includes('fonts.googleapis.com') ||
               url.includes('fontsapi.zeoseven.com');
    });

    if (isImportFont) return 'IMPORT';

    return 'System';
}

// ========= URLåŽ†å²è®°å½•ç®¡ç† =========
const urlHistory = {
    maxItems: 10, // æœ€å¤šä¿å­˜10æ¡è®°å½•

    // èŽ·å–åŽ†å²è®°å½•
    getHistory() {
        try {
            const history = localStorage.getItem(LOCAL_STORAGE_KEY_URL_HISTORY);
            return history ? JSON.parse(history) : [];
        } catch (e) {
            console.error('Font Manager: åŠ è½½URLåŽ†å²è®°å½•å¤±è´¥:', e);
            return [];
        }
    },

    // ä¿å­˜åŽ†å²è®°å½•
    saveHistory(historyArray) {
        try {
            localStorage.setItem(LOCAL_STORAGE_KEY_URL_HISTORY, JSON.stringify(historyArray));
        } catch (e) {
            console.error('Font Manager: ä¿å­˜URLåŽ†å²è®°å½•å¤±è´¥:', e);
        }
    },

    // æ·»åŠ æ–°URL
    addUrl(url) {
        if (!url || !url.trim()) return;

        const history = this.getHistory();
        const trimmedUrl = url.trim();

        // ç§»é™¤å·²å­˜åœ¨çš„ç›¸åŒURL
        const filteredHistory = history.filter(item => item !== trimmedUrl);

        // æ·»åŠ åˆ°å¼€å¤´
        filteredHistory.unshift(trimmedUrl);

        // é™åˆ¶æ•°é‡
        if (filteredHistory.length > this.maxItems) {
            filteredHistory.splice(this.maxItems);
        }

        this.saveHistory(filteredHistory);
    },

    // åˆ é™¤URL
    removeUrl(url) {
        const history = this.getHistory();
        const filteredHistory = history.filter(item => item !== url);
        this.saveHistory(filteredHistory);
    },

    // æ¸…ç©ºåŽ†å²è®°å½•
    clearHistory() {
        this.saveHistory([]);
    }
};

// ========= HLJSè¾…åŠ©å‡½æ•° =========
function hexToRgba(hex, alpha) {
    if (!hex) return 'rgba(0,0,0,1)';

    // ç§»é™¤ # ç¬¦å·
    hex = hex.replace('#', '');

    // å¤„ç†3ä½å’Œ6ä½hexå€¼
    if (hex.length === 3) {
        hex = hex.split('').map(char => char + char).join('');
    }

    const r = parseInt(hex.substr(0, 2), 16);
    const g = parseInt(hex.substr(2, 2), 16);
    const b = parseInt(hex.substr(4, 2), 16);

    return `rgba(${r}, ${g}, ${b}, ${alpha})`;
}

function getBackgroundStyleFromOptions(imageUrl, displayMode, position) {
    if (!imageUrl || imageUrl === 'none') {
        return {
            'background-image': 'none',
            'background-size': '',
            'background-position': '',
            'background-repeat': '',
            'background-attachment': ''
        };
    }

    let backgroundSize, backgroundPosition, backgroundRepeat;

    switch (displayMode) {
        case 'cover':
            backgroundSize = 'cover';
            backgroundRepeat = 'no-repeat';
            break;
        case 'contain':
            backgroundSize = 'contain';
            backgroundRepeat = 'no-repeat';
            break;
        case 'tile':
            backgroundSize = 'auto';
            backgroundRepeat = 'repeat';
            break;
        case 'stretch':
            backgroundSize = '100% 100%';
            backgroundRepeat = 'no-repeat';
            break;
        default:
            backgroundSize = 'cover';
            backgroundRepeat = 'no-repeat';
    }

    switch (position) {
        case 'center':
            backgroundPosition = 'center center';
            break;
        case 'top':
            backgroundPosition = 'center top';
            break;
        case 'bottom':
            backgroundPosition = 'center bottom';
            break;
        case 'left':
            backgroundPosition = 'left center';
            break;
        case 'right':
            backgroundPosition = 'right center';
            break;
        case 'top-left':
            backgroundPosition = 'left top';
            break;
        case 'top-right':
            backgroundPosition = 'right top';
            break;
        case 'bottom-left':
            backgroundPosition = 'left bottom';
            break;
        case 'bottom-right':
            backgroundPosition = 'right bottom';
            break;
        default:
            backgroundPosition = 'center center';
    }

    return {
        'background-image': `url(${imageUrl})`,
        'background-size': backgroundSize,
        'background-position': backgroundPosition,
        'background-repeat': backgroundRepeat,
        'background-attachment': 'local'
    };
}

// åˆ›å»ºå¸¦åŽ†å²è®°å½•çš„URLè¾“å…¥æ¡†
function createUrlInputWithHistory(inputId, currentValue = '', onUrlChange = null) {
    const container = docContext.createElement('div');
    container.style.display = 'flex';
    container.style.flexDirection = 'column';
    container.style.gap = '6px';
    container.style.flex = '1';
    container.style.position = 'relative';

    // è¾“å…¥æ¡†å®¹å™¨
    const inputContainer = docContext.createElement('div');
    inputContainer.style.position = 'relative';
    inputContainer.style.width = '100%';

    // URLè¾“å…¥æ¡†
    const urlInput = docContext.createElement('input');
    urlInput.type = 'url';
    urlInput.id = inputId;
    urlInput.placeholder = 'è¾“å…¥å›¾ç‰‡URL';
    urlInput.value = currentValue;
    urlInput.style.width = '100%';
    urlInput.style.padding = '4px 8px';
    urlInput.style.backgroundColor = '#333';
    urlInput.style.color = '#ddd';
    urlInput.style.border = '1px solid #555';
    urlInput.style.borderRadius = '4px';
    urlInput.style.boxSizing = 'border-box';

    // åŽ†å²è®°å½•ä¸‹æ‹‰æ¡†
    const historyDropdown = docContext.createElement('div');
    historyDropdown.style.position = 'absolute';
    historyDropdown.style.top = '100%';
    historyDropdown.style.left = '0';
    historyDropdown.style.right = '0';
    historyDropdown.style.backgroundColor = '#2a2a2c';
    historyDropdown.style.border = '1px solid #555';
    historyDropdown.style.borderTop = 'none';
    historyDropdown.style.borderRadius = '0 0 4px 4px';
    historyDropdown.style.maxHeight = '200px';
    historyDropdown.style.overflowY = 'auto';
    historyDropdown.style.zIndex = '1000';
    historyDropdown.style.display = 'none';

    // æ¸…é™¤æŒ‰é’®
    const clearButton = docContext.createElement('button');
    clearButton.textContent = 'æ¸…é™¤';
    clearButton.className = 'font-manager-modal-button';
    clearButton.style.padding = '4px 8px';
    clearButton.style.fontSize = '12px';
    clearButton.style.margin = '0';
    clearButton.style.backgroundColor = '#dc3545';
    clearButton.style.alignSelf = 'flex-start';
    clearButton.style.marginTop = '2px';

    // æ›´æ–°åŽ†å²è®°å½•æ˜¾ç¤º
    function updateHistoryDropdown() {
        const history = urlHistory.getHistory();
        historyDropdown.innerHTML = '';

        if (history.length === 0) {
            const emptyItem = docContext.createElement('div');
            emptyItem.textContent = 'æš‚æ— åŽ†å²è®°å½•';
            emptyItem.style.padding = '8px 12px';
            emptyItem.style.color = '#888';
            emptyItem.style.fontSize = '12px';
            historyDropdown.appendChild(emptyItem);
            return;
        }

        history.forEach(url => {
            const historyItem = docContext.createElement('div');
            historyItem.style.padding = '8px 12px';
            historyItem.style.cursor = 'pointer';
            historyItem.style.borderBottom = '1px solid #444';
            historyItem.style.fontSize = '12px';
            historyItem.style.wordBreak = 'break-all';
            historyItem.style.transition = 'background-color 0.2s';

            // æˆªæ–­é•¿URLæ˜¾ç¤º
            const displayUrl = url.length > 50 ? url.substring(0, 47) + '...' : url;
            historyItem.textContent = displayUrl;
            historyItem.title = url; // å®Œæ•´URLä½œä¸ºtooltip

            // é¼ æ ‡æ‚¬åœæ•ˆæžœ
            historyItem.addEventListener('mouseenter', () => {
                historyItem.style.backgroundColor = '#404040';
            });
            historyItem.addEventListener('mouseleave', () => {
                historyItem.style.backgroundColor = 'transparent';
            });

            // ç‚¹å‡»é€‰æ‹©
            historyItem.addEventListener('click', (e) => {
                e.stopPropagation();
                urlInput.value = url;
                urlInput.dispatchEvent(new Event('input'));
                historyDropdown.style.display = 'none';
            });

            // é•¿æŒ‰åˆ é™¤ (ä½¿ç”¨å³é”®èœå•æ¨¡æ‹Ÿ)
            historyItem.addEventListener('contextmenu', (e) => {
                e.preventDefault();
                if (confirm(`ç¡®å®šè¦åˆ é™¤è¿™æ¡åŽ†å²è®°å½•å—ï¼Ÿ\n\n${url}`)) {
                    urlHistory.removeUrl(url);
                    updateHistoryDropdown();
                }
            });

            // ç§»åŠ¨ç«¯é•¿æŒ‰æ”¯æŒ
            let pressTimer = null;
            historyItem.addEventListener('touchstart', (e) => {
                pressTimer = setTimeout(() => {
                    if (confirm(`ç¡®å®šè¦åˆ é™¤è¿™æ¡åŽ†å²è®°å½•å—ï¼Ÿ\n\n${url}`)) {
                        urlHistory.removeUrl(url);
                        updateHistoryDropdown();
                    }
                }, 800); // é•¿æŒ‰800ms
            });
            historyItem.addEventListener('touchend', () => {
                if (pressTimer) {
                    clearTimeout(pressTimer);
                    pressTimer = null;
                }
            });
            historyItem.addEventListener('touchmove', () => {
                if (pressTimer) {
                    clearTimeout(pressTimer);
                    pressTimer = null;
                }
            });

            historyDropdown.appendChild(historyItem);
        });

        // æ·»åŠ æ¸…ç©ºåŽ†å²è®°å½•é€‰é¡¹
        if (history.length > 0) {
            const clearAllItem = docContext.createElement('div');
clearAllItem.innerHTML = '<i class="fas fa-trash-alt"></i> æ¸…ç©ºæ‰€æœ‰åŽ†å²è®°å½•';
            clearAllItem.style.padding = '8px 12px';
            clearAllItem.style.cursor = 'pointer';
            clearAllItem.style.color = '#ff6b6b';
            clearAllItem.style.fontSize = '12px';
            clearAllItem.style.textAlign = 'center';
            clearAllItem.style.borderTop = '1px solid #555';
            clearAllItem.style.transition = 'background-color 0.2s';

            clearAllItem.addEventListener('mouseenter', () => {
                clearAllItem.style.backgroundColor = '#5a2d2d';
            });
            clearAllItem.addEventListener('mouseleave', () => {
                clearAllItem.style.backgroundColor = 'transparent';
            });

            clearAllItem.addEventListener('click', (e) => {
                e.stopPropagation();
                if (confirm('ç¡®å®šè¦æ¸…ç©ºæ‰€æœ‰URLåŽ†å²è®°å½•å—ï¼Ÿ')) {
                    urlHistory.clearHistory();
                    updateHistoryDropdown();
                }
            });

            historyDropdown.appendChild(clearAllItem);
        }
    }

    // è¾“å…¥æ¡†äº‹ä»¶
    urlInput.addEventListener('focus', () => {
        updateHistoryDropdown();
        historyDropdown.style.display = 'block';
    });

    urlInput.addEventListener('input', () => {
        if (onUrlChange) onUrlChange(urlInput.value);
    });

    urlInput.addEventListener('blur', (e) => {
        // å»¶è¿Ÿéšè—ï¼Œå…è®¸ç‚¹å‡»åŽ†å²è®°å½•é¡¹
        setTimeout(() => {
            if (!historyDropdown.contains(docContext.activeElement)) {
                historyDropdown.style.display = 'none';
            }
        }, 200);

        // ä¿å­˜URLåˆ°åŽ†å²è®°å½•
        const url = urlInput.value.trim();
        if (url && url.startsWith('http')) {
            urlHistory.addUrl(url);
        }
    });

    // æ¸…é™¤æŒ‰é’®äº‹ä»¶
    clearButton.onclick = () => {
        urlInput.value = '';
        urlInput.dispatchEvent(new Event('input'));
        historyDropdown.style.display = 'none';
    };

    // ç‚¹å‡»å®¹å™¨å¤–éƒ¨å…³é—­ä¸‹æ‹‰æ¡†
    docContext.addEventListener('click', (e) => {
        if (!container.contains(e.target)) {
            historyDropdown.style.display = 'none';
        }
    });

    inputContainer.appendChild(urlInput);
    inputContainer.appendChild(historyDropdown);

    container.appendChild(inputContainer);
    container.appendChild(clearButton);

    return { container, urlInput };
}

