// å°è¯•ä»Ž@importçš„CSSä¸­æå–å­—ä½“åç§°
function tryExtractFontNameFromImport(cssUrl, tempName) {
    fetch(cssUrl)
        .then(response => response.text())
        .then(cssContent => {
            let realFontName = null;

            // ä¼˜å…ˆæŸ¥æ‰¾@font-faceä¸­çš„font-family
            const fontFaceMatch = cssContent.match(/@font-face[^}]*font-family\s*:\s*(['"]?)([^;'"{}]+)\1/i);
            if (fontFaceMatch) {
                realFontName = fontFaceMatch[2].trim().replace(/['"]/g, '');
            } else {
                // æŸ¥æ‰¾æ™®é€šçš„font-familyå®šä¹‰
                const fontFamilyMatch = cssContent.match(/font-family\s*:\s*(['"]?)([^;'"{}]+)\1/i);
                if (fontFamilyMatch) {
                    realFontName = fontFamilyMatch[2].trim().replace(/['"]/g, '');
                }
            }

            if (realFontName) {
                const index = state.importedFontFamilies.indexOf(tempName);

                if (index !== -1 && !state.importedFontFamilies.includes(realFontName)) {
                    // æ›´æ–°å­—ä½“åç§°
                    state.importedFontFamilies[index] = realFontName;

                    // æ›´æ–°æ˜ å°„å…³ç³»ï¼šä»Žä¸´æ—¶åç§°è½¬ç§»åˆ°çœŸå®žåç§°
                    if (state.fontFamilyUrlMap && state.fontFamilyUrlMap[tempName]) {
                        const url = state.fontFamilyUrlMap[tempName];
                        delete state.fontFamilyUrlMap[tempName];
                        state.fontFamilyUrlMap[realFontName] = url;
                        console.log(`Font Manager: æ›´æ–°æ˜ å°„å…³ç³»: ${tempName} -> ${realFontName} = ${url}`);
                    }

                    saveFontsToStorage();

                    // å¦‚æžœå½“å‰modalæ˜¯æ‰“å¼€çš„ï¼Œåˆ·æ–°æ˜¾ç¤º
                    if (modalElement && modalElement.style.display === 'flex') {
                        renderCombinedMainView();
                    }
                    console.log(`Font Manager: Updated font name from ${tempName} to ${realFontName}`);
                } else if (index === -1) {
                    // å¦‚æžœä¸´æ—¶åç§°ä¸å­˜åœ¨ï¼Œå¯èƒ½æ˜¯å› ä¸ºå·²ç»è¢«æ›¿æ¢ï¼Œç›´æŽ¥æ·»åŠ çœŸå®žåç§°
                    if (!state.importedFontFamilies.includes(realFontName)) {
                        state.importedFontFamilies.push(realFontName);

                        // ä¸ºæ–°å­—ä½“å»ºç«‹æ˜ å°„å…³ç³»
                        if (state.fontFamilyUrlMap) {
                            state.fontFamilyUrlMap[realFontName] = cssUrl;
                        }

                        saveFontsToStorage();

                        if (modalElement && modalElement.style.display === 'flex') {
                            renderCombinedMainView();
                        }
                        console.log(`Font Manager: Added extracted font name: ${realFontName}`);
                    }
                }
            } else {
                // å¦‚æžœæ— æ³•æå–çœŸå®žåç§°ï¼Œå°†ä¸´æ—¶åç§°æ”¹ä¸ºæ›´å‹å¥½çš„æ ¼å¼
                const index = state.importedFontFamilies.indexOf(tempName);
                if (index !== -1) {
                    const fallbackName = `å¯¼å…¥å­—ä½“${index + 1}`;
                    if (!state.importedFontFamilies.includes(fallbackName)) {
                        state.importedFontFamilies[index] = fallbackName;
                        saveFontsToStorage();

                        if (modalElement && modalElement.style.display === 'flex') {
                            renderCombinedMainView();
                        }
                        console.log(`Font Manager: Updated temp name to fallback: ${tempName} -> ${fallbackName}`);
                    }
                }
            }
        })
        .catch(e => {
            console.log('Font Manager: Could not fetch CSS to extract font name:', e);

            // ç½‘ç»œé”™è¯¯æ—¶ï¼Œä¹Ÿä½¿ç”¨å‹å¥½çš„å›žé€€åç§°
            const index = state.importedFontFamilies.indexOf(tempName);
            if (index !== -1) {
                const fallbackName = `å¯¼å…¥å­—ä½“${index + 1}`;
                if (!state.importedFontFamilies.includes(fallbackName)) {
                    state.importedFontFamilies[index] = fallbackName;
                    saveFontsToStorage();

                    if (modalElement && modalElement.style.display === 'flex') {
                        renderCombinedMainView();
                    }
                    console.log(`Font Manager: Network error, updated to fallback: ${tempName} -> ${fallbackName}`);
                }
            }
        });
}

