function processAndImportFonts(cssText) {
    // åŽ»é™¤ <style> æ ‡ç­¾
    cssText = cssText.replace(/<\/?style[^>]*>/gi, '');

    const importRegex = /@import\s+url\((['"])(.*?)\1\);/gi;
    const fontFaceRegex = /@font-face\s*\{[^}]*font-family\s*:\s*(['"]?)([^;'"{}]+)\1[^}]*src\s*:\s*url\((['"]?)([^'"()]+)\3\)[^}]*\}/gi;
    const urlRegex = /^https?:\/\/[^\s<>"]+\.(ttf|otf|woff2?)(\?[^\s<>"]*)?$/gim;

    let importsAttempted = 0;
    let importsSucceeded = 0;
    let familiesFound = [];
    let processedUrls = new Set();
    let duplicateUrls = [];

    // ç”¨äºŽè®¡æ•°å™¨ - åŸºäºŽçŽ°æœ‰å­—ä½“æ•°é‡åˆå§‹åŒ–ï¼ˆåŽ»æŽ‰æ ‡è¯†ç¬¦ç»Ÿè®¡ï¼‰
    let fontUrlCounter = state.importedFontFamilies.filter(name => /^å­—ä½“\d+$/.test(name)).length;
    let importUrlCounter = 0;

// åˆå§‹åŒ– â€œå­—ä½“å <-> URLâ€ åŒå‘æ˜ å°„ï¼ˆè·¨å‡½æ•°å…±äº«ï¼‰
if (!state.fontFamilyUrlMap) {
    state.fontFamilyUrlMap = {};   // ç»“æž„ï¼š{ [fontName]: url }
}

// è®°å½•å‡½æ•°è°ƒç”¨å‰çš„çŠ¶æ€ï¼Œä¾¿äºŽåŒºåˆ†ã€Œè·¨æ‰¹æ¬¡é‡å¤ã€ä¸Žã€Œæœ¬æ‰¹æ¬¡é‡å¤ã€
const previousFontImportUrls     = new Set(state.fontImportUrls);
const previousImportedFontFamilies = new Set(state.importedFontFamilies);

    // é¦–å…ˆæ£€æŸ¥æ˜¯å¦æœ‰æœ¬åœ°å®šä¹‰çš„font-familyï¼ˆæ ¼å¼1ï¼‰
    function extractLocalFontFamily(cssText, importUrl) {
        // æŸ¥æ‰¾bodyæˆ–å…¶ä»–é€‰æ‹©å™¨ä¸­çš„font-familyå®šä¹‰
        const bodyFontMatch = cssText.match(/body\s*\{[^}]*font-family\s*:\s*(['"]?)([^;'"{}]+)\1[^}]*\}/i);
        if (bodyFontMatch) {
            return bodyFontMatch[2].trim().replace(/['"]/g, '');
        }

        // æŸ¥æ‰¾å…¶ä»–å¯èƒ½çš„font-familyå®šä¹‰
        const generalFontMatch = cssText.match(/font-family\s*:\s*(['"]?)([^;'"{}]+)\1/i);
        if (generalFontMatch) {
            return generalFontMatch[2].trim().replace(/['"]/g, '');
        }

        return null;
    }

     // å¤„ç† @font-face è¯­å¥ï¼ˆæœ€é«˜ä¼˜å…ˆçº§ï¼Œä¼šæå–çœŸå®žå­—ä½“åç§°ï¼‰
    let fontFaceMatch;
    while ((fontFaceMatch = fontFaceRegex.exec(cssText)) !== null) {
        const fontFamily = fontFaceMatch[2].trim().replace(/['"]/g, '');
        const fontUrl = fontFaceMatch[4];

        // æ ¼å¼2ï¼šåªæ£€æŸ¥URLé‡å¤ï¼Œä¸æ£€æŸ¥åå­—é‡å¤
        // æ£€æŸ¥æ˜¯å¦å·²ç»å­˜åœ¨ç›¸åŒçš„å­—ä½“URL
        // ä»…å½“ã€Œä¹‹å‰å·²å¯¼å…¥ã€æ‰ç®—é‡å¤ï¼›åŒä¸€æ‰¹å¤„ç†é‡Œå†æ¬¡å‡ºçŽ°åˆ™é™é»˜è·³è¿‡
const alreadyImported = state.localFonts.some(font => font.fontDataUrl && font.fontDataUrl.includes(fontUrl)) ||
                        previousFontImportUrls.has(fontUrl);

if (alreadyImported) {
    duplicateUrls.push(fontUrl);          // è·¨æ‰¹æ¬¡é‡å¤ï¼Œæç¤º
    continue;
}
if (processedUrls.has(fontUrl)) {
    continue;                             // æœ¬æ‰¹æ¬¡å†…ç¬¬äºŒæ¬¡å‡ºçŽ°ï¼Œç›´æŽ¥è·³è¿‡
}

        processedUrls.add(fontUrl);

        importsAttempted++;
        try {
            // ç›´æŽ¥æ·»åŠ æ•´ä¸ª @font-face è§„åˆ™
            const styleElement = docContext.createElement('style');
            styleElement.textContent = fontFaceMatch[0];
docContext.head.appendChild(styleElement);

// æ·»åŠ åˆ°å·²å¯¼å…¥å­—ä½“ååˆ—è¡¨
if (!state.importedFontFamilies.includes(fontFamily)) {
    state.importedFontFamilies.push(fontFamily);
    familiesFound.push(fontFamily);
}
// è®°å½•æ˜ å°„ï¼Œä¾›åˆ é™¤æ—¶åæŸ¥
state.fontFamilyUrlMap[fontFamily] = fontUrl;
// æ·»åŠ åˆ°å·²å¯¼å…¥ URL åˆ—è¡¨ï¼Œé¿å…é‡å¤å¯¼å…¥åŒä¸€ font-face çš„ URL
if (!state.fontImportUrls.includes(fontUrl)) {
    state.fontImportUrls.push(fontUrl);
}

importsSucceeded++;
            console.log('Font Manager: Successfully processed @font-face:', fontFamily);
        } catch (e) {
            console.error('Font Manager: Error processing @font-face:', e);
        }
    }

    // å¤„ç† @import è¯­å¥
    let importMatch;
    while ((importMatch = importRegex.exec(cssText)) !== null) {
        const importUrl = importMatch[2];

        // æ ¼å¼1ï¼šæ£€æŸ¥URLå’Œå­—ä½“åç§°éƒ½ä¸èƒ½é‡å¤
        // å…ˆæ£€æŸ¥URLæ˜¯å¦é‡å¤
        if (previousFontImportUrls.has(importUrl)) {
    duplicateUrls.push(importUrl);        // ä¹‹å‰å¯¼å…¥è¿‡ â†’ é‡å¤
    continue;
}
if (processedUrls.has(importUrl)) {
    continue;                             // æœ¬æ‰¹æ¬¡é‡Œç¬¬äºŒæ¬¡å‡ºçŽ°ï¼Œä¸æç¤º
}

        // æ£€æŸ¥æ˜¯å¦æœ‰æœ¬åœ°font-familyå®šä¹‰ï¼Œå¦‚æžœæœ‰å°±æ£€æŸ¥åç§°é‡å¤
        const localFontFamily = extractLocalFontFamily(cssText, importUrl);
        // åªæœ‰ã€Œè°ƒç”¨å‰å°±å­˜åœ¨ã€çš„å­—ä½“åæ‰ç®—é‡å¤
if (localFontFamily && previousImportedFontFamilies.has(localFontFamily)) {
    duplicateUrls.push(`å­—ä½“åç§°: ${localFontFamily}`);
    continue;
}

        processedUrls.add(importUrl);

        importsAttempted++;
        try {
            const styleElement = docContext.createElement('style');
            styleElement.id = `font-manager-import-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
            styleElement.setAttribute('data-font-url', importUrl);
            styleElement.textContent = `@import url('${importUrl}');`;
            docContext.head.appendChild(styleElement);
            console.log('Font Manager: Successfully queued import:', importUrl);
            importsSucceeded++;
            if (!state.fontImportUrls.includes(importUrl)) {
                state.fontImportUrls.push(importUrl);
            }

            let fontName;
            if (localFontFamily) {
                fontName = localFontFamily;
                console.log('Font Manager: Found local font-family definition:', fontName);
            } else {
                // åˆ›å»ºä¸´æ—¶æ ‡è¯†ç¬¦ï¼Œç¨åŽå°è¯•æå–çœŸå®žåç§°
                importUrlCounter++;
                fontName = `Import-${importUrlCounter}-Temp`;

                // å¼‚æ­¥æå–å­—ä½“åç§°
                setTimeout(() => {
                    tryExtractFontNameFromImport(importUrl, fontName);
                }, 1000);
            }

            if (!state.importedFontFamilies.includes(fontName)) {
    state.importedFontFamilies.push(fontName);
    familiesFound.push(fontName);
}
// è®°å½•æ˜ å°„
state.fontFamilyUrlMap[fontName] = importUrl;
        } catch (e) {
            console.error('Font Manager: Error creating style tag for import ' + importUrl + ':', e);
        }
    }

    // å¤„ç†æ‰€æœ‰ç±»åž‹çš„URLï¼ˆæŒ‰è¡Œåˆ†å‰²å¤„ç†ï¼‰
    const lines = cssText.split('\n');
    lines.forEach(line => {
        const trimmedLine = line.trim();
        if (!trimmedLine) return;

        // è·³è¿‡å·²ç»è¢«@importå¤„ç†è¿‡çš„URLè¡Œ
        if (trimmedLine.includes('@import')) return;

        // åŒ¹é…å­—ä½“æ–‡ä»¶URLï¼ˆæ ¼å¼3ï¼‰
        const fontUrlMatch = trimmedLine.match(/https?:\/\/[^\s<>"]+\.(ttf|otf|woff2?)\b/i);

        if (fontUrlMatch) {
            const url = fontUrlMatch[0];
            const extension = fontUrlMatch[1].toLowerCase();

            // æ£€æŸ¥å­—ä½“æ–‡ä»¶URLæ˜¯å¦å·²å­˜åœ¨ - æ£€æŸ¥ä¿å­˜çš„URLåˆ—è¡¨
            if (previousFontImportUrls.has(url)) {
    duplicateUrls.push(url);              // è·¨æ‰¹æ¬¡é‡å¤
    return;
}
if (processedUrls.has(url)) {
    return;                               // æœ¬æ‰¹æ¬¡å·²å¤„ç†ï¼Œé™é»˜è·³è¿‡
}

            processedUrls.add(url);

            importsAttempted++;
            try {
                fontUrlCounter++;
                // åŽ»æŽ‰[URL]æ ‡è¯†ï¼Œç›´æŽ¥ä½¿ç”¨"å­—ä½“X"æ ¼å¼
                const fontName = `å­—ä½“${fontUrlCounter}`;

                let format = 'truetype';
                if (extension === 'otf') format = 'opentype';
                else if (extension === 'woff') format = 'woff';
                else if (extension === 'woff2') format = 'woff2';

                const styleElement = docContext.createElement('style');
                styleElement.textContent = `
                    @font-face {
                        font-family: '${fontName}';
                        src: url('${url}') format('${format}');
                        font-display: swap;
                    }
                `;
                docContext.head.appendChild(styleElement);

                // ä¿å­˜å­—ä½“æ–‡ä»¶URLåˆ°å¯¼å…¥åˆ—è¡¨ä¸­
                if (!state.fontImportUrls.includes(url)) {
                    state.fontImportUrls.push(url);
                }

                state.importedFontFamilies.push(fontName);
familiesFound.push(fontName);

// è®°å½•æ˜ å°„
state.fontFamilyUrlMap[fontName] = url;

importsSucceeded++;
                console.log('Font Manager: Successfully processed font URL:', url, '-> ', fontName);
            } catch (e) {
                console.error('Font Manager: Error processing font URL:', e);
            }
            return;
        }

        // åŒ¹é…å•ç‹¬çš„CSSæ–‡ä»¶URLï¼ˆæ ¼å¼4ï¼‰- ä¸åŒ…å«@importçš„çº¯URL
        const cssUrlMatch = trimmedLine.match(/^https?:\/\/[^\s<>"]+\.css(\?[^\s<>"]*)?$/i);

        if (cssUrlMatch) {
            const url = cssUrlMatch[0];

            // æ£€æŸ¥CSSæ–‡ä»¶URLæ˜¯å¦å·²å­˜åœ¨
            if (previousFontImportUrls.has(url)) {
    duplicateUrls.push(url);              // è·¨æ‰¹æ¬¡é‡å¤
    return;
}
if (processedUrls.has(url)) {
    return;                               // æœ¬æ‰¹æ¬¡é‡å¤ï¼Œé™é»˜è·³è¿‡
}

            processedUrls.add(url);

            importsAttempted++;
            try {
                const styleElement = docContext.createElement('style');
                styleElement.textContent = `@import url('${url}');`;
                docContext.head.appendChild(styleElement);

                if (!state.fontImportUrls.includes(url)) {
                    state.fontImportUrls.push(url);
                }

                // åˆ›å»ºä¸´æ—¶æ ‡è¯†ç¬¦ï¼Œç¨åŽå°è¯•æå–çœŸå®žåç§°
                importUrlCounter++;
                const tempFontName = `Import-${importUrlCounter}-Temp`;

                state.importedFontFamilies.push(tempFontName);
familiesFound.push(tempFontName);

// è®°å½•æ˜ å°„
state.fontFamilyUrlMap[tempFontName] = url;

importsSucceeded++;
                console.log('Font Manager: Successfully processed CSS import:', url, '-> ', tempFontName);

                // å¼‚æ­¥æå–å­—ä½“åç§°
                setTimeout(() => {
                    tryExtractFontNameFromImport(url, tempFontName);
                }, 1000);
            } catch (e) {
                console.error('Font Manager: Error processing CSS import:', e);
            }
        }
    });

    // å¤„ç†æˆåŠŸå¯¼å…¥çš„æ¶ˆæ¯
    if (importsAttempted > 0) {
        let message = `å­—ä½“å¯¼å…¥å¤„ç†å®Œæˆã€‚å°è¯•äº† ${importsAttempted} ä¸ªå¯¼å…¥ï¼ŒæˆåŠŸ ${importsSucceeded} ä¸ªã€‚`;
        if (familiesFound.length > 0) {
            message += `è¯†åˆ«å¹¶è®°å½•çš„å­—ä½“: ${familiesFound.join(', ')}`;
        }
        showNotification(message, 'success');
    }

    // å¤„ç†é‡å¤é“¾æŽ¥çš„çº¢è‰²æé†’
    if (duplicateUrls.length > 0) {
        showNotification('ä½ å·²ç»å¯¼å…¥è¿‡è¿™ä¸ªé“¾æŽ¥äº†å“¦', 'error');
    }

    // å¦‚æžœä»€ä¹ˆéƒ½æ²¡æ‰¾åˆ°
    if (importsAttempted === 0 && familiesFound.length === 0 && duplicateUrls.length === 0) {
        showNotification('æœªæ‰¾åˆ°æœ‰æ•ˆçš„å­—ä½“é“¾æŽ¥æˆ–å­—ä½“å®šä¹‰ã€‚', 'warning');
    }

    if (familiesFound.length > 0 || importsSucceeded > 0) { 
        saveFontsToStorage(); 
        saveFontImportUrlsToStorage(); 
    }
    renderCombinedMainView();
}

function applyGlobalFontToAllElements(fontFamily) {
    let globalStyleElement = docContext.getElementById('font-manager-global-font');

    if (!globalStyleElement) {
        globalStyleElement = docContext.createElement('style');
        globalStyleElement.id = 'font-manager-global-font';
        docContext.head.appendChild(globalStyleElement);
    }

    if (fontFamily) {
        globalStyleElement.textContent = `
        body, .mes_text, #chat, .message,
        *:not([class*="fa-"]):not([class*="icon"]):not([class*="material"]) {
            font-family: "${fontFamily}", sans-serif !important;
        }

        code, pre, .hljs, .mes_text code,
        input[type="text"], input[type="password"], input[type="email"], 
        textarea {
            font-family: "${fontFamily}", 'Consolas', 'Monaco', monospace !important;
        }
        `;
    } else {
        globalStyleElement.textContent = '';
    }

    console.log('Font Manager: Global font applied to all elements:', fontFamily);
}

function applyFontToBody(fontFamily) {
    if (!fontFamily) {
        console.warn('Font Manager: No font family provided to applyFontToBody');
        return;
    }

    try {
        if (state.multilangEnabled) {
            console.log(`Font Manager: å¤šè¯­è¨€æ¨¡å¼å·²å¯ç”¨ï¼Œä¸èƒ½ç›´æŽ¥åº”ç”¨å•ä¸€å­—ä½“ "${fontFamily}"`);
            showNotification('å½“å‰å¤„äºŽå¤šè¯­è¨€å­—ä½“æ¨¡å¼ï¼Œè¯·åœ¨å¤šè¯­è¨€è®¾ç½®ä¸­é…ç½®å­—ä½“ï¼Œæˆ–å…ˆå…³é—­å¤šè¯­è¨€æ¨¡å¼ã€‚');
            return;
        }

        applyGlobalFontToAllElements(fontFamily);

        localStorage.setItem(LOCAL_STORAGE_KEY_ACTIVE_FONT, fontFamily);
        state.activeFont = fontFamily;

        // è®¾ç½®ä¸ºå•å­—ä½“åº”ç”¨æ¨¡å¼
        state.fontApplicationMode = 'single';
        state.singleFontFamily = fontFamily;
        localStorage.setItem('fontManagerApplicationMode', 'single');
        localStorage.setItem('fontManagerSingleFont', fontFamily);

        console.log('Font Manager: Font applied to body (single mode):', fontFamily);

        if (modalBodyElement) {
            const currentFontDisplay = modalBodyElement.querySelector('.current-font-display');
            if (currentFontDisplay) {
                let currentUISizeDisplay = initialMainFontSizeFromCSS;
                const actualAppliedSize = docContext.documentElement.style.getPropertyValue('--mainFontSize').trim();
                if (actualAppliedSize) {
                    currentUISizeDisplay = actualAppliedSize;
                }
                currentFontDisplay.innerHTML = `å½“å‰å­—ä½“: <span style="font-style: italic; font-family: ${fontFamily};">${fontFamily}</span> (UI å¤§å°: ${currentUISizeDisplay || 'æœªè®¾ç½®'}) [å•å­—ä½“æ¨¡å¼]`;
            }
        }

    } catch (error) {
        console.error('Font Manager: Error applying font to body:', error);
        showNotification(`åº”ç”¨å­—ä½“æ—¶å‡ºé”™: ${error.message}`);
    }
}

