// ========= Drag and Drop Handlers =========
function handleDragStart(event, index) {
    draggedItemIndex = index;
    event.dataTransfer.effectAllowed = 'move';
    event.target.classList.add('dragging-font-item');
}

function handleDragOver(event, targetIndex) {
    event.preventDefault();
    if (targetIndex !== draggedItemIndex) {
        const targetElement = event.target.closest('.font-manage-item');
        if (targetElement) {
            targetElement.classList.add('drag-over-font-item');
        }
    }
}

function handleDragLeave(event) {
    const targetElement = event.target.closest('.font-manage-item');
    if (targetElement) {
        targetElement.classList.remove('drag-over-font-item');
    }
}

function handleDrop(event, targetIndex) {
    event.preventDefault();
    const targetElement = event.target.closest('.font-manage-item');
    if (targetElement) {
        targetElement.classList.remove('drag-over-font-item');
    }

    if (draggedItemIndex !== null && draggedItemIndex !== targetIndex) {
        const itemToMove = state.importedFontFamilies.splice(draggedItemIndex, 1)[0];
        state.importedFontFamilies.splice(targetIndex, 0, itemToMove);

        saveFontsToStorage();
        renderCombinedMainView();
    }
    draggedItemIndex = null;
}

function handleDragEnd(event) {
    event.target.classList.remove('dragging-font-item');
    const allItems = modalBodyElement.querySelectorAll('.font-manage-item.drag-over-font-item');
    allItems.forEach(item => item.classList.remove('drag-over-font-item'));
    draggedItemIndex = null;
    saveFontsToStorage();
    renderCombinedMainView();
}

function editFontName(index) {
    const currentName = state.importedFontFamilies[index];
    const newName = prompt('è¯·è¾“å…¥æ–°çš„å­—ä½“åç§°:', currentName);

    if (newName === null) return;

    if (newName.trim() === '') {
        showNotification('å­—ä½“åç§°ä¸èƒ½ä¸ºç©ºï¼');
        return;
    }

    if (newName.trim() === currentName) return;

    if (state.importedFontFamilies.includes(newName.trim())) {
        showNotification('è¯¥å­—ä½“åç§°å·²å­˜åœ¨ï¼');
        return;
    }

    state.importedFontFamilies[index] = newName.trim();

    const activeFont = localStorage.getItem(LOCAL_STORAGE_KEY_ACTIVE_FONT);
    if (activeFont === currentName) {
        localStorage.setItem(LOCAL_STORAGE_KEY_ACTIVE_FONT, newName.trim());
        state.activeFont = newName.trim();
    }

    Object.entries(languageTypes).forEach(([langKey, langConfig]) => {
        if (state[langConfig.stateKey] === currentName) {
            state[langConfig.stateKey] = newName.trim();
        }
    });

    // æ›´æ–°æœ¬åœ°å­—ä½“æ•°æ®
    const localFont = state.localFonts.find(font => font.fontName === currentName);
    if (localFont) {
        localFont.fontName = newName.trim();

        // æ›´æ–°IndexedDB
        deleteLocalFontFromIndexedDB(currentName).then(() => {
            return saveLocalFontToIndexedDB(newName.trim(), localFont.fontDataUrl, localFont.fileName);
        }).then(() => {
            // æ›´æ–°DOMä¸­çš„æ ·å¼
            const oldStyleElement = docContext.getElementById(`font-manager-local-font-${currentName.replace(/\s+/g, '-')}`);
            if (oldStyleElement) {
                oldStyleElement.remove();
            }

            const newStyleElement = docContext.createElement('style');
            newStyleElement.id = `font-manager-local-font-${newName.trim().replace(/\s+/g, '-')}`;
            newStyleElement.textContent = `
                @font-face {
                    font-family: '${newName.trim()}';
                    src: url('${localFont.fontDataUrl}') format('truetype');
                    font-display: swap;
                }
            `;
            docContext.head.appendChild(newStyleElement);
        }).catch(error => {
            console.error('æ›´æ–°æœ¬åœ°å­—ä½“åç§°æ—¶å‡ºé”™:', error);
        });
    }

    saveFontsToStorage();
    saveMultilangSettings();
    showNotification(`å­—ä½“åç§°å·²ä»Ž "${currentName}" æ›´æ”¹ä¸º "${newName.trim()}"`);
    renderCombinedMainView();
}

function deleteFont(index) {
    const deletedFont = state.importedFontFamilies[index];
    // â€”â€”â€” æ¸…ç†ä¸Žè¯¥å­—ä½“å…³è”çš„ <style>/<link> æ ‡ç­¾å’Œ URL æ˜ å°„ â€”â€”â€”
(function cleanupStyleAndUrl() {
    const map = state.fontFamilyUrlMap || {};
    const associatedUrl = map[deletedFont];

    if (associatedUrl) {
        // ç²¾ç¡®åˆ é™¤ï¼šå…ˆé€šè¿‡dataå±žæ€§æŸ¥æ‰¾ï¼Œå†é€šè¿‡å†…å®¹åŒ¹é…
        Array.from(docContext.querySelectorAll('style')).forEach(el => {
            const dataUrl = el.getAttribute('data-font-url');
            const shouldRemove = dataUrl === associatedUrl || 
                               el.textContent.includes(associatedUrl) || 
                               el.textContent.includes(deletedFont);
            if (shouldRemove) {
                console.log('Font Manager: åˆ é™¤æ ·å¼å…ƒç´ :', el.id || 'unnamed', 'URL:', associatedUrl);
                el.parentNode.removeChild(el);
            }
        });

        // åŒæ—¶ç§»é™¤ <link rel="stylesheet" href="..."> æ ‡ç­¾
        Array.from(docContext.querySelectorAll('link[rel="stylesheet"]')).forEach(link => {
            if (link.href === associatedUrl) {
                link.parentNode.removeChild(link);
            }
        });

        // ä»Ž URL åˆ—è¡¨ä¸­åˆ é™¤
        const urlIdx = state.fontImportUrls.indexOf(associatedUrl);
        if (urlIdx !== -1) {
            state.fontImportUrls.splice(urlIdx, 1);
            console.log('Font Manager: ä»ŽURLåˆ—è¡¨åˆ é™¤:', associatedUrl);
        }

        // åˆ é™¤æ˜ å°„
        delete map[deletedFont];
        console.log('Font Manager: åˆ é™¤å­—ä½“æ˜ å°„:', deletedFont, '->', associatedUrl);
    } else {
        console.log('Font Manager: æœªæ‰¾åˆ°å­—ä½“URLæ˜ å°„:', deletedFont);

        // å¦‚æžœæ²¡æœ‰æ˜ å°„å…³ç³»ï¼Œå°è¯•é€šè¿‡å­—ä½“åç§°æŸ¥æ‰¾å¹¶åˆ é™¤ç›¸å…³æ ·å¼
        Array.from(docContext.querySelectorAll('style')).forEach(el => {
            if (el.textContent.includes(deletedFont)) {
                console.log('Font Manager: é€šè¿‡å­—ä½“ååˆ é™¤æ ·å¼å…ƒç´ :', deletedFont);
                el.parentNode.removeChild(el);
            }
        });
    }
})();

    // æ£€æŸ¥æ˜¯å¦æ˜¯æœ¬åœ°å­—ä½“
    const isLocalFont = state.localFonts.some(font => font.fontName === deletedFont);

    if (isLocalFont) {
        // åˆ é™¤æœ¬åœ°å­—ä½“
        deleteLocalFontFromIndexedDB(deletedFont).then(() => {
            // ä»Ž importedFontFamilies ä¸­ç§»é™¤
            state.importedFontFamilies.splice(index, 1);

            let message = `æœ¬åœ°å­—ä½“ "${deletedFont}" å·²å½»åº•åˆ é™¤ã€‚`;

            // æ£€æŸ¥æ˜¯å¦æ˜¯å½“å‰åº”ç”¨çš„å­—ä½“
            const activeFont = localStorage.getItem(LOCAL_STORAGE_KEY_ACTIVE_FONT);
            if (activeFont === deletedFont) {
                resetToDefaultFont();
                message = `æœ¬åœ°å­—ä½“ "${deletedFont}" å·²å½»åº•åˆ é™¤ï¼Œé¡µé¢å­—ä½“å·²é‡ç½®ä¸ºé»˜è®¤ã€‚`;
            }

            showNotification(message);
            saveFontsToStorage();
            saveFontImportUrlsToStorage(); // æ–°å¢žï¼šä¿å­˜æ›´æ–°åŽçš„URLåˆ—è¡¨
            renderCombinedMainView();
        }).catch(error => {
            console.error('åˆ é™¤æœ¬åœ°å­—ä½“æ—¶å‡ºé”™:', error);
            showNotification('åˆ é™¤å­—ä½“æ—¶å‡ºçŽ°é”™è¯¯ï¼');
        });
    } else {
        // åˆ é™¤å¯¼å…¥çš„å­—ä½“
        state.importedFontFamilies.splice(index, 1);

        let message = `å­—ä½“ "${deletedFont}" å·²åˆ é™¤ã€‚`;

        // æ£€æŸ¥æ˜¯å¦æ˜¯å½“å‰åº”ç”¨çš„å­—ä½“
        const activeFont = localStorage.getItem(LOCAL_STORAGE_KEY_ACTIVE_FONT);
        if (activeFont === deletedFont) {
            resetToDefaultFont();
            message = `å­—ä½“ "${deletedFont}" å·²åˆ é™¤ï¼Œé¡µé¢å­—ä½“å·²é‡ç½®ä¸ºé»˜è®¤ã€‚`;
        }

        showNotification(message);
        saveFontsToStorage();
        saveFontImportUrlsToStorage(); // æ–°å¢žï¼šä¿å­˜æ›´æ–°åŽçš„URLåˆ—è¡¨
        renderCombinedMainView();
    }
}

function handleExportFontPack() {
    const packData = {
        version: FONT_PACK_VERSION,
        timestamp: new Date().toISOString(),
        settings: {
            importedFontFamilies: state.importedFontFamilies,
            fontImportUrls: state.fontImportUrls,
            activeFont: localStorage.getItem(LOCAL_STORAGE_KEY_ACTIVE_FONT) || null,
            globalFontSize: localStorage.getItem(LOCAL_STORAGE_KEY_GLOBAL_FONT_SIZE) || null,
            iconSize: localStorage.getItem(LOCAL_STORAGE_KEY_ICON_SIZE) || null,
            buttonSize: localStorage.getItem(LOCAL_STORAGE_KEY_BUTTON_SIZE) || null,
            hljsColors: {},
            hljsFontFamily: localStorage.getItem(LOCAL_STORAGE_KEY_HLJS_FONT_FAMILY) || null,
            mesCodeBgColor: localStorage.getItem(LOCAL_STORAGE_KEY_MES_CODE_BG_COLOR) || null,
            mesCodeTextColor: localStorage.getItem(LOCAL_STORAGE_KEY_MES_CODE_TEXT_COLOR) || null,
            mesCodeBorderColor: localStorage.getItem(LOCAL_STORAGE_KEY_MES_CODE_BORDER_COLOR) || null,
            mesCodeBackgroundOpacity: localStorage.getItem(LOCAL_STORAGE_KEY_MES_CODE_BACKGROUND_OPACITY) || null,
            mesCodeBackgroundImage: localStorage.getItem(LOCAL_STORAGE_KEY_MES_CODE_BACKGROUND_IMAGE) || null,
            mesCodeFontFamily: localStorage.getItem(LOCAL_STORAGE_KEY_MES_CODE_FONT_FAMILY) || null,
            localFonts: state.localFonts,
            multilangEnabled: state.multilangEnabled,
            multilangFonts: {}
        },
        colorPalette: {}
    };

    Object.values(hljsScopeSettings).forEach(scope => {
        packData.settings.hljsColors[scope.lsKey] = localStorage.getItem(scope.lsKey) || null;
    });

    Object.keys(colorSettings).forEach(key => {
        packData.colorPalette[key] = localStorage.getItem(colorSettings[key].lsKey) || null;
    });

    Object.entries(languageTypes).forEach(([langKey, langConfig]) => {
        packData.settings.multilangFonts[langKey] = state[langConfig.stateKey];
    });

    const jsonString = JSON.stringify(packData, null, 2);
    const blob = new Blob([jsonString], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = docContext.createElement('a');
    a.href = url;
    const date = new Date().toISOString().slice(0, 10);
    a.download = `font-manager-pack-${date}.json`;
    docContext.body.appendChild(a);
    a.click();
    docContext.body.removeChild(a);
    URL.revokeObjectURL(url);
    console.log('Font pack exported.');
}

function resetToDefaultFont() {
    docContext.body.style.fontFamily = '';

    const globalStyleElement = docContext.getElementById('font-manager-global-font');
    if (globalStyleElement) {
        globalStyleElement.textContent = '';
    }

    state.activeFont = null;
    state.fontApplicationMode = 'default';
    state.singleFontFamily = null;

    try {
        localStorage.removeItem(LOCAL_STORAGE_KEY_ACTIVE_FONT);
        localStorage.setItem('fontManagerApplicationMode', 'default');
        localStorage.removeItem('fontManagerSingleFont');
        console.log('Font Manager: Active font cleared from localStorage.');
    } catch (e) {
        console.error('Font Manager: Error clearing active font from localStorage:', e);
    }
    //showNotification('å­—ä½“å·²é‡ç½®ä¸ºé¡µé¢é»˜è®¤è®¾ç½®ã€‚');
    renderCombinedMainView();
}

function handleFontPackFileSelected(event) {
    const file = event.target.files[0];
    if (!file) {
        console.log('Font Manager: No file selected for import.');
        return;
    }

    if (file.type !== 'application/json') {
        showNotification('è¯·é€‰æ‹©ä¸€ä¸ªæœ‰æ•ˆçš„ .json å­—ä½“åŒ…æ–‡ä»¶ï¼');
        event.target.value = null;
        return;
    }

    const reader = new FileReader();
    reader.onload = function(e) {
        try {
            const fileContent = e.target.result;
            const importedData = JSON.parse(fileContent);
            processImportedFontPackData(importedData);
        } catch (err) {
            console.error('Font Manager: Error parsing font pack file:', err);
            showNotification('æ— æ³•è§£æžå­—ä½“åŒ…æ–‡ä»¶ã€‚æ–‡ä»¶å¯èƒ½å·²æŸåæˆ–æ ¼å¼ä¸æ­£ç¡®ã€‚');
        }
        event.target.value = null;
    };
    reader.onerror = function() {
        console.error('Font Manager: Error reading font pack file.');
        showNotification('è¯»å–å­—ä½“åŒ…æ–‡ä»¶æ—¶å‘ç”Ÿé”™è¯¯ã€‚');
        event.target.value = null;
    };
    reader.readAsText(file);
}

function processImportedFontPackData(data) {
    if (typeof data !== 'object' || data === null) {
        showNotification('å¯¼å…¥å¤±è´¥ï¼šå­—ä½“åŒ…æ–‡ä»¶æ ¼å¼æ— æ•ˆã€‚');
        return;
    }

    let confirmationMessage = 'è¿™å°†è¦†ç›–æ‚¨å½“å‰çš„å­—ä½“è®¾ç½®å’Œå¯¼å…¥çš„@importè§„åˆ™ã€‚\n\n';

    if (data.version !== FONT_PACK_VERSION) {
        confirmationMessage += `è­¦å‘Šï¼šå­—ä½“åŒ…ç‰ˆæœ¬ (${data.version || 'æœªçŸ¥'}) ä¸Žå½“å‰è„šæœ¬æœŸæœ›çš„ç‰ˆæœ¬ (${FONT_PACK_VERSION}) ä¸åŒ¹é…ã€‚ç»§ç»­å¯¼å…¥å¯èƒ½å¯¼è‡´éƒ¨åˆ†è®¾ç½®æ— æ³•æ­£ç¡®åº”ç”¨æˆ–å‡ºçŽ°æ„å¤–è¡Œä¸ºã€‚\n\n`;
    }
    confirmationMessage += 'æ‚¨ç¡®å®šè¦ç»§ç»­å—ï¼Ÿ';

    const proceed = confirm(confirmationMessage);
    if (!proceed) return;

    try {
        const existingStyles = docContext.head.querySelectorAll('style[id^="font-manager-runtime-import-"]');
        existingStyles.forEach(style => style.remove());
        console.log('Cleared old runtime @import styles.');

        const settings = data.settings || {};
        const colorPalette = data.colorPalette || {};

        state.importedFontFamilies = Array.isArray(settings.importedFontFamilies) ? settings.importedFontFamilies : [];
        state.fontImportUrls = Array.isArray(settings.fontImportUrls) ? settings.fontImportUrls : [];

        saveFontsToStorage();
        saveFontImportUrlsToStorage();
        console.log('Imported font families and URLs:', state.importedFontFamilies, state.fontImportUrls);

        let importedLocalFontsCount = 0;
        if (Array.isArray(settings.localFonts) && settings.localFonts.length > 0) {
            const existingLocalFontStyles = docContext.querySelectorAll('style[id^="font-manager-local-font-"]');
            existingLocalFontStyles.forEach(style => style.remove());

            state.localFonts = settings.localFonts;
            importedLocalFontsCount = settings.localFonts.length;

            applyLocalFontsToPage();

            console.log(`Font Manager: å¯¼å…¥å¹¶åº”ç”¨äº† ${importedLocalFontsCount} ä¸ªæœ¬åœ°å­—ä½“`);
        }

        if (settings.multilangEnabled !== undefined) {
            state.multilangEnabled = settings.multilangEnabled;
        }

        if (settings.multilangFonts && typeof settings.multilangFonts === 'object') {
            Object.entries(languageTypes).forEach(([langKey, langConfig]) => {
                state[langConfig.stateKey] = settings.multilangFonts[langKey] || null;
            });
        }

        saveMultilangSettings();
        applyMultilangCSS();

        state.fontImportUrls.forEach((importUrl, index) => {
            if (importUrl && typeof importUrl === 'string' && importUrl.trim().toLowerCase().startsWith('@import')) {
                try {
                    const styleTag = docContext.createElement('style');
                    styleTag.id = `font-manager-runtime-import-pack-${Date.now()}-${index}`;
                    styleTag.textContent = importUrl;
                    docContext.head.appendChild(styleTag);
                    console.log('Injected from pack:', importUrl);
                } catch (e) {
                    console.error('Error injecting style tag from pack:', importUrl, e);
                }
            } else {
                 console.warn('Skipping invalid import URL from pack:', importUrl);
            }
        });

        if (settings.activeFont) {
            setTimeout(() => {
                applyFontToBody(settings.activeFont);
                 console.log('Applied active font from pack:', settings.activeFont);
            }, 700);
        } else {
            resetToDefaultFont();
        }

        if (settings.globalFontSize) {
            applyGlobalFontSize(parseFloat(settings.globalFontSize));
            const fontSizeInput = docContext.getElementById('globalFontSizeInput');
            if (fontSizeInput) fontSizeInput.value = parseFloat(settings.globalFontSize);
        } else {
            applyGlobalFontSize(null);
            const fontSizeInput = docContext.getElementById('globalFontSizeInput');
            if (fontSizeInput) fontSizeInput.value = initialMainFontSizeNumber;
        }

        if (settings.iconSize) {
            applyIconSize(parseFloat(settings.iconSize));
            const iconSizeInput = docContext.getElementById('iconSizeInput');
            if (iconSizeInput) iconSizeInput.value = parseFloat(settings.iconSize);
        } else {
            applyIconSize(null);
            const iconSizeInput = docContext.getElementById('iconSizeInput');
            if (iconSizeInput) iconSizeInput.value = initialIconSizeNumber;
        }

        if (settings.buttonSize) {
            applyButtonSize(parseFloat(settings.buttonSize));
            const buttonSizeInput = docContext.getElementById('buttonSizeInput');
            if (buttonSizeInput) buttonSizeInput.value = parseFloat(settings.buttonSize);
        } else {
            applyButtonSize(null);
            const buttonSizeInput = docContext.getElementById('buttonSizeInput');
            if (buttonSizeInput) buttonSizeInput.value = initialButtonSizeNumber;
        }

        Object.keys(colorSettings).forEach(key => {
            if (colorPalette[key]) {
                applyColorSetting(key, colorPalette[key]);
            } else {
                resetColorSetting(key);
            }
        });

        if (settings.hljsColors && typeof settings.hljsColors === 'object') {
            const colorsToApply = {};
            Object.values(hljsScopeSettings).forEach(scope => {
                if (settings.hljsColors[scope.lsKey]) {
                    colorsToApply[scope.stateKey] = settings.hljsColors[scope.lsKey];
                }
            });
            if (Object.keys(colorsToApply).length > 0) {
                 applyHljsStyling(colorsToApply, true); 
            } else {
                resetHljsStyling(); 
            }
            Object.entries(colorsToApply).forEach(([stateKey, colorValue]) => {
                 const Sconfig = Object.values(hljsScopeSettings).find(s => s.stateKey === stateKey);
                 if(Sconfig && colorValue) localStorage.setItem(Sconfig.lsKey, colorValue);
            });
        } else {
            resetHljsStyling();
        }

        if (settings.hljsFontFamily) {
            applyHljsFontFamily(settings.hljsFontFamily);
        } else {
            applyHljsFontFamily(null);
        }

        const mesCodeStylesToApply = {};
        let appliedMesCodeStyle = false;
        if (settings.mesCodeBgColor) {
            mesCodeStylesToApply.bgColor = settings.mesCodeBgColor;
            appliedMesCodeStyle = true;
        }
        if (settings.mesCodeTextColor) {
            mesCodeStylesToApply.textColor = settings.mesCodeTextColor;
            appliedMesCodeStyle = true;
        }
        if (settings.mesCodeBorderColor) {
            mesCodeStylesToApply.borderColor = settings.mesCodeBorderColor;
            appliedMesCodeStyle = true;
        }
        if (settings.mesCodeBackgroundOpacity) {
            mesCodeStylesToApply.backgroundOpacity = settings.mesCodeBackgroundOpacity;
            appliedMesCodeStyle = true;
        }
        if (settings.mesCodeBackgroundImage) {
            mesCodeStylesToApply.backgroundImage = settings.mesCodeBackgroundImage;
            appliedMesCodeStyle = true;
        }
        if (appliedMesCodeStyle) {
            applyMessageInlineCodeStyles(mesCodeStylesToApply, true);
            if(settings.mesCodeBgColor) localStorage.setItem(LOCAL_STORAGE_KEY_MES_CODE_BG_COLOR, settings.mesCodeBgColor);
            if(settings.mesCodeTextColor) localStorage.setItem(LOCAL_STORAGE_KEY_MES_CODE_TEXT_COLOR, settings.mesCodeTextColor);
            if(settings.mesCodeBorderColor) localStorage.setItem(LOCAL_STORAGE_KEY_MES_CODE_BORDER_COLOR, settings.mesCodeBorderColor);
            if(settings.mesCodeBackgroundOpacity) localStorage.setItem(LOCAL_STORAGE_KEY_MES_CODE_BACKGROUND_OPACITY, settings.mesCodeBackgroundOpacity);
            if(settings.mesCodeBackgroundImage) localStorage.setItem(LOCAL_STORAGE_KEY_MES_CODE_BACKGROUND_IMAGE, settings.mesCodeBackgroundImage);
        } else {
            resetMessageInlineCodeStyles();
        }

        if (settings.mesCodeFontFamily) {
            applyMesCodeFontFamily(settings.mesCodeFontFamily);
        } else {
            applyMesCodeFontFamily(null);
        }

        renderCombinedMainView();

        let successMessage = 'å­—ä½“åŒ…å¯¼å…¥æˆåŠŸï¼\n';
        successMessage += `- ${settings.importedFontFamilies ? settings.importedFontFamilies.length : 0} ä¸ªå­—ä½“\n`;
        successMessage += `- ${settings.fontImportUrls ? settings.fontImportUrls.length : 0} ä¸ªå¯¼å…¥é“¾æŽ¥\n`;
        if (importedLocalFontsCount > 0) {
            successMessage += `- ${importedLocalFontsCount} ä¸ªæœ¬åœ°TTFå­—ä½“\n`;
        }
        successMessage += `- æ´»åŠ¨å­—ä½“: ${settings.activeFont || 'æ— '}\n`;
        if (settings.globalFontSize) successMessage += '- å…¨å±€å­—ä½“å¤§å°å·²åº”ç”¨\n';
        if (settings.iconSize) successMessage += '- å›¾æ ‡å¤§å°å·²åº”ç”¨\n';
        if (settings.buttonSize) successMessage += '- æŒ‰é’®å¤§å°å·²åº”ç”¨\n';
        if (Object.keys(colorPalette).length > 0 && Object.values(colorPalette).some(v => v !== null)) successMessage += '- UIé¢œè‰²è®¾ç½®å·²åº”ç”¨\n';
        if (settings.hljsColors && Object.keys(settings.hljsColors).length > 0 && Object.values(settings.hljsColors).some(v => v !== null)) successMessage += '- HLJSé¢œè‰²è®¾ç½®å·²åº”ç”¨\n';
        if (settings.mesCodeBgColor || settings.mesCodeTextColor || settings.mesCodeBorderColor) successMessage += '- å†…è”ä»£ç æ ·å¼å·²åº”ç”¨\n';
        if (settings.multilangEnabled !== undefined) successMessage += `- å¤šè¯­è¨€å­—ä½“ç¾ŽåŒ–: ${settings.multilangEnabled ? 'å·²å¯ç”¨' : 'å·²ç¦ç”¨'}\n`;
        if (settings.multilangFonts && Object.values(settings.multilangFonts).some(v => v)) successMessage += '- å¤šè¯­è¨€å­—ä½“è®¾ç½®å·²åº”ç”¨';

        showNotification(successMessage.trim());

    } catch (error) {
        console.error('å¤„ç†å¯¼å…¥çš„å­—ä½“åŒ…æ•°æ®æ—¶å‡ºé”™:', error);
        showNotification(`å¯¼å…¥å¤±è´¥: ${error.message}`);
    }
}

function applyGlobalFontSize(sizeInPx) {
    const inputElement = docContext.getElementById('globalFontSizeInput');
    const mesList = docContext.querySelectorAll('.mes');
    const mesTextList = docContext.querySelectorAll('.mes_text');

    if (sizeInPx === null || sizeInPx === undefined || sizeInPx === '') {
        // æ¢å¤é»˜è®¤å­—ä½“å¤§å°
        mesList.forEach(mes => mes.style.removeProperty('font-size'));
        mesTextList.forEach(mesText => {
            mesText.style.removeProperty('line-height');
            mesText.style.removeProperty('letter-spacing');
        });
        localStorage.removeItem(LOCAL_STORAGE_KEY_GLOBAL_FONT_SIZE);
        if (inputElement) inputElement.value = initialMainFontSizeNumber;
        console.log('Font Manager: Chat bubble font size reset to default (' + initialMainFontSizeFromCSS + ').');
    } else {
        const newSizeString = `${sizeInPx}px`;
        // è®¾ç½®æ°”æ³¡å­—ä½“å¤§å°
        mesList.forEach(mes => mes.style.fontSize = newSizeString);

        // ===== è‡ªåŠ¨æŽ¨ç®—è¡Œé«˜å’Œå­—é—´è· =====
        const autoLineHeight = (sizeInPx * 1.5) + 'px';
        const autoLetterSpacing = (sizeInPx / 25) + 'px';
        mesTextList.forEach(mesText => {
            mesText.style.lineHeight = autoLineHeight;
            mesText.style.letterSpacing = autoLetterSpacing;
        });

        localStorage.setItem(LOCAL_STORAGE_KEY_GLOBAL_FONT_SIZE, sizeInPx.toString());
        if (inputElement) inputElement.value = sizeInPx;
        console.log('Font Manager: Chat bubble font size applied:', newSizeString);
    }
}

function applyIconSize(sizeInPx) {
