state.rgbToHex = (colorStr) => {
    if (!colorStr || typeof colorStr !== 'string') return null;
    if (colorStr.startsWith('#')) return colorStr;

    const tempEl = docContext.createElement('div');
    tempEl.style.color = colorStr;
    docContext.body.appendChild(tempEl);
    const computedColor = getComputedStyle(tempEl).color;
    docContext.body.removeChild(tempEl);

    const match = computedColor.match(/^rgb\((\d+),\s*(\d+),\s*(\d+)\)$/);
    if (match) {
        const r = parseInt(match[1], 10).toString(16).padStart(2, '0');
        const g = parseInt(match[2], 10).toString(16).padStart(2, '0');
        const b = parseInt(match[3], 10).toString(16).padStart(2, '0');
        return `#${r}${g}${b}`;
    }
    return colorStr;
};

function getInitialMessageInlineCodeStyles() {
    const rgbToHex = (rgb) => {
        if (!rgb || typeof rgb !== 'string') return null;
        if (rgb.startsWith('#')) return rgb;
        if (rgb === 'rgba(0, 0, 0, 0)' || rgb === 'transparent') return 'transparent';

        const result = rgb.match(/^rgba?\((\d+),\s*(\d+),\s*(\d+)(?:,\s*([\d.]+))?\)$/);
        if (!result) return null;

        // æ£€æŸ¥é€æ˜Žåº¦
        const alpha = result[4] ? parseFloat(result[4]) : 1;
        if (alpha === 0) return 'transparent';

        const r = parseInt(result[1], 10).toString(16).padStart(2, '0');
        const g = parseInt(result[2], 10).toString(16).padStart(2, '0');
        const b = parseInt(result[3], 10).toString(16).padStart(2, '0');
        return `#${r}${g}${b}`;
    };

    // è®¾ç½®é»˜è®¤çš„å®‰å…¨å›žé€€å€¼
    let defaultBgColor = 'transparent';
    let defaultTextColor = '#d19a66';
    let defaultBorderColor = '#d2b48c';

    // é¦–å…ˆå°è¯•ä»Žé¡µé¢çŽ°æœ‰çš„ .mes_text code å…ƒç´ èŽ·å–æ ·å¼
    const existingMesCodeElements = docContext.querySelectorAll('.mes_text code');

    if (existingMesCodeElements.length > 0) {
        console.log(`Font Manager: æ£€æµ‹åˆ° ${existingMesCodeElements.length} ä¸ªçŽ°æœ‰.mes_text codeå…ƒç´ ï¼Œç›´æŽ¥è¯»å–æ ·å¼`);

        // ä»Žç¬¬ä¸€ä¸ªçŽ°æœ‰å…ƒç´ èŽ·å–æ ·å¼
        const firstElement = existingMesCodeElements[0];

        try {
            const computedStyle = docContext.defaultView.getComputedStyle(firstElement);

            const bgColor = computedStyle.backgroundColor;
            const textColor = computedStyle.color;
            const borderColor = computedStyle.borderColor || computedStyle.borderTopColor || computedStyle.borderLeftColor;

            // å¤„ç†èƒŒæ™¯è‰²
            const convertedBgColor = rgbToHex(bgColor);
            state.initialMesCodeBgColor = convertedBgColor || defaultBgColor;

            // å¤„ç†æ–‡æœ¬è‰²
            const convertedTextColor = rgbToHex(textColor);
            state.initialMesCodeTextColor = convertedTextColor || defaultTextColor;

            // å¤„ç†è¾¹æ¡†è‰²
            const convertedBorderColor = rgbToHex(borderColor);
            state.initialMesCodeBorderColor = convertedBorderColor || defaultBorderColor;

            console.log(`Font Manager: ä»ŽçŽ°æœ‰å…ƒç´ è¯»å–.mes_text codeæ ·å¼ - BG: ${state.initialMesCodeBgColor}, Text: ${state.initialMesCodeTextColor}, Border: ${state.initialMesCodeBorderColor}`);

        } catch (e) {
            console.warn('Font Manager: è¯»å–çŽ°æœ‰.mes_text codeå…ƒç´ æ ·å¼å¤±è´¥ï¼Œä½¿ç”¨é»˜è®¤å€¼', e);
            state.initialMesCodeBgColor = defaultBgColor;
            state.initialMesCodeTextColor = defaultTextColor;
            state.initialMesCodeBorderColor = defaultBorderColor;
        }

    } else {
        console.log('Font Manager: æœªæ‰¾åˆ°çŽ°æœ‰.mes_text codeå…ƒç´ ï¼Œåˆ›å»ºçº¯å‡€ä¸´æ—¶å…ƒç´ æ£€æµ‹ä¸»é¢˜æ ·å¼');

        // åˆ›å»ºå®Œå…¨çº¯å‡€çš„ä¸´æ—¶å…ƒç´ æ¥æ£€æµ‹ä¸»é¢˜çš„çœŸå®žæ ·å¼
        const tempContainer = docContext.createElement('div');
        tempContainer.style.cssText = 'position: absolute; left: -9999px; visibility: hidden; z-index: -1; pointer-events: none;';

        const tempParent = docContext.createElement('div');
        tempParent.className = 'mes_text';

        const tempElement = docContext.createElement('code');
        tempElement.textContent = 'test';

        tempParent.appendChild(tempElement);
        tempContainer.appendChild(tempParent);
        docContext.body.appendChild(tempContainer);

        // ç­‰å¾…ä¸€å¸§ç¡®ä¿æ ·å¼åº”ç”¨
        setTimeout(() => {
            try {
                const computed = docContext.defaultView.getComputedStyle(tempElement);

                const bgColor = computed.backgroundColor;
                const textColor = computed.color;
                const borderColor = computed.borderColor || computed.borderTopColor || computed.borderLeftColor;

                console.log(`Font Manager: ä¸´æ—¶å…ƒç´ åŽŸå§‹CSSå€¼ - BG: "${bgColor}", Text: "${textColor}", Border: "${borderColor}"`);

                // å¤„ç†èƒŒæ™¯è‰² - ä¿æŒé€æ˜Žå°±æ˜¯é€æ˜Ž
                const convertedBgColor = rgbToHex(bgColor);
                if (convertedBgColor === 'transparent' || convertedBgColor === null) {
                    state.initialMesCodeBgColor = 'transparent';
                } else {
                    state.initialMesCodeBgColor = convertedBgColor;
                }

                // å¤„ç†æ–‡æœ¬è‰²
                const convertedTextColor = rgbToHex(textColor);
                state.initialMesCodeTextColor = convertedTextColor || defaultTextColor;

                // å¤„ç†è¾¹æ¡†è‰²
                const convertedBorderColor = rgbToHex(borderColor);
                state.initialMesCodeBorderColor = convertedBorderColor || defaultBorderColor;

                console.log(`Font Manager: ä»Žä¸´æ—¶å…ƒç´ æ£€æµ‹.mes_text codeæ ·å¼ - BG: ${state.initialMesCodeBgColor}, Text: ${state.initialMesCodeTextColor}, Border: ${state.initialMesCodeBorderColor}`);

            } catch (e) {
                console.warn('Font Manager: ä¸´æ—¶å…ƒç´ æ ·å¼æ£€æµ‹å¤±è´¥ï¼Œä½¿ç”¨é¢„å®šä¹‰é»˜è®¤å€¼', e);
                state.initialMesCodeBgColor = defaultBgColor;
                state.initialMesCodeTextColor = defaultTextColor;  
                state.initialMesCodeBorderColor = defaultBorderColor;
            }

            // æ¸…ç†ä¸´æ—¶å…ƒç´ 
            if (tempContainer.parentNode) {
                docContext.body.removeChild(tempContainer);
            }

            console.log(`Font Manager: .mes_text code åˆå§‹æ ·å¼æ£€æµ‹å®Œæˆ - BG: ${state.initialMesCodeBgColor}, Text: ${state.initialMesCodeTextColor}, Border: ${state.initialMesCodeBorderColor}`);
        }, 0);

        // åŒæ­¥è®¾ç½®é»˜è®¤å€¼ä»¥é˜²å¼‚æ­¥æ£€æµ‹å¤±è´¥
        state.initialMesCodeBgColor = defaultBgColor;
        state.initialMesCodeTextColor = defaultTextColor;
        state.initialMesCodeBorderColor = defaultBorderColor;
    }
}

function applyMessageInlineCodeStyles(styles, fromImport = false) {
    // åªå¤„ç†ä¸å±žäºŽHLJSçš„ä»£ç å…ƒç´ 
    const elements = docContext.querySelectorAll('.mes_text code:not(.hljs):not(.hljs *)');
    if (!elements.length) return;

    const { bgColor, textColor, borderColor, backgroundOpacity, backgroundImage } = styles;

    elements.forEach(el => {
        // ç¡®ä¿ä¸ä¼šå½±å“HLJSå…ƒç´ 
        if (el.closest('.hljs') || el.classList.contains('hljs')) {
            return;
        }

        if (bgColor !== undefined) {
            if (backgroundOpacity !== undefined) {
                // å¦‚æžœåŒæ—¶è®¾ç½®äº†èƒŒæ™¯è‰²å’Œé€æ˜Žåº¦ï¼Œä½¿ç”¨rgbaæ ¼å¼
                const rgbaColor = mesCodeHexToRgba(bgColor, parseFloat(backgroundOpacity));
                el.style.setProperty('background-color', rgbaColor, 'important');
            } else {
                el.style.setProperty('background-color', bgColor, 'important');
            }
        } else if (backgroundOpacity !== undefined && state.mesCodeBgColor) {
            // åªè®¾ç½®é€æ˜Žåº¦æ—¶ï¼Œä½¿ç”¨çŽ°æœ‰èƒŒæ™¯è‰²
            const rgbaColor = mesCodeHexToRgba(state.mesCodeBgColor, parseFloat(backgroundOpacity));
            el.style.setProperty('background-color', rgbaColor, 'important');
        }

        if (textColor !== undefined) el.style.setProperty('color', textColor, 'important');
        if (borderColor !== undefined) el.style.setProperty('border-color', borderColor, 'important');

        if (backgroundImage !== undefined) {
            const displayMode = state.mesCodeBackgroundDisplayMode || 'cover';
            const position = state.mesCodeBackgroundPosition || 'center';
            const backgroundStyles = getBackgroundStyleFromOptions(backgroundImage, displayMode, position);

            Object.entries(backgroundStyles).forEach(([property, value]) => {
                if (value && value !== 'none') {
                    el.style.setProperty(property, value, 'important');
                } else {
                    el.style.removeProperty(property);
                }
            });
        }
    });

    if (bgColor !== undefined) state.mesCodeBgColor = bgColor;
    if (textColor !== undefined) state.mesCodeTextColor = textColor;
    if (borderColor !== undefined) state.mesCodeBorderColor = borderColor;
    if (backgroundOpacity !== undefined) state.mesCodeBackgroundOpacity = backgroundOpacity;
    if (backgroundImage !== undefined) state.mesCodeBackgroundImage = backgroundImage;

    if (!fromImport) {
        if (bgColor !== undefined) localStorage.setItem(LOCAL_STORAGE_KEY_MES_CODE_BG_COLOR, bgColor);
        if (textColor !== undefined) localStorage.setItem(LOCAL_STORAGE_KEY_MES_CODE_TEXT_COLOR, textColor);
        if (borderColor !== undefined) localStorage.setItem(LOCAL_STORAGE_KEY_MES_CODE_BORDER_COLOR, borderColor);
        if (backgroundOpacity !== undefined) localStorage.setItem(LOCAL_STORAGE_KEY_MES_CODE_BACKGROUND_OPACITY, backgroundOpacity);
        if (backgroundImage !== undefined) localStorage.setItem(LOCAL_STORAGE_KEY_MES_CODE_BACKGROUND_IMAGE, backgroundImage);
    }

    // æ›´æ–°æ‰€æœ‰è¾“å…¥æ¡†çš„å€¼
    const bgInput = docContext.getElementById('mesCodeBgColorInput');
    const textInput = docContext.getElementById('mesCodeTextColorInput');
    const borderInput = docContext.getElementById('mesCodeBorderColorInput');
    const opacityInput = docContext.getElementById('mesCodeBackgroundOpacityInput');
    const imageInput = docContext.getElementById('mesCodeBackgroundImageInput');

    if (bgInput && state.mesCodeBgColor) {
        bgInput.value = state.mesCodeBgColor;
        if (bgInput.nextElementSibling) bgInput.nextElementSibling.textContent = state.mesCodeBgColor;
    }
    if (textInput && state.mesCodeTextColor) {
        textInput.value = state.mesCodeTextColor;
        if (textInput.nextElementSibling) textInput.nextElementSibling.textContent = state.mesCodeTextColor;
    }
    if (borderInput && state.mesCodeBorderColor) {
        borderInput.value = state.mesCodeBorderColor;
        if (borderInput.nextElementSibling) borderInput.nextElementSibling.textContent = state.mesCodeBorderColor;
    }
    if (opacityInput && state.mesCodeBackgroundOpacity !== null) {
        opacityInput.value = state.mesCodeBackgroundOpacity;
        const valueDisplay = opacityInput.nextElementSibling;
        if (valueDisplay) valueDisplay.textContent = (parseFloat(state.mesCodeBackgroundOpacity) * 100).toFixed(0) + '%';
    }
    if (imageInput && state.mesCodeBackgroundImage !== null) {
        imageInput.value = state.mesCodeBackgroundImage || '';
    }

    console.log('Applied .mes_text code styles:', styles);
}

