function applyCurrentHljsStylesToElement(hljsBlockElement) {
    // åº”ç”¨å­—ä½“åˆ°ä¸»å…ƒç´ å’Œæ‰€æœ‰å­å…ƒç´ 
    if (state.hljsFontFamily) {
        hljsBlockElement.style.setProperty('font-family', state.hljsFontFamily, 'important');
        // åŒæ—¶åº”ç”¨åˆ°æ‰€æœ‰å­å…ƒç´ 
        const subElements = hljsBlockElement.querySelectorAll('*');
        subElements.forEach(subEl => {
            subEl.style.setProperty('font-family', state.hljsFontFamily, 'important');
        });
    } else {
        hljsBlockElement.style.removeProperty('font-family');
        const subElements = hljsBlockElement.querySelectorAll('*');
        subElements.forEach(subEl => {
            subEl.style.removeProperty('font-family');
        });
    }

    Object.values(hljsScopeSettings).forEach(scope => {
        const colorValue = state[scope.stateKey];
        if (colorValue !== undefined && colorValue !== null) {
            try {
                const subElements = hljsBlockElement.querySelectorAll(scope.selectors.join(', '));

                if (scope.isImage) {
                    // å¤„ç†èƒŒæ™¯å›¾ç‰‡
                    subElements.forEach(subEl => {
                        if (colorValue && colorValue !== 'none') {
                            subEl.style.setProperty('background-image', `url(${colorValue})`, 'important');
                            subEl.style.setProperty('background-size', 'cover', 'important');
                            subEl.style.setProperty('background-position', 'center', 'important');
                            subEl.style.setProperty('background-repeat', 'no-repeat', 'important');
                            subEl.style.setProperty('background-attachment', 'local', 'important');
                        }
                    });

                    // æ£€æŸ¥ä¸»å…ƒç´ æ˜¯å¦åŒ¹é…
                    let mainBlockMatches = false;
                    for (const sel of scope.selectors) {
                        if (hljsBlockElement.matches && hljsBlockElement.matches(sel)) {
                            mainBlockMatches = true;
                            break;
                        }
                    }
                    if (mainBlockMatches && colorValue && colorValue !== 'none') {
                        hljsBlockElement.style.setProperty('background-image', `url(${colorValue})`, 'important');
                        hljsBlockElement.style.setProperty('background-size', 'cover', 'important');
                        hljsBlockElement.style.setProperty('background-position', 'center', 'important');
                        hljsBlockElement.style.setProperty('background-repeat', 'no-repeat', 'important');
                        hljsBlockElement.style.setProperty('background-attachment', 'local', 'important');
                    }
                } else {
                    // å¤„ç†å…¶ä»–æ ·å¼
                    subElements.forEach(subEl => {
                        subEl.style.setProperty(scope.cssProperty, colorValue, 'important');
                    });

                    let mainBlockMatches = false;
                    for (const sel of scope.selectors) {
                        if (hljsBlockElement.matches && hljsBlockElement.matches(sel)) {
                            mainBlockMatches = true;
                            break;
                        }
                    }
                    if (mainBlockMatches) {
                        hljsBlockElement.style.setProperty(scope.cssProperty, colorValue, 'important');
                    }
                }
            } catch (e) {
            }
        }
    });
}

let hljsObserver = null;

function handleDynamicContentChanges(mutationsList, observer) { 
    console.log('Font Manager: handleDynamicContentChanges triggered.');
    for (const mutation of mutationsList) {
        if (mutation.type === 'childList') {
            if (mutation.addedNodes.length > 0) {
                mutation.addedNodes.forEach(node => {
                    if (node.nodeType === Node.ELEMENT_NODE) {
                        if (node.matches && (node.matches('.hljs') || node.matches('pre code.hljs') || node.matches('code.hljs'))) {
                            console.log(`%cFont Manager: Applying HLJS styles (font: "${state.hljsFontFamily || '(none)'}") to added node:`, 'color: orange', node);
                            applyCurrentHljsStylesToElement(node);
                        }
                        const newHljsElements = node.querySelectorAll('.hljs, pre code.hljs, code.hljs');
                        if (newHljsElements.length > 0) {
                            console.log(`%cFont Manager: Found ${newHljsElements.length} new HLJS elements in added subtree (font: "${state.hljsFontFamily || '(none)'}"). Applying styles.`, 'color: orange', newHljsElements);
                            newHljsElements.forEach(applyCurrentHljsStylesToElement);
                        }

                        if (node.matches && node.matches('.mes_text code')) {
                            console.log(`%cFont Manager: Applying .mes_text code styles (font: "${state.mesCodeFontFamily || '(none)'}") to added node:`, 'color: cyan', node);
                            applyCurrentMesCodeStylesToElement(node);
                        }
                        const newMesCodeElements = node.querySelectorAll('.mes_text code');
                        if (newMesCodeElements.length > 0) {
                            console.log(`%cFont Manager: Found ${newMesCodeElements.length} new .mes_text code elements in added subtree (font: "${state.mesCodeFontFamily || '(none)'}"). Applying styles.`, 'color: cyan', newMesCodeElements);
                            newMesCodeElements.forEach(applyCurrentMesCodeStylesToElement);
                        }
                    }
                });
            }
        }
    }
}

function setupDynamicContentObserver() { 
    if (hljsObserver) {
        hljsObserver.disconnect(); 
    }

    const targetNode = docContext.getElementById('chat') || docContext.getElementById('messages') || docContext.body;
    const config = { childList: true, subtree: true };

    hljsObserver = new MutationObserver(handleDynamicContentChanges); 
    hljsObserver.observe(targetNode, config);
    console.log('Font Manager: DynamicContentObserver set up for HLJS and .mes_text code on target:', targetNode);

    setTimeout(() => {
        const existingHljsElements = docContext.querySelectorAll('.hljs, pre code.hljs, code.hljs');
        console.log(`%cFont Manager: Delayed Initial scan - Found ${existingHljsElements.length} existing HLJS elements. Applying styles. HLJS Font in state: "${state.hljsFontFamily || '(none)'}"`, 'color: orange');
        existingHljsElements.forEach(applyCurrentHljsStylesToElement);

        const existingMesCodeElements = docContext.querySelectorAll('.mes_text code');
        console.log(`%cFont Manager: Delayed Initial scan - Found ${existingMesCodeElements.length} existing .mes_text code elements. Applying styles. MesCode Font in state: "${state.mesCodeFontFamily || '(none)'}"`, 'color: cyan');
        existingMesCodeElements.forEach(applyCurrentMesCodeStylesToElement);
        console.log('Font Manager: Delayed initial style application complete.');
    }, 100);

}

function applyCurrentMesCodeStylesToElement(mesCodeElement) {
    // ç¡®ä¿ä¸ä¼šå½±å“HLJSå…ƒç´ 
    if (mesCodeElement.closest('.hljs') || mesCodeElement.classList.contains('hljs')) {
        return;
    }

    // å¦‚æžœæ˜¯å•å­—ä½“æ¨¡å¼ï¼Œä½¿ç”¨å•å­—ä½“ï¼›å¦åˆ™ä½¿ç”¨å†…è”ä»£ç å­—ä½“è®¾ç½®
    if (state.fontApplicationMode === 'single' && state.singleFontFamily) {
        mesCodeElement.style.setProperty('font-family', state.singleFontFamily, 'important');
    } else if (state.mesCodeFontFamily) {
        mesCodeElement.style.setProperty('font-family', state.mesCodeFontFamily, 'important');
    } else {
        mesCodeElement.style.removeProperty('font-family');
    }

    if (state.mesCodeBgColor) {
        if (state.mesCodeBackgroundOpacity) {
            // å¦‚æžœè®¾ç½®äº†é€æ˜Žåº¦ï¼Œä½¿ç”¨rgbaæ ¼å¼
            const rgbaColor = mesCodeHexToRgba(state.mesCodeBgColor, parseFloat(state.mesCodeBackgroundOpacity));
            mesCodeElement.style.setProperty('background-color', rgbaColor, 'important');
        } else {
            mesCodeElement.style.setProperty('background-color', state.mesCodeBgColor, 'important');
        }
    }
    if (state.mesCodeTextColor) {
        mesCodeElement.style.setProperty('color', state.mesCodeTextColor, 'important');
    }
    if (state.mesCodeBorderColor) {
        mesCodeElement.style.setProperty('border-color', state.mesCodeBorderColor, 'important');
    }

    if (state.mesCodeBackgroundImage) {
        if (state.mesCodeBackgroundImage !== 'none') {
            // ä½¿ç”¨æ­£ç¡®çš„èƒŒæ™¯å›¾ç‰‡è®¾ç½®å‡½æ•°
            const displayMode = state.mesCodeBackgroundDisplayMode || 'cover';
            const position = state.mesCodeBackgroundPosition || 'center';
            const backgroundStyles = getBackgroundStyleFromOptions(state.mesCodeBackgroundImage, displayMode, position);

            Object.entries(backgroundStyles).forEach(([property, value]) => {
                if (value && value !== 'none') {
                    mesCodeElement.style.setProperty(property, value, 'important');
                } else {
                    mesCodeElement.style.removeProperty(property);
                }
            });
        } else {
            mesCodeElement.style.removeProperty('background-image');
            mesCodeElement.style.removeProperty('background-size');
            mesCodeElement.style.removeProperty('background-position');
            mesCodeElement.style.removeProperty('background-repeat');
            mesCodeElement.style.removeProperty('background-attachment');
        }
    }

    // åº”ç”¨é«˜åº¦è®¾ç½®
    if (state.mesCodeHeight) {
        const heightValue = `${state.mesCodeHeight}px`;
        mesCodeElement.style.setProperty('display', 'inline-block', 'important');
        mesCodeElement.style.setProperty('padding-block', heightValue, 'important');
        mesCodeElement.style.setProperty('white-space', 'nowrap', 'important');
    } else {
        mesCodeElement.style.removeProperty('padding-block');
        mesCodeElement.style.removeProperty('display');
        mesCodeElement.style.removeProperty('white-space');
    }
}

