// ç›‘å¬ä¸»é¢˜å˜åŒ–
function setupThemeChangeObserver() {
    // ç›´æŽ¥ç›‘å¬SillyTavernçš„ä¸»é¢˜é€‰æ‹©å™¨
    const themeSelector = docContext.getElementById('themes');
    if (themeSelector) {
        themeSelector.addEventListener('change', (event) => {
            const newTheme = event.target.value;
            if (newTheme && newTheme !== currentTheme) {
                const bindingPreset = themeMultilangBindings[newTheme];
                if (bindingPreset && multilangPresets[bindingPreset]) {
                    console.log(`æ£€æµ‹åˆ°ä¸»é¢˜å˜åŒ–: ${currentTheme} -> ${newTheme}ï¼Œåº”ç”¨ç»‘å®šé¢„è®¾: ${bindingPreset}`);
                    currentTheme = newTheme;
                    applyMultilangPreset(bindingPreset, true); // ä¼ å…¥trueè¡¨ç¤ºæ˜¯è‡ªåŠ¨åº”ç”¨
                } else {
                    currentTheme = newTheme;
                    console.log(`ä¸»é¢˜åˆ‡æ¢åˆ°: ${newTheme}ï¼Œæ²¡æœ‰ç»‘å®šçš„å­—ä½“é¢„è®¾ï¼Œé‡ç½®åˆ°é»˜è®¤çŠ¶æ€`);
                    resetToDefaultMultilangState(newTheme);
                }
            }
        });
        console.log('Font Manager: å·²è®¾ç½®ä¸»é¢˜é€‰æ‹©å™¨å˜åŒ–ç›‘å¬');
    } else {
        console.warn('Font Manager: æœªæ‰¾åˆ°ä¸»é¢˜é€‰æ‹©å™¨ï¼Œå°†åœ¨å»¶è¿ŸåŽé‡è¯•');
        // å»¶è¿Ÿé‡è¯•ï¼Œç¡®ä¿é¡µé¢å®Œå…¨åŠ è½½
        setTimeout(() => {
            setupThemeChangeObserver();
        }, 2000);
    }

    // ä¿ç•™åŽŸæœ‰çš„DOMå˜åŒ–ç›‘å¬ä½œä¸ºå¤‡é€‰æ–¹æ¡ˆ
    const observer = new MutationObserver((mutations) => {
        mutations.forEach((mutation) => {
            if (mutation.type === 'attributes' && 
                (mutation.attributeName === 'href' || mutation.attributeName === 'data-theme' || mutation.attributeName === 'class')) {
                const newTheme = detectCurrentTheme();
                const bindingPreset = themeMultilangBindings[newTheme];
                if (newTheme !== currentTheme) {
                    if (bindingPreset && multilangPresets[bindingPreset]) {
                        console.log(`é€šè¿‡DOMç›‘å¬æ£€æµ‹åˆ°ä¸»é¢˜å˜åŒ–: ${currentTheme} -> ${newTheme}ï¼Œåº”ç”¨ç»‘å®šé¢„è®¾: ${bindingPreset}`);
                        currentTheme = newTheme;
                        applyMultilangPreset(bindingPreset, true);
                    } else {
                        console.log(`é€šè¿‡DOMç›‘å¬æ£€æµ‹åˆ°ä¸»é¢˜å˜åŒ–: ${currentTheme} -> ${newTheme}ï¼Œæ²¡æœ‰ç»‘å®šçš„å­—ä½“é¢„è®¾ï¼Œé‡ç½®åˆ°é»˜è®¤çŠ¶æ€`);
                        currentTheme = newTheme;
                        resetToDefaultMultilangState(newTheme);
                    }
                }
            }
        });
    });

    observer.observe(docContext.documentElement, {
        attributes: true,
        subtree: true,
        attributeFilter: ['href', 'data-theme', 'class']
    });

    const links = docContext.querySelectorAll('link');
    links.forEach(link => {
        observer.observe(link, {
            attributes: true,
            attributeFilter: ['href']
        });
    });
}

