        saveBtn.setAttribute("class", "menu_button");
        saveBtn.setAttribute("style", "margin-right: 10px;");
        saveBtn.onclick = (function(tagName, textarea) {
            return function() {
                var newContent = textarea.value.trim();
                if (newContent === '') {
                    var currentData = getStoredData();
