                    var currentData = getStoredData();
                    currentData[tagName] = newDataArray;
                    saveStoredData(currentData);
                }
                updateStorageDisplay(container);
                extensions.toastr.info("æ ‡è®° " + tagName + " çš„æ•°æ®å·²ä¿å­˜ï¼");
