async function batchOperateRegexes($items, action, applyUIState) {
  const operationId = `batch_${action}_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

devLog(`[FilterGroup] å¼€å§‹æ‰¹é‡æ“ä½œ [${operationId}]: ${action}`);
