function addInteractionEffects($modal) {
  // æ£€æµ‹è®¾å¤‡ç±»åž‹
  const isTouchDevice = 'ontouchstart' in window || navigator.maxTouchPoints > 0;

devLog(`[FilterGroup] è®¾å¤‡ç±»åž‹: ${isTouchDevice ? 'è§¦æ‘¸è®¾å¤‡' : 'éžè§¦æ‘¸è®¾å¤‡'}`);

