window.addEventListener('load', () => {
    // ç¡®ä¿ UI å·²ç»å‡†å¤‡å¥½ï¼ˆåŒ…æ‹¬ A å­—ä½“ç®¡ç†æŒ‰é’®ï¼‰
    const ensureInit = () => {
        if (!document.getElementById(MENU_BUTTON_ID)) {
            console.log("Font Manager: UI æœªå‡†å¤‡å¥½ï¼Œå»¶è¿Ÿæ¢å¤å­—ä½“...");
            setTimeout(ensureInit, 200);
            return;
        }

        // 1. åŠ è½½å¹¶åº”ç”¨æœ¬åœ°å­—ä½“
        loadLocalFontsFromIndexedDB().then(() => {
            applyLocalFontsToPage();

            // 2. æ¢å¤é“¾æŽ¥å­—ä½“
            const savedUrls = JSON.parse(localStorage.getItem(LOCAL_STORAGE_KEY_FONT_IMPORT_URLS) || "[]");
            savedUrls.forEach(url => {
                const styleId = `font-manager-runtime-import-${btoa(url).replace(/=/g, '')}`;
                if (!document.getElementById(styleId)) {
                    const styleEl = document.createElement('style');
                    styleEl.id = styleId;

                    const fontSource = detectFontSource(url);
                    if (fontSource === 'IMPORT') {
                        styleEl.textContent = `@import url("${url}");`;
                    } else if (fontSource === 'URL') {
                        const fontName = url.split('/').pop().split('.')[0];
                        styleEl.textContent = `
                            @font-face {
                                font-family: '${fontName}';
                                src: url('${url}');
                                font-display: swap;
                            }
                        `;
                    } else {
                        console.log(`Font Manager: è·³è¿‡æ— æ³•è¯†åˆ«çš„é“¾æŽ¥å­—ä½“ => ${url}`);
                    }

                    document.head.appendChild(styleEl);
                    console.log(`Font Manager: å·²æ¢å¤é“¾æŽ¥å­—ä½“/æ ·å¼ => ${url}`);
                }
            });

            // 3. æ¢å¤ä¸Šæ¬¡æ¿€æ´»çš„å­—ä½“
            const savedActiveFont = localStorage.getItem(LOCAL_STORAGE_KEY_ACTIVE_FONT);
            if (savedActiveFont) {
                state.activeFont = savedActiveFont;
                document.body.style.fontFamily = `"${savedActiveFont}", sans-serif`;
                console.log(`Font Manager: å·²æ¢å¤æ¿€æ´»å­—ä½“ "${savedActiveFont}"`);
            }

            // 4. æ¢å¤å¤šè¯­è¨€æ¨¡å¼
            loadMultilangSettings();
            applyMultilangCSS();
        });
    };

    ensureInit();
});

