    // ä»Žäº‘ç«¯èŽ·å–æ•°æ®
    fetchRecommendSites()
        .then(sitesData => {
            modalBodyElement.innerHTML = '';
            renderRecommendSites(sitesData);
        })
        .catch(error => {
            console.error('èŽ·å–æŽ¨èç½‘ç«™å¤±è´¥:', error);
            modalBodyElement.innerHTML = '';
        });

    _renderFooterButtons([
        { text: 'å…³é—­', className: 'font-manager-modal-button close', onClick: () => renderCombinedMainView() }
    ]);

    modalElement.style.display = 'flex';
}

function fetchRecommendSites() {
    return fetch(CLOUD_FONT_SITES_URL, {
        method: 'GET',
        headers: {
            'Accept': 'application/json',
        }
    })
    .then(response => {
        if (!response.ok) {
            throw new Error(`HTTP ${response.status}: ${response.statusText}`);
        }
        return response.json();
    })
    .catch(error => {
        console.error('äº‘ç«¯èŽ·å–å¤±è´¥:', error);
        throw error;
    });
}

function renderRecommendSites(sitesData) {
    if (!sitesData || !sitesData.websites || !Array.isArray(sitesData.websites)) {
        return;
    }

    const container = docContext.createElement('div');

    // æ·»åŠ æ›´æ–°æ—¶é—´æ˜¾ç¤º
