<script>
class BLMX_Protocol {
    // --- Unified log for chat and moments, preserving chronological order ---
    constructor(tavernHelperBridge, charId) {
        this.logEntries = []; // Unified log for all entries
        this.messageId = null;
        this.charId = charId;
        this.LOG_START_TAG = "===BLMX_LOG_BEGIN===";
        this.LOG_END_TAG = "===BLMX_LOG_END===";
        this.bridge = tavernHelperBridge;
    }

    async initialize() {
        console.log("[BLMX] Initializing and scanning for chat history...");
        const lastMessageId = await this.bridge.getLastMessageId();

        if (lastMessageId === null) {
            console.warn("[BLMX] No messages found. Starting fresh at message ID 0.");
            this.messageId = 0;
            this.logEntries = [];
            await this.persistLogToStorage();
            return true;
        }

        let latestUiMessage = null;
        const previousUiMessages = [];

        for (let i = lastMessageId; i >= 0; i--) {
            try {
                const msg = (await this.bridge.getChatMessages(i))[0];
                if (msg && msg.message && msg.message.includes(this.LOG_START_TAG)) {
                    if (!latestUiMessage) {
                        latestUiMessage = { id: i, content: msg.message };
                    } else {
                        previousUiMessages.push({ id: i, content: msg.message });
                    }
                }
            } catch (error) {
                // Ignore errors, likely message not found
            }
        }

        if (!latestUiMessage) {
            console.log("[BLMX] No UI log found. Creating a new one in the latest message.");
            this.messageId = lastMessageId;
            this.logEntries = [];
            await this.persistLogToStorage();
            return true;
        }

        console.log(`[BLMX] Found latest UI log in message ${latestUiMessage.id}. Consolidating...`);
        this.messageId = latestUiMessage.id;
        let consolidatedLogParts = [];

        const latestLogStartIndex = latestUiMessage.content.indexOf(this.LOG_START_TAG);
        const latestLogEndIndex = latestUiMessage.content.indexOf(this.LOG_END_TAG);
        if (latestLogStartIndex !== -1 && latestLogEndIndex !== -1) {
            const logPart = latestUiMessage.content.slice(latestLogStartIndex + this.LOG_START_TAG.length, latestLogEndIndex).trim();
            if(logPart) consolidatedLogParts.push(logPart);
        }

        for (const prevMsg of previousUiMessages) {
            const prevLogStartIndex = prevMsg.content.indexOf(this.LOG_START_TAG);
            const prevLogEndIndex = prevMsg.content.indexOf(this.LOG_END_TAG);
            if (prevLogStartIndex !== -1 && prevLogEndIndex !== -1) {
                const logPart = prevMsg.content.slice(prevLogStartIndex + this.LOG_START_TAG.length, prevLogEndIndex).trim();
                if(logPart) consolidatedLogParts.unshift(logPart);

                const cleanedContent = prevMsg.content.substring(0, prevLogStartIndex) + prevMsg.content.substring(prevLogEndIndex + this.LOG_END_TAG.length);
                await this.bridge.setChatMessage(cleanedContent.trim(), prevMsg.id, { refresh: "none" });
                console.log(`[BLMX] Cleaned and moved UI log from message ${prevMsg.id}.`);
            }
        }

        const finalLogString = consolidatedLogParts.join('\n');
        this._parseLogFromString(finalLogString);

        await this.persistLogToStorage();
        console.log(`[BLMX] Consolidated log saved to message ${this.messageId}.`);

        return true;
    }

    async persistLogToStorage() {
        if (this.messageId === null) {
             console.warn("[BLMX] Cannot save log, message_id not initialized.");
             return;
        }
        try {
            const logString = this._renderLogToString();
            const existingMessage = (await this.bridge.getChatMessages(this.messageId))[0];
            let existingContent = existingMessage ? existingMessage.message : '';

            const logStartIndex = existingContent.indexOf(this.LOG_START_TAG);
            const logEndIndex = existingContent.indexOf(this.LOG_END_TAG);

            const newLogBlock = `${this.LOG_START_TAG}\n${logString}\n${this.LOG_END_TAG}`;
            let fullText;

            if (logStartIndex !== -1 && logEndIndex !== -1) {
                fullText = existingContent.substring(0, logStartIndex) + newLogBlock + existingContent.substring(logEndIndex + this.LOG_END_TAG.length);
            } else {
                fullText = existingContent + '\n' + newLogBlock;
            }

            await this.bridge.setChatMessage(fullText.trim(), this.messageId, { refresh: "none" });
        } catch (error) { console.error("[BLMX] Failed to save narrative log to text box:", error); }
    }

    _parseLogFromString(logString) {
        this.logEntries = [];
        const lines = logString.split('\n').filter(line => line.trim() !== '');

        lines.forEach(line => {
            const key = line.substring(0, line.indexOf(':')).trim();
            const value = line.substring(line.indexOf(':') + 1).trim();
            try {
                switch (key) {
                    // --- Unified handling: All entries go into this.logEntries ---
                    case 'USER_MOMENT': case 'CHAR_MOMENT': case 'USER_COMMENT': case 'CHAR_COMMENT': case 'USER_LIKE': case 'CHAR_LIKE':
                        this.logEntries.push({ key, data: JSON.parse(value) }); break;
                    case 'TIME':
                    case 'EVENT_LOG':
                        if (value) {
                            const data = JSON.parse(value);
                            const entryType = key === 'TIME' ? 'time' : 'event';
                            this.logEntries.push({ type: entryType, content: data });

                            const timeDate = new Date(data.date);
                            if (!isNaN(timeDate)) {
                                window.currentGameDate = timeDate;
                            }
                        }
                        break;
                    case 'RECALL': {
                        const recallData = JSON.parse(value);
                        const senderToFind = recallData.sender === 'USER' ? 'me' : 'them';
                        const entryToRecall = this.logEntries.slice().reverse().find(e => e.sender === senderToFind && e.content === recallData.target_text);
                        if(entryToRecall) {
                            entryToRecall.recalled = true;
                            entryToRecall.recalled_content = entryToRecall.content;
                            entryToRecall.recalled_timestamp = recallData.timestamp;
                        }
                        break;
                    }
                    case 'USER': case 'CHAR': {
                        const sender = key === 'USER' ? 'me' : 'them';
                        const voiceMatch = value.match(/^\[è¯­éŸ³:\s*({.*})\]/);
                        const stickerMatch = value.match(/^\[è¡¨æƒ…:\s*(.*)\]/);
                        const imageMatch = value.match(/^\[å›¾ç‰‡:\s*(.*)\]/); const locationMatch = value.match(/^\[ä½ç½®:\s*(.*)\]/);
                        const transferMatch = value.match(/^\[è½¬è´¦:\s*(.*)\]/); const fileMatch = value.match(/^\[æ–‡ä»¶:\s*(.*)\]/);
                        const giftMatch = value.match(/^\[ç¤¼ç‰©:\s*(.*)\]/);
                        const id = 'msg-' + Date.now() + Math.random() + sender;

                        if (voiceMatch) {
                            try {
                                this.logEntries.push({ id, type: 'voice', sender, content: JSON.parse(voiceMatch[1]) });
                            } catch(e) { console.error("Failed to parse voice data:", voiceMatch[1], e); }
                        }
                        else if (stickerMatch) this.logEntries.push({ id, type: 'sticker', sender, content: stickerMatch[1] });
                        else if (imageMatch) {
                            try {
                                const imgData = JSON.parse(imageMatch[1]);
                                this.logEntries.push({ id, type: 'image', sender, content: imgData });
                            } catch (e) {
                                this.logEntries.push({ id, type: 'image', sender, content: { type: 'desc', value: imageMatch[1] } });
                            }
                        }
                        else if (locationMatch) this.logEntries.push({ id, type: 'location', sender, content: locationMatch[1] });
                        else if (transferMatch) this.logEntries.push({ id, type: 'transfer', sender, content: transferMatch[1], data: JSON.parse(transferMatch[1]) });
                        else if (fileMatch) this.logEntries.push({ id, type: 'file', sender, content: fileMatch[1] });
                        else if (giftMatch) this.logEntries.push({ id, type: 'gift', sender, content: giftMatch[1], data: JSON.parse(giftMatch[1]) });
                        else this.logEntries.push({ id, type: 'message', sender, content: value });
                        break;
                    }
                }
            } catch(e) { console.error("Failed to parse log line:", line, e); }
        });
    }

    _renderLogToString() {
        const recallCommands = [];
        const allEntriesString = this.logEntries.map(e => {
            if (e.recalled) {
                const recallData = {
                    sender: e.sender === 'me' ? 'USER' : 'CHAR',
                    target_text: typeof e.recalled_content === 'object' ? e.recalled_content.text : e.recalled_content,
                    timestamp: e.recalled_timestamp
                };
                if (recallData.target_text) {
                    recallCommands.push(`RECALL: ${JSON.stringify(recallData)}`);
                }
            }

            // --- Unified rendering from single logEntries array ---
            if (e.key && e.data) { // This is a Moment entry
                return `${e.key}: ${JSON.stringify(e.data)}`;
            } 

            if (e.type) { // This is a Chat/Event entry
                if (e.type === 'time' || e.type === 'event') {
                    const key = e.type === 'time' ? 'TIME' : 'EVENT_LOG';
                    return `${key}: ${JSON.stringify(e.content)}`;
                }

                const prefix = e.sender === 'me' ? 'USER' : 'CHAR';
                let content = e.content;
                switch(e.type) {
                    case 'sticker': content = `[è¡¨æƒ…: ${e.content}]`; break;
                    case 'voice': content = `[è¯­éŸ³: ${JSON.stringify(e.content)}]`; break;
                    case 'image': content = `[å›¾ç‰‡: ${JSON.stringify(e.content)}]`; break;
                    case 'location': content = `[ä½ç½®: ${e.content}]`; break;
                    case 'file': content = `[æ–‡ä»¶: ${e.content}]`; break;
                    case 'gift': content = `[ç¤¼ç‰©: ${e.content}]`; break;
                    case 'transfer': content = `[è½¬è´¦: ${e.content}]`; break;
                }
                return `${prefix}: ${content}`;
            }
            return ''; // Should not happen for valid entries
        }).join('\n');

        return [allEntriesString, ...recallCommands].filter(Boolean).join('\n');
    }

    addEntry(entry) { this.logEntries.push(entry); }
    getContextForAI() { return this._renderLogToString(); }
}

