    function parseTimeToMinutes(e){ const t=e.match(/(\d{1,2}):(\d{2})/); return t?60*parseInt(t[1],10)+parseInt(t[2],10):null; }

    function formatMomentTimestamp(dateString, timeString) {
        if (!dateString || !timeString) return ' ';
        const postDate = new Date(dateString);
        const now = new Date(window.currentGameDate);

        const postYear = postDate.getFullYear();
        const postMonth = postDate.getMonth();
        const postDay = postDate.getDate();

        const nowYear = now.getFullYear();
        const nowMonth = now.getMonth();
        const nowDay = now.getDate();

        if (postYear === nowYear && postMonth === nowMonth && postDay === nowDay) {
            return timeString;
        }

        const yesterday = new Date(now);
        yesterday.setDate(now.getDate() - 1);
        if (postYear === yesterday.getFullYear() && postMonth === yesterday.getMonth() && postDay === yesterday.getDate()) {
            return `æ˜¨å¤© ${timeString}`;
        }

        if (postYear === nowYear) {
            return `${postMonth + 1}æœˆ${postDay}æ—¥ ${timeString}`;
        }

        return `${postYear}å¹´${postMonth + 1}æœˆ${postDay}æ—¥`;
    }

