                                                 <div class="post-meta-left">
                                                     <span class="timestamp">${displayTime}</span>
                                                     ${deleteBtnHtml}
                                                 </div>
                                                 <div class="post-actions">
                                                     <span class="comment-button" title="è¯„è®º/ç‚¹èµ"><i class="fas fa-comment-dots"></i></span>
                                                 </div>
                                             </div>
                                             ${interactionsHtml}
                                         </div>`;
            momentsFeedList.appendChild(li);
        });
    }

    function stageAndDisplayEntry(entry) {
        if (entry.type !== 'event' && entry.type !== 'time') {
            entry.id = 'msg-' + Date.now() + Math.random();
        }
        if (entry.type === 'image' && entry.content.type === 'url' && entry.content.value.startsWith('data:image')) {
            entry.content.isNewForAI = true;
        }
        userMessageQueue.push(entry);
        addEntryToUI(entry);
        updateFooterButtonsState();
    }

    async function triggerAiResponse(immediate = false) {
        if (isGenerating || !blmxManager) return;
        const messagesToProcess = userMessageQueue;

        if (!immediate && messagesToProcess.length === 0 && !hasPendingNotifications) return;

        isGenerating = true;

        let contextForAI = blmxManager.getContextForAI();
        const tempContextEntries = [];
        messagesToProcess.forEach(entry => {
            const prefix = entry.sender === 'me' ? 'USER' : 'CHAR';
            if (entry.type === 'image' && entry.content.isNewForAI) {
                tempContextEntries.push(`${prefix}: [å›¾ç‰‡: ${entry.content.value}]`);
            } else {
                let content = entry.content;
                switch(entry.type) {
                    case 'sticker': content = `[è¡¨æƒ…: ${entry.content}]`; break;
                    case 'voice': content = `[è¯­éŸ³: ${JSON.stringify(entry.content)}]`; break;
                    case 'image': content = `[å›¾ç‰‡: ${JSON.stringify(entry.content)}]`; break;
                    case 'location': content = `[ä½ç½®: ${entry.content}]`; break;
                    case 'file': content = `[æ–‡ä»¶: ${entry.content}]`; break;
                    case 'gift': content = `[ç¤¼ç‰©: ${JSON.stringify(entry.data)}]`; break;
                    case 'transfer': content = `[è½¬è´¦: ${JSON.stringify(entry.data)}]`; break;
                }
                 tempContextEntries.push(`${prefix}: ${content}`);
            }
        });
        if (tempContextEntries.length > 0) {
            contextForAI += '\n' + tempContextEntries.join('\n');
        }

        messagesToProcess.forEach(entry => {
            if (entry.type === 'image' && entry.content.isNewForAI) {
                entry.content.value = `[ç”¨æˆ·å‘é€çš„å›¾ç‰‡]`;
                delete entry.content.isNewForAI;
            }
        });

        blmxManager.logEntries.push(...messagesToProcess);
        userMessageQueue = [];

        updateFooterButtonsState();
        await blmxManager.persistLogToStorage();
        renderChatHistory();

        const MAX_RETRIES = 2;
        let attempt = 0;
        let success = false;

        while (attempt <= MAX_RETRIES && !success) {
            if (attempt > 0) {
                console.log(`[BLMX] AI response was empty. Retrying, attempt ${attempt + 1}/${MAX_RETRIES + 1}...`);
                await new Promise(resolve => setTimeout(resolve, 1000 * attempt));
            }

            try {
                const stream = await tavernGenerateFunc({ user_input: contextForAI, should_stream: true });
                let finalAiResponse = '';
                for await (const chunk of stream) { finalAiResponse += chunk; }

                let processedResponse = finalAiResponse.trim();
                const len = processedResponse.length;
                if (len > 0 && len % 2 === 0) {
                    const firstHalf = processedResponse.substring(0, len / 2);
                    const secondHalf = processedResponse.substring(len / 2);
                    if (firstHalf === secondHalf && firstHalf.trim() !== "") {
                        console.warn('[BLMX] Detected fully duplicated AI response, trimming automatically.');
                        processedResponse = firstHalf;
                    }
                }

                const responseLines = processedResponse.split('\n').filter(line => line.trim());

                if (responseLines.length > 0) {
                    responseLines.forEach(line => {
                        const key = line.substring(0, line.indexOf(':')).trim(); const value = line.substring(line.indexOf(':') + 1).trim();
                        if (!key || !value) return;
                        try {
                            switch (key) {
                                case 'CHAR_MOMENT': case 'CHAR_COMMENT': case 'CHAR_LIKE': blmxManager.addEntry({ key, data: JSON.parse(value) }); break;
                                case 'RECALL': {
                                    const recallData = JSON.parse(value);
                                    const senderToFind = recallData.sender === 'USER' ? 'me' : 'them';
                                    const entryToRecall = blmxManager.logEntries.slice().reverse().find(e => e.sender === senderToFind && e.content === recallData.target_text);
                                    if (entryToRecall) {
                                        entryToRecall.recalled = true;
                                        entryToRecall.recalled_content = entryToRecall.content;
                                        entryToRecall.recalled_timestamp = recallData.timestamp;
                                    }
                                    break;
                                }
                                case 'CHAR': {
                                    const id = 'msg-' + Date.now() + Math.random();
                                    const voiceMatch = value.match(/^\[è¯­éŸ³:\s*({.*})\]/);
                                    const stickerMatch = value.match(/^\[è¡¨æƒ…:\s*(.*)\]/);
                                    const imageMatch = value.match(/^\[å›¾ç‰‡:\s*(.*)\]/);
                                    const locationMatch = value.match(/^\[ä½ç½®:\s*(.*)\]/);
                                    const transferMatch = value.match(/^\[è½¬è´¦:\s*(.*)\]/);
                                    const fileMatch = value.match(/^\[æ–‡ä»¶:\s*(.*)\]/);
                                    const giftMatch = value.match(/^\[ç¤¼ç‰©:\s*(.*)\]/);

                                    if (voiceMatch) {
                                        try { blmxManager.addEntry({ id, type: 'voice', sender: 'them', content: JSON.parse(voiceMatch[1]) }); } catch(e) { console.error("Failed to parse AI voice response:", e); }
                                    } else if (stickerMatch) {
                                        blmxManager.addEntry({ id, type: 'sticker', sender: 'them', content: stickerMatch[1] });
                                    } else if (imageMatch) {
                                        try {
                                            const imgData = JSON.parse(imageMatch[1]);
                                            blmxManager.addEntry({ id, type: 'image', sender: 'them', content: imgData });
                                        } catch (e) {
                                            blmxManager.addEntry({ id, type: 'image', sender: 'them', content: { type: 'desc', value: imageMatch[1] } });
                                        }
                                    } else if (locationMatch) {
                                        blmxManager.addEntry({ id, type: 'location', sender: 'them', content: locationMatch[1] });
                                    } else if (transferMatch) {
                                        blmxManager.addEntry({ id, type: 'transfer', sender: 'them', content: transferMatch[1], data: JSON.parse(transferMatch[1]) });
                                    } else if (fileMatch) {
                                        blmxManager.addEntry({ id, type: 'file', sender: 'them', content: fileMatch[1] });
                                    } else if (giftMatch) {
                                        blmxManager.addEntry({ id, type: 'gift', sender: 'them', content: giftMatch[1], data: JSON.parse(giftMatch[1]) });
                                    } else {
                                        blmxManager.addEntry({ id, type: 'message', sender: 'them', content: value });
                                    }
                                    break;
                                }
                                case 'TIME':
                                case 'EVENT_LOG':
                                    const data = JSON.parse(value);
                                    const entryType = key === 'TIME' ? 'time' : 'event';
                                    blmxManager.addEntry({ type: entryType, content: data });
                                    const timeDate = new Date(data.date);
                                    if (!isNaN(timeDate)) {
                                        window.currentGameDate = timeDate;
                                    }
                                    break;
                            }
                        } catch(e) { console.error("Failed to parse AI response:", line, e); }
                    });
                    renderChatHistory();
                    renderMomentsFeed();
                    success = true;
                }
            } catch (error) {
                console.error(`[BLMX Proxy] Error on attempt ${attempt}:`, error);
                if (attempt >= MAX_RETRIES) {
                     addMessageToWeChat({id: 'error', sender: 'them', type:'message', content: 'AIä»£ç†å‡ºé”™ (è¯¦æƒ…è§F12æ§åˆ¶å°)'});
                }
            }
            attempt++;
        }

        if (!success && attempt > MAX_RETRIES) {
            console.error(`[BLMX] AI generation failed after ${MAX_RETRIES + 1} attempts (empty response).`);
            addMessageToWeChat({id: 'error', sender: 'them', type:'message', content: '(AIå“åº”ä¸ºç©ºï¼Œè¯·ç¨åå†è¯•æˆ–æ‰‹åŠ¨è§¦å‘)'});
        }

        await blmxManager.persistLogToStorage();
        isGenerating = false;
    }

