    <script>
        // è·å–æ‰€æœ‰æ ‡ç­¾å’Œå†…å®¹åŒºåŸŸ
        const tabs = document.querySelectorAll('.tab');
        const contents = document.querySelectorAll('.content');

        // ä¸ºæ¯ä¸ªæ ‡ç­¾æ·»åŠ ç‚¹å‡»äº‹ä»¶
        tabs.forEach(tab => {
            tab.addEventListener('click', () => {
                // ç§»é™¤æ‰€æœ‰æ ‡ç­¾å’Œå†…å®¹åŒºåŸŸçš„activeç±»
                tabs.forEach(t => {
                    t.classList.remove('active');
                    t.classList.remove('active-tab');
                    t.classList.remove('passive-tab');
                    t.classList.remove('dark-tab');
                    t.classList.remove('funny-tab');
                });
                contents.forEach(c => c.classList.remove('active'));

                // ä¸ºå½“å‰ç‚¹å‡»çš„æ ‡ç­¾æ·»åŠ activeç±»å’Œå¯¹åº”çš„ä¸»é¢˜ç±»
                tab.classList.add('active');
                const theme = tab.getAttribute('data-theme');
                tab.classList.add(`${theme}-tab`);

                // æ›´æ–°å…¶ä»–æ ‡ç­¾é¢œè‰²
                tabs.forEach(t => {
                    if (t !== tab) {
                        t.classList.add(`${theme}-tab`);
                    }
                });

                // è·å–å¯¹åº”çš„å†…å®¹åŒºåŸŸå¹¶æ·»åŠ activeç±»
                const tabId = tab.getAttribute('data-tab');
                document.getElementById(tabId).classList.add('active');

                // æ›´æ–°å­—ä½“å¤§å°æ§åˆ¶å™¨çš„ä½ç½®ï¼Œç¡®ä¿å®ƒæ˜¾ç¤ºåœ¨å½“å‰æ¿€æ´»çš„å†…å®¹åŒºåŸŸä¸­
                const activeContent = document.querySelector('.content.active');
                const fontSizeControl = document.querySelector('.font-size-control');
                if (activeContent && fontSizeControl) {
                    activeContent.appendChild(fontSizeControl);
                }
            });
        });

        // åˆå§‹åŒ–æ ‡ç­¾é¢œè‰²
        tabs.forEach(tab => {
            if (!tab.classList.contains('active')) {
                tab.classList.add('active-tab');
            }
        });

        // å¤„ç†å¸¦*å·çš„æ–‡æœ¬ï¼Œæ˜¾ç¤ºæ—¶éšè—*å·
        function processButtonText() {
            const buttons = document.querySelectorAll('.big-button');

            buttons.forEach(button => {
                const originalText = button.textContent.trim();
                // ä¿å­˜åŸå§‹æ–‡æœ¬åˆ°dataå±æ€§ä¸­
                button.setAttribute('data-original-text', originalText);

                // å¦‚æœæ–‡æœ¬åŒ…å«*å·ï¼Œéšè—*å·æ˜¾ç¤º
                if (originalText.includes('*')) {
                    const displayText = originalText.replace(/\*/g, '');
                    button.textContent = displayText;
                }
            });
        }

        // é¡µé¢åŠ è½½åå¤„ç†æŒ‰é’®æ–‡æœ¬
        processButtonText();

        // å½“æŒ‰é’®å†…å®¹è¢«æ›´æ”¹æ—¶é‡æ–°å¤„ç†
        function updateButtonDisplay(button) {
            const originalText = button.getAttribute('data-original-text');
            if (originalText && originalText.includes('*')) {
                const displayText = originalText.replace(/\*/g, '');
                if (button.textContent !== displayText) {
                    button.textContent = displayText;
                }
            }
        }

        // ç›‘æ§DOMå˜åŒ–ï¼Œå®æ—¶æ›´æ–°æŒ‰é’®æ˜¾ç¤º
        const observer = new MutationObserver(mutations => {
            mutations.forEach(mutation => {
                if (mutation.type === 'childList' || mutation.type === 'characterData') {
                    const button = mutation.target.closest('.big-button');
                    if (button) {
                        updateButtonDisplay(button);
                    }
                }
            });
        });

        document.querySelectorAll('.big-button').forEach(button => {
            observer.observe(button, { 
                childList: true, 
                characterData: true,
                subtree: true 
            });
        });

        // ä¸ºæ‰€æœ‰æŒ‰é’®æ·»åŠ ç‚¹å‡»äº‹ä»¶ï¼Œå°†æ–‡æœ¬å‘é€åˆ°è¾“å…¥æ¡†
        document.querySelectorAll('.big-button').forEach(button => {
            button.addEventListener('click', () => {
                // ä½¿ç”¨åŸå§‹æ–‡æœ¬ï¼ˆåŒ…å«*å·ï¼‰
                const originalText = button.getAttribute('data-original-text');

                // ä½¿ç”¨SillyTavernçš„APIå‘é€æ–‡æœ¬åˆ°è¾“å…¥æ¡†
                try {
                    // å°è¯•è°ƒç”¨çˆ¶çª—å£çš„å‡½æ•°
                    if (window.parent && window.parent.triggerSlash) {
                        // ä½¿ç”¨/setinputå‘½ä»¤è®¾ç½®è¾“å…¥æ¡†å†…å®¹
                        window.parent.triggerSlash(`/setinput ${originalText}`);
                    } else {
                        // ç›´æ¥è®¿é—®STçš„è¾“å…¥æ¡†å¹¶è®¾ç½®å†…å®¹
                        const stInputElement = window.parent.document.querySelector('#send_textarea');
                        if (stInputElement) {
                            stInputElement.value = originalText;
                            // è§¦å‘è¾“å…¥äº‹ä»¶ä»¥æ›´æ–°UI
                            const event = new Event('input', { bubbles: true });
                            stInputElement.dispatchEvent(event);
                        }
                    }
                } catch (error) {
                    console.error('å‘SillyTavernå‘é€æ–‡æœ¬æ—¶å‡ºé”™:', error);
                }
            });
        });

        // å­—ä½“å¤§å°è°ƒæ•´åŠŸèƒ½
        document.addEventListener('DOMContentLoaded', () => {
            // è·å–æ‰€æœ‰å­—ä½“å¤§å°ç›¸å…³å…ƒç´ 
            const decreaseBtns = document.querySelectorAll('.decrease-font');
            const increaseBtns = document.querySelectorAll('.increase-font');
            const fontSizeDisplays = document.querySelectorAll('.font-size-display');
            const menuToggles = document.querySelectorAll('.menu-toggle');
            const fontControls = document.querySelectorAll('.font-size-control');

            // è®¾ç½®åˆå§‹å­—ä½“å¤§å°
            let currentFontSize = 15;
            document.documentElement.style.setProperty('--button-font-size', `${currentFontSize}px`);

            // åŠ è½½ä¿å­˜çš„å­—ä½“å¤§å°
            loadFontSize();

            // ä¸ºæ¯ä¸ªä¸‰ç‚¹èœå•æŒ‰é’®æ·»åŠ ç‚¹å‡»äº‹ä»¶
            menuToggles.forEach(toggle => {
                toggle.addEventListener('click', (e) => {
                    e.stopPropagation(); // é˜»æ­¢äº‹ä»¶å†’æ³¡

                    // è·å–å¯¹åº”çš„å­—ä½“æ§åˆ¶å™¨
                    const contentId = toggle.id.split('-')[0];
                    const fontControl = document.getElementById(`${contentId}-font-control`);

                    // åˆ‡æ¢å­—ä½“æ§åˆ¶å™¨çš„æ˜¾ç¤ºçŠ¶æ€
                    fontControl.classList.toggle('show');

                    // éšè—å…¶ä»–æ ‡ç­¾é¡µçš„å­—ä½“æ§åˆ¶å™¨
                    fontControls.forEach(control => {
                        if (control !== fontControl) {
                            control.classList.remove('show');
                        }
                    });
                });
            });

            // ç‚¹å‡»é¡µé¢å…¶ä»–åœ°æ–¹éšè—å­—ä½“æ§åˆ¶å™¨
            document.addEventListener('click', (e) => {
                // å¦‚æœç‚¹å‡»çš„ä¸æ˜¯å­—ä½“æ§åˆ¶å™¨æˆ–å…¶å­å…ƒç´ 
                if (!e.target.closest('.font-size-control') && !e.target.closest('.menu-toggle')) {
                    fontControls.forEach(control => {
                        control.classList.remove('show');
                    });
                }
            });

            // ä¿å­˜å­—ä½“å¤§å°åˆ°æœ¬åœ°å­˜å‚¨
            function saveFontSize(size) {
                localStorage.setItem('card-button-font-size', size);
            }

            // ä»æœ¬åœ°å­˜å‚¨è¯»å–å­—ä½“å¤§å°
            function loadFontSize() {
                const savedSize = localStorage.getItem('card-button-font-size');
                if (savedSize) {
                    currentFontSize = parseInt(savedSize);
                    // æ›´æ–°æ‰€æœ‰æ˜¾ç¤ºå­—ä½“å¤§å°çš„å…ƒç´ 
                    fontSizeDisplays.forEach(display => {
                        display.textContent = currentFontSize;
                    });
                    document.documentElement.style.setProperty('--button-font-size', `${currentFontSize}px`);
                }
            }

            // ä¸ºæ‰€æœ‰å‡å°å­—ä½“æŒ‰é’®æ·»åŠ ç‚¹å‡»äº‹ä»¶
            decreaseBtns.forEach(btn => {
                btn.addEventListener('click', (e) => {
                    e.stopPropagation(); // é˜»æ­¢äº‹ä»¶å†’æ³¡
                    if (currentFontSize > 10) { // é™åˆ¶æœ€å°å­—ä½“å¤§å°
                        currentFontSize -= 1;
                        // æ›´æ–°æ‰€æœ‰æ˜¾ç¤ºå­—ä½“å¤§å°çš„å…ƒç´ 
                        fontSizeDisplays.forEach(display => {
                            display.textContent = currentFontSize;
                        });
                        document.documentElement.style.setProperty('--button-font-size', `${currentFontSize}px`);
                        saveFontSize(currentFontSize);
                    }
                });
            });

            // ä¸ºæ‰€æœ‰å¢å¤§å­—ä½“æŒ‰é’®æ·»åŠ ç‚¹å‡»äº‹ä»¶
            increaseBtns.forEach(btn => {
                btn.addEventListener('click', (e) => {
                    e.stopPropagation(); // é˜»æ­¢äº‹ä»¶å†’æ³¡
                    if (currentFontSize < 24) { // é™åˆ¶æœ€å¤§å­—ä½“å¤§å°
                        currentFontSize += 1;
                        // æ›´æ–°æ‰€æœ‰æ˜¾ç¤ºå­—ä½“å¤§å°çš„å…ƒç´ 
                        fontSizeDisplays.forEach(display => {
                            display.textContent = currentFontSize;
                        });
                        document.documentElement.style.setProperty('--button-font-size', `${currentFontSize}px`);
                        saveFontSize(currentFontSize);
                    }
                });
            });

            // ä¸ºæ ‡ç­¾åˆ‡æ¢æ·»åŠ å­—ä½“æ§åˆ¶å™¨çš„æ›´æ–°
            tabs.forEach(tab => {
                tab.addEventListener('click', () => {
                    // éšè—æ‰€æœ‰å­—ä½“æ§åˆ¶å™¨
                    fontControls.forEach(control => {
                        control.classList.remove('show');
                    });
                });
            });
        });
    </script>
