            } else if(n){ n(`/setinput ${payload}`); }
          } catch(err){ console.error("è®¾ç½®è¾“å…¥æ–‡æœ¬æ—¶å‡ºé”™:", err); }
        });
        root.appendChild(item);
      });
    }

