      };

      btn.addEventListener("click", (e)=>{
        e.stopPropagation();
        const nextTheme = card.classList.contains("dark-mode") ? "light" : "dark";
        applyTheme(nextTheme);
      });

      const savedTheme = localStorage.getItem("postItTheme") || "light";
      applyTheme(savedTheme);
