    <script>
        document.addEventListener('DOMContentLoaded', () => {
            const contentArea = document.getElementById('content-area');
            const osTitle = document.getElementById('os-title');
            const multiHint = document.getElementById('multi-hint');
            const rawData = document.getElementById('raw-data').innerHTML;

            let characters = [];
            let currentIndex = 0;

            function parseData() {
                // å®½å®¹æ¨¡å¼åŒ¹é…æ­£åˆ™
                const charBlocks = rawData.match(/<(\w+)[^>]*>([\s\S]*?)<\/\1>/g);

                if (charBlocks) {
                    characters = charBlocks.map(block => {
                        const extract = (regex) => {
                            const match = block.match(regex);
                            return match ? match[1].trim() : '';
                        };
                        const parseStat = (regex) => {
                            const match = block.match(regex);
                            if (!match) return { val: 0, note: '' };
                            return { val: match[1], note: match[2] ? match[2].trim() : '' };
                        };

                        let doodleRaw = block.match(/éšæ€§æ¶‚é¸¦ï¼š\n([\s\S]*?)æ¶‚é¸¦è§£æï¼š/)?.[1] || '';
                        doodleRaw = doodleRaw.replace(/^\n+|\n+$/g, '');

                        const statRegex = /\s*(-?\d+(?:\.\d+)?)\s*(?:[ï¼ˆ(]([^\n]*)[)ï¼‰])?/;

                        return {
                            name: extract(/è§’è‰²åç§°ï¼š(.*?)\n/),
                            favor: parseStat(new RegExp("å¥½æ„ŸæŒ‡æ•°ï¼š" + statRegex.source)),
                            mood: extract(/æƒ…ç»ªæ°”æ³¡ï¼š(.*?)\n/),
                            secret: extract(/éšç§˜å¿ƒå£°ï¼š(.*?)\n/),
                            lust: parseStat(new RegExp("æ€§æ¬²æŒ‡æ•°ï¼š" + statRegex.source)),
                            body: extract(/å™¨å®˜çŠ¶æ€ï¼š([\s\S]*?)äº‹åŠ¡å¤‡å¿˜ï¼š/),
                            memo: extract(/äº‹åŠ¡å¤‡å¿˜ï¼š([\s\S]*?)éšæ€§æ¶‚é¸¦ï¼š/),
                            doodle: doodleRaw,
                            doodleCaption: extract(/æ¶‚é¸¦è§£æï¼š(.*?)\n/)
                        };
                    });
                }
            }

            function updateUIState() {
                if (characters.length > 1) {
                    multiHint.style.display = 'block';
                    osTitle.textContent = `PINK OS [${currentIndex + 1}/${characters.length}]`;
                } else {
                    multiHint.style.display = 'none';
                    osTitle.textContent = 'PINK OS';
                }
            }

            function render(index) {
                if (!characters[index]) return;
                const char = characters[index];

                // æ›´æ–°ç•Œé¢çŠ¶æ€ï¼ˆé¡µç æç¤ºï¼‰
                updateUIState();

                const favorPercent = Math.min(100, Math.max(0, parseFloat(char.favor.val)));
                const lustPercent = Math.min(100, Math.max(0, parseFloat(char.lust.val)));

                const memoHtml = char.memo.split('\n').filter(line => line.trim()).map(line => {
                    const isDone = line.includes('<s>') || line.includes('DONE');
                    return `<li class="${isDone ? 'done' : ''}">${line.replace(/<\/?s>/g, '')}</li>`;
                }).join('');

                const bodyLines = char.body.split('\n').filter(line => line.trim() !== '');
                const bodyHtml = bodyLines.map(line => {
                    const processedLine = line.replace(/ã€.*?ã€‘/g, (match) => {
                        return `<span class="organ-tag">${match}</span>`;
                    });
                    return `<p>${processedLine}</p>`;
                }).join('');

                const html = `
                    <div class="char-header">
                        <div class="char-name">${char.name}</div>
                    </div>
                    <div class="section">
                        <h3><i class="fa-solid fa-heart-pulse"></i> STATUS</h3>
                        <div class="stat-block">
                            <div class="stat-header"><span>FAVOR</span><span>${char.favor.val}</span></div>
                            <div class="progress-track"><div class="progress-fill fill-favor" style="width: ${favorPercent}%"></div></div>
                            ${char.favor.note ? `<div class="stat-note">${char.favor.note}</div>` : ''}
                        </div>
                        <div style="height:10px"></div>
                        <div class="stat-block">
                            <div class="stat-header"><span>LUST</span><span>${char.lust.val}</span></div>
                            <div class="progress-track"><div class="progress-fill fill-lust" style="width: ${lustPercent}%"></div></div>
                            ${char.lust.note ? `<div class="stat-note">${char.lust.note}</div>` : ''}
                        </div>
                        <div style="margin-top:10px; font-size:14px; color:var(--dim-text)">
                            <i class="fa-regular fa-face-smile"></i> ${char.mood.replace(/ï¼›/g, ' <span style="color:var(--accent-pink)">/</span> ')}
                        </div>
                    </div>
                    <div class="section">
                        <h3><i class="fa-solid fa-comment-dots"></i> THOUGHTS</h3>
                        <div class="quote-box text-content">${char.secret.replace(/\*/g, '')}</div>
                    </div>
                    <div class="section">
                        <h3><i class="fa-solid fa-person-rays"></i> BODY_CHECK</h3>
                        <div class="organ-list text-content">${bodyHtml}</div>
                    </div>
                    <div class="section">
                        <h3><i class="fa-solid fa-list-check"></i> TODO</h3>
                        <ul class="memo-list">${memoHtml}</ul>
                    </div>
                    <div class="section">
                        <h3><i class="fa-solid fa-pen-nib"></i> DOODLE</h3>
                        <div class="doodle-box">
                            <div class="ascii-art">${char.doodle.replace(/</g, '&lt;')}</div>
                            <div class="doodle-cap">${char.doodleCaption}</div>
                        </div>
                    </div>
                `;
                contentArea.innerHTML = html;
            }

            parseData();
            if (characters.length > 0) render(0);

            document.getElementById('prev').addEventListener('click', () => {
                if(characters.length > 1) {
                    currentIndex = (currentIndex - 1 + characters.length) % characters.length;
                    render(currentIndex);
                }
            });
            document.getElementById('next').addEventListener('click', () => {
                if(characters.length > 1) {
                    currentIndex = (currentIndex + 1) % characters.length;
                    render(currentIndex);
                }
            });
        });
    </script>
