    <script>
        document.addEventListener('DOMContentLoaded', () => {

            // --- ç«¹å¶ç³»ç»Ÿ (ä¿æŒä¸å˜) ---
            function initBamboo() {
                const container = document.getElementById('bamboo-layer');
                // ç¨å¾®å‡å°‘æ•°é‡ï¼Œé¿å…é®æŒ¡æ–‡å­—
                const leafCount = 10;
                for(let i=0; i<leafCount; i++) {
                    const leaf = document.createElement('div');
                    leaf.classList.add('falling-leaf');

                    const width = Math.random() * 8 + 6; 
                    leaf.style.width = `${width}px`;
                    leaf.style.height = `${width * 2}px`;
                    leaf.style.left = `${Math.random() * 100}%`;

                    const duration = Math.random() * 5 + 8; 
                    const delay = Math.random() * -10; 

                    leaf.style.setProperty('--r-start', Math.random() * 360 + 'deg');
                    leaf.style.setProperty('--r-end', (Math.random() * 360 + 360) + 'deg');
                    leaf.style.setProperty('--x-end', (Math.random() * 100 - 50) + 'px');

                    leaf.style.animationDuration = `${duration}s`;
                    leaf.style.animationDelay = `${delay}s`;

                    container.appendChild(leaf);
                }
            }

            const rawData = document.getElementById('raw-data').innerHTML;
            const contentBody = document.getElementById('content-body');
            const mainContainer = document.getElementById('main-container');
            const footerNav = document.getElementById('footer-nav');
            const pageIndicator = document.getElementById('page-indicator');

            let characters = [];
            let currentIndex = 0;
            let isCollapsed = true;

            const cnNums = ['é›¶', 'å£¹', 'è´°', 'å', 'è‚†', 'ä¼', 'é™†', 'æŸ’', 'æŒ', 'ç–'];
            const toCnNum = (n) => (n <= 9) ? cnNums[n] : n;

            document.getElementById('header-trigger').addEventListener('click', () => {
                isCollapsed = !isCollapsed;
                if(isCollapsed) mainContainer.classList.add('is-collapsed');
                else mainContainer.classList.remove('is-collapsed');
            });

            function parseData() {
                const blocks = rawData.match(/<char[\s\S]*?>([\s\S]*?)<\/char[\d]*>/g);
                if(blocks) {
                    characters = blocks.map(b => {
                        const get = (r) => (b.match(r) || [])[1]?.trim() || '';
                        // ä¿®æ­£ï¼šæ”¯æŒè¯»å–å°æ•°ï¼Œä¾‹å¦‚ 0.0 æˆ– 50.5
                        const getNum = (r) => parseFloat((b.match(r) || [])[1]) || 0;

                        let art = b.match(/ç¬”å¢¨ä¸¹é’ï¼š\n([\s\S]*?)ç¬”å¢¨é¢˜è·‹/)?.[1] || '';
                        art = art.replace(/^\n+|\n+$/g, '');

                        // ä¿®æ­£æ­£åˆ™ï¼šå…è®¸åŒ¹é…å°æ•°ä½
                        return {
                            name: get(/äººç‰©åè®³ï¼š(.*?)\n/),
                            fateNum: getNum(/ä¸–ä¿—å°˜ç¼˜ï¼š\s*(-?\d+(?:\.\d+)?)/),
                            mood: get(/å½“å‰å¿ƒå¢ƒï¼š(.*?)\n/),
                            innerVoice: get(/çµå°æš—è¯­ï¼š([\s\S]*?)å¿ƒä¹‹æ¬²å¿µ/),
                            desireNum: getNum(/å¿ƒä¹‹æ¬²å¿µï¼š\s*(\d+(?:\.\d+)?)/),
                            bodyCheck: get(/è‚‰èº«åé¦ˆï¼š\n([\s\S]*?)çº¢å°˜ä¿—åŠ¡/),
                            tasks: get(/çº¢å°˜ä¿—åŠ¡ï¼š\n([\s\S]*?)ç¬”å¢¨ä¸¹é’/),
                            ascii: art,
                            caption: get(/ç¬”å¢¨é¢˜è·‹ï¼š(.*?)\n/)
                        };
                    });
                }
            }

            function render(index) {
                if(!characters[index]) return;
                const char = characters[index];

                if(characters.length > 1) {
                    footerNav.classList.remove('hidden');
                    pageIndicator.textContent = `${toCnNum(index+1)} Â· ${toCnNum(characters.length)}`;
                } else {
                    footerNav.classList.add('hidden');
                }

                // ä¿®æ­£è¿›åº¦æ¡é€»è¾‘ï¼šå®Œå…¨æŒ‰ç…§æ•°å€¼ 0-100 æ˜¾ç¤º
                // 0 å°±æ˜¯ 0%ï¼Œ50 å°±æ˜¯ 50%
                let fatePer = Math.min(100, Math.max(0, char.fateNum));
                let desirePer = Math.min(100, Math.max(0, char.desireNum));

                const moodHtml = char.mood.split(/[ï¼›;]/).filter(t=>t).map(t => 
                    `<div class="tag-pill">${t}</div>`
                ).join('');

                const bodyRows = char.bodyCheck.split('\n').filter(l=>l).map(l => {
                    const match = l.match(/ã€(.*?)ã€‘\s*[ï¼š:]?\s*(.*)/);
                    const organ = match ? match[1] : 'èº¯å¹²';
                    const desc = match ? match[2] : l;
                    return `
                        <div class="tcm-row">
                            <div class="tcm-label">${organ}</div>
                            <div class="tcm-value">${desc}</div>
                        </div>`;
                }).join('');

                const taskHtml = char.tasks.split('\n').filter(l=>l).map(l => {
                    const isDone = l.includes('<s>') || l.includes('DONE');
                    const txt = l.replace(/<\/?s>/g, '').replace(/^-\s*/, '').trim();
                    return `
                        <div class="task-plaque ${isDone?'task-done':''}">
                            <span class="task-text">${txt}</span>
                            <div class="stamp-finished">ç»ˆäº†</div>
                        </div>`;
                }).join('');

                const html = `
                    <div class="char-hero">
                        <div class="char-bg-text">å¤©åœ°ç„é»„</div>
                        <div class="char-name">${char.name}</div>
                        <div class="tags-wrapper">${moodHtml}</div>
                    </div>

                    <div class="stat-group">
                        <div class="stat-item">
                            <div class="stat-header"><span>ä¸–ä¿—å°˜ç¼˜</span><span>${char.fateNum}</span></div>
                            <div class="stat-track"><div class="stat-fill fill-fate" style="width:${fatePer}%"></div></div>
                        </div>
