            const message = lines[2] ? lines[2].replace(/[()ï¼ˆï¼‰]/g, '') : 'èµ äºˆæœ‰ç¼˜äººã€‚';

            const wrapper = document.createElement('div');
            wrapper.className = 'card-content-wrapper';

            wrapper.innerHTML = `
