            // éšæœºç”Ÿæˆä¸€ä¸ªè£…é¥°ç¬¦å· (å…«å¦ç¬¦å·æˆ–ç±»ä¼¼)
            const decoSymbols = ['â˜·', 'â˜¶', 'â˜µ', 'â˜´', 'â˜³', 'â˜²', 'â˜±', 'â˜°'];
            const randomSymbol = decoSymbols[Math.floor(Math.random() * decoSymbols.length)];

