        } else if (mode === 'fortune') {
            // Line 1: æµ‹å­— - ä¸Šä¸Šç­¾
            const firstLine = lines[0] ? lines[0].replace(/[()ï¼ˆï¼‰]/g, '') : '';

            // ä¿®å¤è§£æé€»è¾‘ï¼šä½¿ç”¨ ' - ' æˆ– '-' åˆ†å‰²ï¼Œå–æœ€åä¸€éƒ¨åˆ†ä½œä¸ºç­‰çº§
            let method = 'é—®åœ';
