            }

            // Line 2: æ ¸å¿ƒåˆ¤è¯
            const mainTextRaw = lines[1] ? lines[1].replace(/[()ï¼ˆï¼‰]/g, '') : '...';
            // å°è¯•å°†åˆ¤è¯åˆ†ä¸ºæ ‡é¢˜å’Œè§£é‡Š (å¦‚æœä¸­é—´æœ‰ç©ºæ ¼)
            // ä¾‹å¦‚: "æ½œé¾™å‹¿ç”¨ å›°é¡¿ä¹‹æ—¶..."
            let mainTitle = mainTextRaw;
