async function handleMoveAllScopedToGlobal() {
  const operationId = `move_scoped_to_global_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

devLog(`[FilterGroup] [${operationId}] ======= å¼€å§‹å¤„ç†ç§»åŠ¨å±€éƒ¨è„šæœ¬åˆ°å…¨å±€ =======`);

  try {
    // å…¼å®¹æ€§æ£€æŸ¥
