    const enabledCount = groupRegexes.filter(regex => regex.enabled).length;
    const totalCount = groupRegexes.length;

    // æ›´æ–°åˆ†ç»„æ ‡é¢˜ä¸­çš„ç»Ÿè®¡ä¿¡æ¯
