    async function pollChatMessages() {
        if (!coreApisAreReady || !TavernHelper_API || typeof TavernHelper_API.getLastMessageId !== 'function') {
            logDebug("pollChatMessages: Core APIs or getLastMessageId not ready. Skipping poll.");
