        const ruleText = `æ˜¾ç¤ºè§„åˆ™: ä»…æ˜¾ç¤ºæœªæ€»ç»“çš„æ¶ˆæ¯ã€‚`;
        const triggerText = `è§¦å‘è§„åˆ™: åŸºäº"${currentSummaryTypeName}", å½“æœªæ€»ç»“æ¶ˆæ¯æ•°è¾¾åˆ° ${triggerThreshold} (N=${autoChunkSizeForDisplay} + X=${offsetForDisplay}) æ—¶è‡ªåŠ¨æ€»ç»“ã€‚`;

        $hideCurrentValueDisplay.html(`${ruleText}<br>${triggerText}`);
