        // logDebug("Combined System Prompt for API call:\n", combinedSystemPrompt); // For debugging combined prompt
        const response = await fetch(fullApiUrl, { method: 'POST', headers: headers, body: body });
        if (!response.ok) {
            const errorText = await response.text();
            logError("è‡ªå®šä¹‰APIè°ƒç”¨å¤±è´¥:", response.status, response.statusText, errorText);
            throw new Error(`è‡ªå®šä¹‰APIè¯·æ±‚å¤±è´¥: ${response.status} ${response.statusText}. è¯¦æƒ…: ${errorText}`);
        }
        const data = await response.json();
        logDebug("è‡ªå®šä¹‰APIå“åº”:", data);
        if (data.choices && data.choices.length > 0 && data.choices[0].message && data.choices[0].message.content) {
            return data.choices[0].message.content.trim();
        } else {
            logError("è‡ªå®šä¹‰APIå“åº”æ ¼å¼ä¸æ­£ç¡®æˆ–æ— å†…å®¹:", data);
            throw new Error("è‡ªå®šä¹‰APIå“åº”æ ¼å¼ä¸æ­£ç¡®æˆ–æœªè¿”å›å†…å®¹ã€‚");
        }
