// ========= IndexedDB ç›¸å…³å‡½æ•° =========
function initializeIndexedDB() {
    return new Promise((resolve, reject) => {
        if (!window.indexedDB) {
            console.error('Font Manager: æ‚¨çš„æµè§ˆå™¨ä¸æ”¯æŒ IndexedDBï¼Œæœ¬åœ°å­—ä½“å°†æ— æ³•æŒä¹…åŒ–å­˜å‚¨ã€‚');
            reject('æµè§ˆå™¨ä¸æ”¯æŒ IndexedDB');
            return;
        }

        const request = indexedDB.open(INDEXED_DB_NAME, INDEXED_DB_VERSION);

        request.onerror = function(event) {
            console.error('Font Manager: æ‰“å¼€ IndexedDB å¤±è´¥:', event.target.error);
            reject(event.target.error);
        };

        request.onupgradeneeded = function(event) {
            const db = event.target.result;

            if (!db.objectStoreNames.contains(FONT_STORE_NAME)) {
                const store = db.createObjectStore(FONT_STORE_NAME, { keyPath: 'fontName' });
                store.createIndex('fontName', 'fontName', { unique: true });
                store.createIndex('dateAdded', 'dateAdded', { unique: false });
                console.log('Font Manager: åˆ›å»ºäº†å­—ä½“æ•°æ®å­˜å‚¨ç©ºé—´');
            }
        };

        request.onsuccess = function(event) {
            const db = event.target.result;
            console.log('Font Manager: IndexedDB æˆåŠŸæ‰“å¼€');
            resolve(db);
        };
    });
}

function saveLocalFontToIndexedDB(fontName, fontDataUrl, fileName) {
    return new Promise((resolve, reject) => {
        initializeIndexedDB().then(db => {
            const transaction = db.transaction([FONT_STORE_NAME], 'readwrite');
            const store = transaction.objectStore(FONT_STORE_NAME);

            const fontData = {
                fontName: fontName,
                fontDataUrl: fontDataUrl,
                fileName: fileName,
                dateAdded: new Date().toISOString()
            };

            const request = store.put(fontData);

            request.onsuccess = function() {
                console.log(`Font Manager: æˆåŠŸä¿å­˜æœ¬åœ°å­—ä½“ "${fontName}" åˆ° IndexedDB`);
                resolve();
            };

            request.onerror = function(event) {
                console.error(`Font Manager: ä¿å­˜æœ¬åœ°å­—ä½“ "${fontName}" åˆ° IndexedDB å¤±è´¥:`, event.target.error);
                reject(event.target.error);
            };

            transaction.oncomplete = function() {
                db.close();
            };
        }).catch(error => {
            reject(error);
        });
    });
}

function loadLocalFontsFromIndexedDB() {
    return new Promise((resolve, reject) => {
        initializeIndexedDB().then(db => {
            const transaction = db.transaction([FONT_STORE_NAME], 'readonly');
            const store = transaction.objectStore(FONT_STORE_NAME);
            const request = store.getAll();

            request.onsuccess = function() {
                const fonts = request.result;
                console.log(`Font Manager: ä» IndexedDB åŠ è½½äº† ${fonts.length} ä¸ªæœ¬åœ°å­—ä½“`);
                state.localFonts = fonts;

                // ç¡®ä¿æ‰€æœ‰æœ¬åœ°å­—ä½“éƒ½åœ¨ importedFontFamilies ä¸­
                fonts.forEach(fontData => {
                    if (fontData.fontName && !state.importedFontFamilies.includes(fontData.fontName)) {
                        state.importedFontFamilies.push(fontData.fontName);
                    }
                });

                if (fonts.length > 0) {
                    saveFontsToStorage();
                }

                resolve(fonts);
            };

            request.onerror = function(event) {
                console.error('Font Manager: ä» IndexedDB åŠ è½½å­—ä½“å¤±è´¥:', event.target.error);
                reject(event.target.error);
            };

            transaction.oncomplete = function() {
                db.close();
            };
        }).catch(error => {
            reject(error);
        });
    });
}

function applyLocalFontsToPage() {
    if (!state.localFonts || state.localFonts.length === 0) {
        console.log('Font Manager: æ²¡æœ‰æœ¬åœ°å­—ä½“éœ€è¦åº”ç”¨');
        return;
    }

    console.log(`Font Manager: å¼€å§‹åº”ç”¨ ${state.localFonts.length} ä¸ªæœ¬åœ°å­—ä½“åˆ°é¡µé¢`);

    state.localFonts.forEach((fontData, index) => {
        if (!fontData.fontName || !fontData.fontDataUrl) {
            console.error(`Font Manager: æœ¬åœ°å­—ä½“æ•°æ®ä¸å®Œæ•´ï¼Œè·³è¿‡åº”ç”¨:`, fontData);
            return;
        }

        const fontStyleId = `font-manager-local-font-${fontData.fontName.replace(/\s+/g, '-')}`;
        if (docContext.getElementById(fontStyleId)) {
            console.log(`Font Manager: æœ¬åœ°å­—ä½“ "${fontData.fontName}" å·²åº”ç”¨ï¼Œè·³è¿‡`);
            return;
        }

        try {
            const styleElement = docContext.createElement('style');
            styleElement.id = fontStyleId;
            styleElement.textContent = `
                @font-face {
                    font-family: '${fontData.fontName}';
                    src: url('${fontData.fontDataUrl}') format('truetype');
                    font-display: swap;
                }
            `;
            docContext.head.appendChild(styleElement);

            if (!state.importedFontFamilies.includes(fontData.fontName)) {
                state.importedFontFamilies.push(fontData.fontName);
                saveFontsToStorage();
            }

            console.log(`Font Manager: æˆåŠŸåº”ç”¨æœ¬åœ°å­—ä½“ "${fontData.fontName}" (${index + 1}/${state.localFonts.length}) åˆ°é¡µé¢`);
        } catch (err) {
            console.error(`Font Manager: åº”ç”¨æœ¬åœ°å­—ä½“ "${fontData.fontName}" å¤±è´¥:`, err);
        }
    });

    console.log(`Font Manager: æœ¬åœ°å­—ä½“åº”ç”¨å®Œæˆï¼Œæ€»å…± ${state.localFonts.length} ä¸ª`);
}

function deleteLocalFontFromIndexedDB(fontName) {
    return new Promise((resolve, reject) => {
        initializeIndexedDB().then(db => {
            const transaction = db.transaction([FONT_STORE_NAME], 'readwrite');
            const store = transaction.objectStore(FONT_STORE_NAME);

            const request = store.delete(fontName);

            request.onsuccess = function() {
                console.log(`Font Manager: æˆåŠŸä» IndexedDB åˆ é™¤æœ¬åœ°å­—ä½“ "${fontName}"`);

                // ä» state.localFonts ä¸­ç§»é™¤
                state.localFonts = state.localFonts.filter(font => font.fontName !== fontName);

                // ä» DOM ä¸­ç§»é™¤æ ·å¼
                const styleElement = docContext.getElementById(`font-manager-local-font-${fontName.replace(/\s+/g, '-')}`);
                if (styleElement) {
                    styleElement.remove();
                }

                // ä» importedFontFamilies ä¸­ç§»é™¤
                const fontIndex = state.importedFontFamilies.indexOf(fontName);
                if (fontIndex > -1) {
                    state.importedFontFamilies.splice(fontIndex, 1);
                    saveFontsToStorage();
                }

                resolve();
            };

            request.onerror = function(event) {
                console.error(`Font Manager: ä» IndexedDB åˆ é™¤æœ¬åœ°å­—ä½“ "${fontName}" å¤±è´¥:`, event.target.error);
                reject(event.target.error);
            };

            transaction.oncomplete = function() {
                db.close();
            };
        }).catch(error => {
            reject(error);
        });
    });
}

function deleteAllFonts() {
    if (!confirm('ç¡®å®šè¦åˆ é™¤æ‰€æœ‰å­—ä½“å—ï¼Ÿè¿™ä¸ªæ“ä½œæ— æ³•æ’¤é”€ï¼')) {
        return;
    }

    // åˆ é™¤æ‰€æœ‰æœ¬åœ°å­—ä½“
    const deletePromises = state.localFonts.map(font => 
        deleteLocalFontFromIndexedDB(font.fontName)
    );

    Promise.all(deletePromises).then(() => {
        // æ¸…ç©ºæ‰€æœ‰å­—ä½“ç›¸å…³çš„çŠ¶æ€å’Œå­˜å‚¨
        state.importedFontFamilies = [];
        state.fontImportUrls = [];
        state.activeFont = null;
        state.localFonts = [];

        // æ¸…ç©º localStorage
        localStorage.removeItem(LOCAL_STORAGE_KEY_FONTS);
        localStorage.removeItem(LOCAL_STORAGE_KEY_FONT_IMPORT_URLS);
        localStorage.removeItem(LOCAL_STORAGE_KEY_ACTIVE_FONT);

        // ç§»é™¤æ‰€æœ‰å­—ä½“æ ·å¼
        const fontStyles = docContext.querySelectorAll('style[id^="font-manager-local-font-"], style[id^="font-manager-runtime-import-"]');
        fontStyles.forEach(style => style.remove());

        // é‡ç½®é¡µé¢å­—ä½“
        resetToDefaultFont();

        showNotification('æ‰€æœ‰å­—ä½“å·²åˆ é™¤ï¼');
        renderCombinedMainView();
    }).catch(error => {
        console.error('åˆ é™¤å­—ä½“æ—¶å‡ºé”™:', error);
        showNotification('åˆ é™¤å­—ä½“æ—¶å‡ºç°é”™è¯¯ï¼');
    });
}

